-- ----------------------------
-- Table structure for hotel
-- ----------------------------
DROP TABLE IF EXISTS `hotel`;
CREATE TABLE `hotel`
(
    `hid`       int(11) NOT NULL AUTO_INCREMENT,
    `hNum`      int(11) NOT NULL,
    `hName`     varchar(255) NOT NULL,
    `hAddress`  text         NOT NULL,
    `tel`       varchar(255) DEFAULT NULL,
    `describle` text,
    `pic`       varchar(255) DEFAULT NULL,
    `price`     double       DEFAULT NULL,
    `stage`     int(11) DEFAULT NULL,
    PRIMARY KEY (`hid`),
    KEY         `hNum` (`hNum`),
    CONSTRAINT `hNum` FOREIGN KEY (`hNum`) REFERENCES `area` (`areaNum`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of hotel
-- ----------------------------
INSERT INTO `hotel`
VALUES ('1', '1101', '香格里拉大酒店', '江西省南昌市红谷滩新区世贸路以南，赣江中大道以西', '123455',
        '南昌香格里拉大酒店位于江西省南昌市红谷滩新区世贸路以南，赣江中大道以西。南昌香格里拉大酒店及综合发展项目位于南昌市红谷滩新区中央商业区核心地段东南位置，东临赣江，毗邻南昌市政府办公大楼及新会展中心，与滕王阁隔江相望，总面积为72亩，为全亚著名国际大酒店之一，总建筑面积约24万平方米，由五星级酒店、公寓及5A级写字楼三部分组成。',
        'area/1101/hotel/1.jpg', '110', '1');
INSERT INTO `hotel`
VALUES ('2', '1101', '江西宾馆', '南昌市最宽阔繁华的八一大道中段', '2324324',
        '江西宾馆是一家五星级现代商务酒店，位于江西省南昌市最宽阔繁华的八一大道中段，距南昌火车站三公里，到南昌昌北机场三十公里，置南昌政治、经济、文化、体育娱乐及商务活动的中心地带。优雅现代的装璜，充分体现了生态与环保理念的完美统一，为您提供舒适惬意的下榻享受。无论您是到此公干或度假消闲，都会受到专业体贴的接待。',
        'area/1101/hotel/aed149fb-1b6d-4484-af03-88f6aaa196e1.jpg', '150', '1');
INSERT INTO `hotel`
VALUES ('3', '1101', '明园大酒店', '南昌市二七南路日月休闲广场旁', '345345',
        '南昌明园大酒店系涉外挂牌三星级旅游宾馆，坐落于南昌市二七南路日月休闲广场旁，毗邻火车站400余米。酒店翠竹环绕，与日月广场交相映托，巧然天成。优雅的装饰、网络化智能管理与服务，具有专业化水准的工作人员以及酒店所拥有的豪华套房、行政数码房、标准间、酒楼、多功能会议中心、商务中心、商场、休闲咖啡吧、棋牌娱乐、现代化休闲桑拿等，显现出非凡魅力，是旅游住宿、商务洽谈、各类会议和休闲娱乐的理想场所。 　　\r\n    酒店拥有160套（间）商务个性凸现、设备先进的各类客房，配有有线电视、无线视频和宽带上网系统，风格各异的总统套房、豪华套房、景观房、标准间、单人房、以及多功能会议中心、豪华桑拿洗浴中心、商务中心和咖啡吧，提供多层次、全方位尽善尽美的服务。下榻商务客房，可随时遨游网络精彩境界，一览全球最新资讯。 　　酒楼拥有商务餐饮包房16间（套），酒楼大厅可同时容纳200余人用餐，聘请名厨主理，以赣菜为主，独具风情的环境，呈现一道亮丽的美食休闲娱乐风景线。18间休闲娱乐棋牌室，配有全自动麻将桌，轻松娱乐、自由自在。',
        'area/1101/hotel/3.jpg', '230', '1');
INSERT INTO `hotel`
VALUES ('4', '1101', '米兰假日酒店', '天佑路距南昌火车站、汽车站一步之遥', '345435',
        ' 南昌米兰假日酒店位于天佑路距南昌火车站、汽车站一步之遥。楼高10层，拥有各类宽敞舒适的客房。酒店有著名的老树咖啡，极富特色的食魅厨房为您提供各式精美菜肴，异域风情的巴厘岛温泉。\r\n　　南昌米兰假日酒店不仅仅提供住宿，更是一种生活方式。地处闹市然而幽静致远，体现了个性和时尚的完美结合，是您寂寞旅途中一个温暖的栖息地。无论您身处何地，何不跟随我们，坐享悠然的感觉。\r\n　　南昌米兰假日酒店按国际标准打造，具有国际级接待水准的商务酒店。酒店聘请国际著名设计师进行整体设计规划，独具匠心、典雅精致、使文化与艺术融为一体。\r\n南昌米兰假日酒店位于天佑路距南昌火车站、汽车站一步之遥。楼高10层，拥有各类宽敞舒适的客房。酒店有著名的老树咖啡，极富特色的食魅厨房为您提供各式精美菜肴，异域风情的巴厘岛温泉。\r\n　　南昌米兰假日酒店不仅仅提供住宿，更是一种生活方式。地处闹市然而幽静致远，体现了个性和时尚的完美结合，是您寂寞旅途中一个温暖的栖息地。无论您身处何地，何不跟随我们，坐享悠然的感觉。\r\n　　南昌米兰假日酒店按国际标准打造，具有国际级接待水准的商务酒店。酒店聘请国际著名设计师进行整体设计规划，独具匠心、典雅精致、使文化与艺术融为一体。',
        'area/1101/hotel/4.jpg', '245', '1');
INSERT INTO `hotel`
VALUES ('5', '1101', '嘉莱特酒店', '南昌东湖区广场南路10号', '235345435',
        ' 嘉莱特和平国际酒店是南昌市第一家按照五星级标准建造，具有国际一流接待水准的商务酒店，并于2008年1月7日荣膺为国家五星级旅游饭店。\r\n    酒店由国外著名设计师进行总体设计规划，秉承现代欧派风格，设计独具匠心，典雅精致，气派非凡，自然风情洋溢，融艺术化与人文化为一体。\r\n    酒店位于南昌黄金地段商业中心 —— 八一大道广场南路 10 号。承接八一广场鼎沸人气，四通八达，路路畅顺，距火车站仅 800 米之遥，昌北国际机场仅 30 分钟车程，大型商业、外贸、银行等机构近在咫尺，尽享地利之便，汇聚都市风采，紧握时代脉搏。\r\n    酒店总建筑面积约 4 万平方米，高 32 层，各类型客房 252 套。独具特色的中餐厅、宴会厅、西餐厅等可一次性接待 1200 余人，是目前南昌接待能力最大的酒店。风格迥异的各式佳肴使贵宾尽享天下美食，浪漫的嘉莱特酒吧让您尽享真我的风采！\r\n    酒店的娱乐设施齐全，室内游泳池、健身中心、美容美发厅、桑拿按摩和足浴区域，水疗保健、室内游泳池，应有尽有，令倦意全消。娱乐中心更是您舒展身心的乐园，无论是室外网球场、还是室内棋牌室、 KTV 会馆，都倍感悠然自在。\r\n    酒店员工将秉承“ CAN DO ”的服务理念，为宾客提供高品质的服务特色。不断追求卓越，以严谨、高效的管理和真诚的服务为海内外宾客提供一个温馨的家外之家。无论您是商务公干，还是旅游度假，在这里您都能感受到方便、舒适、自然。',
        'area/1101/hotel/5.jpg', '143', '1');
INSERT INTO `hotel`
VALUES ('6', '1101', '南昌唐古拉酒店', '	红谷滩新区翠苑路980号 近翠苑路地铁口', '124434',
        ' 南昌唐古拉酒店是全球最大的国际经济型连锁酒店之一，南昌红谷滩店是宜必思目前入驻江西省的第一家店。酒店位于南昌红谷滩新区---南昌市红谷滩新区翠苑路乡企城11，12栋。地处南昌经济、政治、文化娱乐交流中心地段。毗邻商业中心：3C未来谷（数码产品），天虹商城，万达广场，华润万家，法国欧尚等多个大型购物中心。紧临政治中心：南昌市委市政府，国际会展中心，奥体中心，国际金融中心。周边休闲旅游的景点有：摩天轮（红谷滩标志型建筑），秋水广场（亚洲第一高音乐喷泉），滕王阁（江南四大名楼之一），南昌八一起义纪念馆。南昌大学、航空大学、江西科技师范学院等高等学府也尽在周边。酒店交通便利，出行方便，距离南昌会展中心步行15分钟路程，距离南昌火车西站仅需20分钟车程，距离南昌昌北机场仅需30分钟车程。无线网络全面覆盖，更能满足商务客人的需求，是您商务出差，旅游的理想下榻酒店。',
        'area/1101/hotel/6.jpg', '435', '1');
INSERT INTO `hotel`
VALUES ('7', '1102', '九江汉庭酒店', '浔阳区878号', '1112344',
        ' 宜必思酒店是全球最大的国际经济型连锁酒店之一，南昌红谷滩店是宜必思目前入驻江西省的第一家店。酒店位于南昌红谷滩新区---南昌市红谷滩新区翠苑路乡企城11，12栋。地处南昌经济、政治、文化娱乐交流中心地段。毗邻商业中心：3C未来谷（数码产品），天虹商城，万达广场，华润万家，法国欧尚等多个大型购物中心。紧临政治中心：南昌市委市政府，国际会展中心，奥体中心，国际金融中心。周边休闲旅游的景点有：摩天轮（红谷滩标志型建筑），秋水广场（亚洲第一高音乐喷泉），滕王阁（江南四大名楼之一），南昌八一起义纪念馆。南昌大学、航空大学、江西科技师范学院等高等学府也尽在周边。酒店交通便利，出行方便，距离南昌会展中心步行15分钟路程，距离南昌火车西站仅需20分钟车程，距离南昌昌北机场仅需30分钟车程。无线网络全面覆盖，更能满足商务客人的需求，是您商务出差，旅游的理想下榻酒店。',
        'area/1102/hotel/1.jpg', '234', '1');
INSERT INTO `hotel`
VALUES ('8', '1102', '九江铂悦·仙境酒店', '九江 濂溪区 金凤路65号 ，近旅游集散中心。 【 山下 】', '25423543',
        '九江铂悦·仙境酒店是一家集客房、餐饮、会议、娱乐等为一体的新一代智能化精品酒店，也是我国中部五省首家采用智能云平台“机器人服务”的高端智能化酒店。酒店座落于“三江之口，七省通衢”的中国魅力城市——九江市城区中心地带，毗邻长运加油站，万达广场，禧徕乐国际商业中心，距离火车站1.5公里，交通便利。去庐山旅游，因为入住庐山牯岭大酒店后，在其工作人员的推荐下，说是同一家公司在九江的分店，所以选择入住这家酒店。酒店刚开业不久，装修很有格调，原以为新酒店会有气味，房间里有一些绿植，感觉没什么气味！客房的床垫睡的很舒服，看了一下是雅兰的床垫！酒店在长途汽车总站的附近，上庐山很方便！对了，我们房间还有一个很萌很可爱的机器人小白，小孩很喜欢！两百多能入住这样的酒店，很超值的一次入住哦！早餐尽管品种不是很多，但很精致！建议酒店的标识尽快完善，电梯有点不好找！会推荐给其他好友的啦',
        'area/1102/hotel/2.png', '112', '1');
INSERT INTO `hotel`
VALUES ('9', '1102', '九江锦都龙荷酒店', '九江 濂溪区 庐山大道99号 ，近九江外国语学校。', '2342342',
        '九江锦都龙荷酒店地处九江市庐山大道，周边交通便捷，餐饮、商铺、购物云集。按国际标准打造，具有国际级接待水准的商务酒店。酒店聘请国际著名设计师进行整体设计规划，独具匠心、典雅精致、使文化与艺术融为一体。',
        'area/1102/hotel/3.png', '234', '1');
INSERT INTO `hotel`
VALUES ('10', '1103', '赣龙大酒店', '赣州 章贡区 红旗大道50号 ，近南门广场', '1232323',
        '　赣州赣龙大酒店位于红旗大道，毗邻南门广场，地理位置优越，交通便利。\r\n　　赣州赣龙大酒店是一家集食宿、娱乐为一体的综合型大酒店，装修华贵典雅。大厦共十七层，一楼为酒店大堂，设有大堂酒吧、商务中心；三楼为中餐厅，设有多功能宴会厅和包厢。四楼至十二楼为客房，拥有不同装修风格与档次的客房。\r\n　　行政楼层于2012年全新装修，以“科技”为品牌，打造精品客房，拥有全方位智能控制系统，创造独特的宾客体验。\r\n　　酒店十三楼建有三个豪华会议厅，登上十七层顶楼，纵观赣州市区，古城风光尽收眼底。\r\n　　酒店管理严谨，服务周到，深受海内外宾客的赞誉，连续多年荣获“江西省明星旅游饭店”称号。全体员工秉承“用心服务，以情感人”的服务理念，我们真诚恭候您的光临！\r\n赣州赣龙大酒店位于红旗大道，毗邻南门广场，地理位置优越，交通便利。\r\n　　赣州赣龙大酒店是一家集食宿、娱乐为一体的综合型大酒店，装修华贵典雅。大厦共十七层，一楼为酒店大堂，设有大堂酒吧、商务中心；三楼为中餐厅，设有多功能宴会厅和包厢。四楼至十二楼为客房，拥有不同装修风格与档次的客房。\r\n　　行政楼层于2012年全新装修，以“科技”为品牌，打造精品客房，拥有全方位智能控制系统，创造独特的宾客体验。\r\n　　酒店十三楼建有三个豪华会议厅，登上十七层顶楼，纵观赣州市区，古城风光尽收眼底。\r\n　　酒店管理严谨，服务周到，深受海内外宾客的赞誉，连续多年荣获“江西省明星旅游饭店”称号。全体员工秉承“用心服务，以情感人”的服务理念，我们真诚恭候您的光临！',
        'area/1103/hotel/1.png', '123', '1');
INSERT INTO `hotel`
VALUES ('11', '1103', '赣州宾馆', '赣州 章贡区 红旗大道50号 ，近南门广场', '1232434',
        '1989年开业  2008年装修  130间房   联系方式　　\r\n　　赣州宾馆位于健康路，地处市中心繁华地段，馆内环境优雅是一座园林式宾馆。被江西省委、省政府授予“省级文明单位”，被全国旅游星级饭店评定为银叶级绿色饭店，是中央党政机关公务出差和会议定点饭店。 \r\n　　赣州宾馆拥有豪华商务套间、豪华商务标间及豪华商务单间，客房装修新颖，造型别致，可满足各层次客人的需求；有多功能会议厅及大、小会议室7个，配有先进的音响和投影设备，可承接50至300人的各种会议。宴会厅装修考究，可承办各种大型宴会，一次性可供600人同时用餐，有十余个各式KTV包厢、三个豪华大包厢供客人选用。院内设有大型停车场，现代健身房和歌舞娱乐城。\r\n　　赣州宾馆是您洽谈商务，下榻休息的理想选择！',
        'area/1103/hotel/2.png', '90', '1');
INSERT INTO `hotel`
VALUES ('12', '1103', '赣州蓝璞酒店', '赣州 章贡区 红旗大道50号 ，近南门广场', '3535345',
        '2014年开业  2014年装修  225间房   联系方式　　\r\n　　赣州蓝璞酒店位于赣州市中心市政区域中心地段的赞贤路，距市政府咫尺之遥。\r\n　　赣州蓝璞酒店楼高72米，共23层，总建筑面积3万平方米，气派雄伟。\r\n　　酒店配备两层大型地下停车场及室外停车场；设有雅致舒适的客房，所有客房均由名家设计，设施齐备，环境舒适。\r\n　　酒店设有中、西餐厅，共有餐位500多个，并设有高端豪华包房11间。中餐包房设计时尚、高雅；西餐厅由室内、室外两部组成，可迎合客人的不同需求。所有名贵海鲜、高档食材每日空运至赣，均由名厨主理。\r\n　　五楼茶坊设有17间豪包，清新雅致、宽敞明亮，是商务洽谈、私人聚会、以茶会友的完美场所；高贵典雅的多功能宴会厅及功能齐全的、配备顶尖设施的会议室，可供优质的会务服务，确保每一项商务和社交盛事圆满成功。酒店可根据客人的要求提供大小不同的会议场地，满足客人各种会议需要。\r\n　　酒店KTV采用先进的高端的音响、灯光设备、环回立体声效果、高品质受，让您在工作闲暇之余与友人共聚一堂K歌。\r\n　　赣州蓝璞酒店由香港亿锋顾问有限公司派驻管理专家顾问团队管理，引入全新的管理服务理念，拥有一支专业素质高的员工队伍。酒店用心服务，精益求精，务求令每一位客人宾至如归。',
        'area/1103/hotel/3.png', '123', '1');
INSERT INTO `hotel`
VALUES ('13', '1104', '上饶沐鑫城市酒店', '上饶 凤凰中大道（帅特龙广场） ，近市政府。', '23435435',
        '江西（上饶）沐鑫城市酒店是按照国际四星级酒店秉持“精致高雅，时尚舒适，豪华气派”的设计理念及装修。并由严武国际集团独资兴建，酒店地处上饶市信州区凤凰中大道中段，交通便利，离市中心0.5公里，距火车站约5分钟车程，距新飞机场约10公里。\r\n　　酒店拥有各类豪华商务套房、豪华休闲套房及高档标准房等，共计141间（套），客房在采景效果上匠心独具，时尚通透的卫浴设施，并提供殷勤贴心的管家式服务，酒店所有客房独家采用移动高速4G网络宽带，WIFI覆盖全酒店。国际、国内长途直拨电话，百套高清晰卫星电视接收，迷你酒吧，私人保险箱等。带给您品味非凡的尊贵享受，为商务名流人士下榻之首选。\r\n　　马克西姆自助餐厅设在酒店4楼，以经营特色自助餐为主装饰精美，提供中西式美食风味、东方美食及本地特色菜肴。热菜、凉菜、养生煲、中西糕点、热饮、冷饮、小吃等百余品种，并设有独特包间，是您商务聚会、生日派对及休闲娱乐的最佳选择。\r\n　　    H2咖啡休闲酒吧设在5楼，柔和的灯光，优雅的环境，更有种类繁多的各类名茶名酒、咖啡。精美小点、鲜榨果汁等，是您休闲聊天上网的好去处，给您带来无尽的享受。\r\n　　酒店拥有三个不同规格的会议厅（室），配有现代化的高清晰多媒体等先进音响设备、便于您组织各类会议、商务洽谈、歌咏晚会、新闻发布，能满足您不同重要商务活动的理想场所。\r\n　　酒店一楼设有精品商场，商场内引进世界及国内一线品牌的工艺品及著名雕刻艺术品及当地土特产品琳琅满目，一应具有。酒店设有大型停车场，为住店客人提供泊车。\r\n　　\r\n　　酒店宗旨：拥有精品设施 提供精细服务 享受精美人生',
        'area/1104/hotel/d3ffe866-8ea5-4101-9722-c3a0d71dd9fa.png', '345', '1');
INSERT INTO `hotel`
VALUES ('14', '1104', '欣凯皇冠酒店', '上饶县 武夷山大道4号 ，近县广场。', '4554444',
        '　上饶欣凯皇冠酒店位于上饶市信州区凤凰大道和紫阳大道的交接口处，距上饶东高速路口仅10分钟左右车程，距长途客运中心约5分钟车程，地理位置优越，交通便利。\r\n　　\r\n　　酒店拥有各式客房200多间/套，每间房间均通过人性化、创新性的空间设计规划，为你提供体贴入微的休憩环境。\r\n　　\r\n　　餐饮和会议服务设施完善，中餐厅、西餐厅共800多个餐位，18间各种格调的宴会包房，包罗万象，璀璨夺目的精美膳食，令你有品尝不尽的诱人感受。\r\n　　\r\n　　500平方米的无柱多功能宴会厅，5间各种规格的会议厅，致力于提供最高水准的服务，无论你是举办会议、宴请，还是节日庆典，都将获得最满意的服务。\r\n　　\r\n　　康乐服务设施俱全，欣凯水疗会所、阳光健身房、环境优雅的台球厅等优良、齐备的康乐设施，令宾客享尽乐趣。\r\n　　\r\n　　酒店本着全方位的服务，主动式的服务，让每个客人感受到关心、关爱的服务理念，为商务活动、旅游休闲等客人提供健康、舒适、超值的人性化服务',
        'area/1104/hotel/4339624f-b670-4a9c-967e-7461cf2c2801.png', '230', '1');
INSERT INTO `hotel`
VALUES ('15', '1104', '全季酒店', '上饶 凤凰大道广信大道店交汇处（星河国际2号楼） ，近上饶师院。', '3423567',
        '　全季酒店（上饶行政中心店）地处凤凰大道广信大道交汇处，毗邻上饶市政府，紧靠市民公园、创业公园，于2016年11月试营业，酒店层高20层，拥有各类豪华商务套房、豪华大床房及高档标准房等，共计116间（套），WIFI覆盖全酒店，国际、国内长途直拨电话，百套高清晰卫星电视接收。酒店设有地下两层大型停车场，为住店客人提供免费泊车。酒店距离上饶市政府、市行中心政服务100米，距市民公园、创业公园100米，距市中心0.5公里，距火车站10分钟车程，距上饶机场约10公里；距灵山20公里，国家5A级旅游景区三清山70公里，距婺源138公里；途经公交6路车可直达火车站，途径酒店4路、8路、10路车可直达市区，途径酒店4路车可直达带湖路汽车站。\r\n所属集团 华住\r\n华住酒店集团 (NASDAQ:HTHT)，是中国领先的多品牌酒店集团，在全国370多座城市，运营3,000多家酒店，并拥有近70,000多名员工。自2005年创立以来，华住始终以专业而高效的智能化管理系统，专注于为客户提供高品质和多元化的住宿和出行体验，成就美好生活。 自2014年，华住酒店集团与雅高酒店集团形成长期战略联盟，共同开辟在华酒店业务。目前，华住运营的酒店品牌已经覆盖所有市场，包含高端市场的美爵、VUE、禧玥，中端市场的诺富特、美居、漫心、全季、桔子水晶、桔子精选、CitiGO、星程、宜必思尚品，以及大众市场的宜必思、汉庭优佳、汉庭、怡莱、海友等知名酒店品牌，满足从商务到休闲的个性化需求。“华住会”，作为华住推出的会员俱乐部，也是一个高效、简单、温情的酒店预订平台，正为全球超过1亿会员提供住宿、出行、购物等服务。 以“成为世界级的伟大企业”为愿景的华住酒店集团，在创始人季琦的带领下，已经成为中国发展最快的酒店集团之一。2010年，华住在美国纳斯达克成功上市。目前，集团位列美国Hotels杂志公布的全球酒店集团排名第9位，旗下汉庭品牌连续四年荣登BrandZ最具价值中国品牌100强。',
        'area/1104/hotel/e3605b89-afd7-45d4-abcf-b9ed2ab2b2e5.png', '135', '1');
INSERT INTO `hotel`
VALUES ('16', '1105', '明月山大酒店', '宜春 温汤镇温泉路110号 ，近古井泉眼', '2343567',
        '明月山温汤大酒店位于温汤镇中心位置，与温泉古井泉眼仅一墙之隔，并将世界独有的富硒温泉直接接入酒店客房，部分房型设有独立泡池，让你足不出户便可享受浪漫、舒适、惬意、的温泉之旅 。酒店采用了中式园林的装修设计风格，并精选尊贵、环保的红木、实木家具。红木、实木家具有助于人的身体健康，其散发的芬芳气味有以安神和降血压的功效，长期接触还能促进细胞再生，预防皱纹出现，让您越睡越年轻。酒店所有客房采用无油漆设计，环保而健康。\r\n　　明月山温汤大酒店于2016年元旦正式营业，酒店根据功能分区不同设有养生客房、养生音乐餐厅、养生SPA中心。为了让您拥有更优质的体验，温汤大酒店与格兰云天酒店管理公司达成合作，并委派专业管理团队进行管理，力求打造温汤乃至全省独具文化特色主题的酒店。',
        'area/1105/hotel/f47f499a-d062-4866-8c3e-ced4157ee05c.png', '230', '1');
INSERT INTO `hotel`
VALUES ('17', '1105', '宜春红林大酒店', '宜春 市府北路10号 ，近明月北路。', '123243',
        '　宜春红林大酒店位于宜春市府北路，地处商业、文化、购物、金融中心地段，紧邻宜春市行政中心、市文化艺术中心、市博物馆、市科技馆等。\r\n　　\r\n　　这里坐拥宜春市CBD核心区，距离风景秀丽的人民公园、袁州公园咫尺之遥，距离富硒温泉温汤镇约18公里、国家5A景区明月山约28公里。\r\n　　 \r\n　　酒店是集餐饮、住宿、商务、休闲度假于一体的多功能现代化豪华涉外酒店。装饰风格豪华时尚、精致典雅；配备各种房型满足高端商务、度假客人的不同需求。\r\n　　\r\n　　房间特配西班牙Giessdorf高档卫具；全球床具品牌King Koil，是ICA全球认证的床垫品牌，定能给你带来深度睡眠。绝妙的城市花园景观，独特的个性化服务更让你感受亲切的奢华。\r\n　　\r\n　　酒店附设大堂吧、24小时茶市等配套设施，供你商务洽谈、品佳肴美食，享轻松愉悦；同时设有豪华红林多功能厅、广林厅等6个宴会厅及8间不同规格贵宾会议室，满足你商务会议、宴会等多元化消费需求。',
        'area/1105/hotel/c20e15db-4806-4798-8c4d-8c11b40a172e.png', '256', '1');
INSERT INTO `hotel`
VALUES ('18', '1105', '宜春秀江宾馆', '宜春 袁州区 袁山东路699号 ，近高铁站、万达购物广场。', '2324324',
        '全新的秀江宾馆是袁州区首家豪华园林式酒店，隶属于袁州区政府指定接待宾馆，宾馆坐落于宜春市袁州区区政府旁，毗邻高铁站、万达购物广场。伴山而居，依水而眠，得天独厚的地理优势造就了山清水秀、鸟语花香的幽美环境，是您放松身心、休闲度假的首选之地，是袁州区继宜春万达商业圈财富中心的又一个新地标建筑。\r\n　　宾馆占地面积20000多平方米，建筑面积15040平方米，分客房楼、餐饮楼、会议楼、独立水上餐厅及傍湖别墅。其中，客房楼有豪华大气的接待大厅、西餐厅、大堂吧、健身房、可容纳110人左右的会议室及贵宾接待室，同时还拥有标准客房、棋牌房、豪华套房98间。餐饮楼设有各类包厢7间、豪华水上包厢4间，大型宴会厅。会议楼设有大型多功能会议厅及两个中小型会议室；此外还设有150个停车位，户外网球场、羽毛球场等娱乐设施。\r\n　　秀江宾馆竭诚为商务旅客提供一流的服务和齐全的酒店设施。我们将秉承“宾客至上，服务第一”的宗旨为您提供优质服务。时刻为您营造温馨、恬适、雅致的食宿环境。',
        'area/1105/hotel/325ce519-65e0-4992-81a2-bf4ae6ac20a3.png', '150', '1');
INSERT INTO `hotel`
VALUES ('19', '1106', '吉安海联国际饭店', '吉安市吉州区大桥西路20号城北大转盘东侧', '123243',
        '　　吉安海联国际饭店位于井冈山下、城南新区，供电局旁，广大高速、武吉高速、泰井高速、105国道、衡茶吉铁路、京九铁路纵贯境内，距离井冈山景区1小时。酒店总投资8.8亿，建筑面积8.2万平方米。\r\n　　吉安海联国际饭店的客房典雅大方，舒适整洁的环境，让宾客放松身心，忘却旅行中的舟车劳顿。店内设中、西餐厅、会议中心、商务中心、会所、健身、桑拿、KTV、茶艺、洗浴、购物、康乐、美发等十几个项目，是宾客商务接洽、休闲度假的理想选择。',
        'area/1106/hotel/8ef964e0-509c-4a67-bebc-30ded5cb6390.png', '235', '1');
INSERT INTO `hotel`
VALUES ('20', '1106', '圣欧顿大酒店', '吉安 青原区 文天祥大道8号 ，近火车站、吉安大桥。', '345567',
        '　吉安圣欧顿大酒店毗邻吉安火车站，可俯览市区、赣江和吉安第一桥的无限美景。酒店靠近105国道，距樟吉高速10分钟车程，距井冈山机场30分钟车程，地理位置优越，交通非常便利。\r\n　　吉安圣欧顿洲际大酒店营业总面积2万平米，主楼共24层，店内设施齐全，行政楼层、无烟楼层、特色客房、专业中西餐厅、会议室、品牌足浴、康体健身、桑拿休闲等项目可满足客人的不同需求。\r\n　　拥有多类客房供您选择，客房装修风格独特，设计理念别俱一格，客房使用专业床垫并提供不同规格的睡枕，确保您在旅途中拥有良好的休息及睡眠，房间另设一体电脑、宽带上网、国际电话直拔、棋牌等配套设施；中、西餐特邀粤、赣名厨主理，主营燕、鲍、翅、生猛海河鲜和吉品赣菜，宴请之时彰显宾客之尊贵主人之盛情。\r\n　　开业以来坚持自制榨油，倡导现代绿色健康饮食消费，各式特色豪华包房及多功能会议室，可同时容纳近千位宾客同时就餐。“尽心尽力、尽善尽美”，是圣欧顿酒店人不断追求的目标。',
        'area/1106/hotel/ba18e0d8-1189-4b70-ad34-73a388f7d44c.png', '345', '1');
INSERT INTO `hotel`
VALUES ('21', '1106', '中信会议中心', '井冈山 茨坪镇 中信梨坪 ，319国道旁', '123456',
        '　井冈山中信会议中心位于江西省西南部，湘赣两省交界的罗霄山中段，是著名的革命旅游文化圣地。该项目基地从西南到东南呈狭长带状，本项目利用这一地形，依山而建，由入口处呈阶梯状排布，与周围的自然山体溶为一体，而且还减少了土方量和工程造价。建筑整体分为三部分，地势平坦的地方做体量大的会议中心，而进深较小的国宾馆选择山腹部位，院落式套房选择山体部位，以达到闹中取静的效果。建筑的形象考虑和当地秀美的自然环境相结合，采用当地的自然材料毛竹，木料和石材。使建筑具有清秀的灵气，也是建筑对所处地域的精彩辉映。',
        'area/1106/hotel/9c18dd53-2765-4068-b3ec-a8f236ede1fd.png', '456', '1');
INSERT INTO `hotel`
VALUES ('22', '1107', '非繁城品酒店', '抚州 临川区大桥南路 ，近金巢大道', '123243',
        '非繁城品酒店（抚州轻纺城大润发店）位于临川区大桥南路，抚州市轻纺城旁，周边餐饮、娱乐、购物广场，银行、医院一应俱全。\r\n　　酒店距离长途汽车站、抚州高铁站及各景点都不远，交通便捷。\r\n　　酒店建筑面积3500平米，拥有商务间、豪华间、行政间在内的各式优质客房。每间客房均设有宽带上网、液晶电视、中央空调、24小时热水、独立淋浴间等设施。套房配有自动麻将桌。\r\n　　为满足商务人士的需求，大小会议厅可容纳20-100人，同时提供专业、高效的服务。\r\n所属集团 7天（铂涛）\r\n7天连锁酒店集团（7 Days Group Holdings Limited）创立于2005年，2009年11月20日在美国纽约证券交易所上市（股票代码：SVN）。作为第一家登陆纽交所的中国酒店集团，7天连锁酒店秉承让顾客“天天睡好觉”的愿景，致力为注重价值的商旅客人提供干净、环保、舒适、安全的住宿服务，满足客户核心的住宿需求。7天连锁酒店现已拥有分店超过1500家， 7天连锁酒店室外覆盖全国近30个省和直辖市共200个主要城市，业已建成经济型连锁酒店全国网络体系。7天连锁酒店建立的“7天会”拥有会员超过5000万，是中国经济型酒店中规模最大的会员体系。作为业内科技领航者，7天是目前少数能“7×24小时”同时提供多达5种便利预订方式的连锁酒店，包括：网上预订、电话预订、WAP预订、短信预订，手机客户端。',
        'area/1107/hotel/fd4a38f7-5f36-42f7-8974-3f2de63413ac.png', '234', '1');
INSERT INTO `hotel`
VALUES ('23', '1107', '利兹堡精品酒店', '抚州 曾巩大道 ，近万达广场(保育院旁)。', '123456',
        '　开业于2017-01-01，相信无论您是一日游还是深度游，抚州利兹堡精品酒店都是您抚州之行的不二选择。酒店距离抚州站仅有5km。若是计划在酒店周边好好游玩，梦湖景区和名人雕塑园定能满足您的需求。\r\n酒店为您在客房内配备了国际长途电话、房内保险箱和空调，所有入住的客人均可便捷的使用。服务人员会提前为您准备好电热水壶和免费瓶装水，以满足您的饮水需求。浴室内提供拖鞋、24小时热水和吹风机，让您感受到宾至如归的享受。旅客可以在中餐厅享用可口的美食。您可以到大堂吧与朋友畅饮聊天度过闲暇时光。如果旅客愿意，酒店可以提供满足需求的客房送餐服务。\r\n对于顾客来说，可以在室外游泳池享受锻炼的乐趣。酒店配置有会议厅，定能满足您商务活动方面的需求。24小时开放的前台服务可为您随时提供信息，以帮助您探索这个魅力之都。入住酒店的旅客，可以使用酒店免费停车场。',
        'area/1107/hotel/0fcfd744-c310-4b8c-a35d-b4826a700392.png', '134', '1');
INSERT INTO `hotel`
VALUES ('24', '1107', '源清大酒店', '抚州 金巢大道与南门路交汇路口 ，金巢广场对面。', '345567',
        '抚州源清大酒店是按照准星级标准设计装修的大型商务旅游酒店，地理位置得天独厚，整体建筑面积12000余平方米。由主楼十一层构成，设有各类型的总统套房、豪华套房、标准房及单间，共计260余张床位及大型地下停车场；酒店还设有容纳500余人的宴会厅、会议室及咖啡厅、休闲会所、棋牌娱乐等完善服务配套设施。完美先进的消防安全监控系统，格力空调系统，24小时热水供应系统，CENTREX通讯系统和闭路电视系统，从而构筑一座安全舒适的现代化水平较高的大型商务旅游酒店。     \r\n  抚州源清大酒店地理位置优越，设施齐全，服务周到，安全舒适，适合开展商贸洽谈，文化交流等活动，是旅游观光的理想下榻之处。',
        'area/1107/hotel/f4cbcf78-269b-4b34-b3ac-3c85900ede7a.png', '234', '1');
INSERT INTO `hotel`
VALUES ('25', '1108', '枫丹白露酒店', '鹰潭 月湖区 高新区炬能路1号', '234234',
        '鹰潭枫丹白露酒店傲踞龙虎山必经之路，位于月湖区炬能路，毗邻鹰潭市体育馆和月湖岩公园，轻松通达繁华的商业区及各大旅游景点，火车站、汽车站近在咫尺，抵达龙虎山景区仅需约15分钟车程。\r\n　酒店集商务、会议、旅游、度假、休闲于一体，主体建筑面积3万平米，分地上21层地下2层共计23层。\r\n  在这里，有着包括总统套房在内的各类格调高雅的客房、套房供你选择。会议及活动场地超过1600平方米， 其中包括可容纳400余位宾客的多功能宴会厅、4间自然采光充沛的会议室，设施先进，服务专业，是世界级高端会议与中西宴会的理想场所。\r\n　　\r\n　　豪华的中餐包房、西餐厅及大堂吧提供精品粤菜、赣菜、养生菜、国际美食及世界佳酿。\r\n　　\r\n　　康乐会所、精品购物中心、茶艺室、红酒屋、棋牌室、健身房、空中游泳池、SPA馆等配套设施一应俱全。',
        'area/1108/hotel/63e9f294-9621-44d9-8cc8-9bb415a04b5a.png', '157', '1');
INSERT INTO `hotel`
VALUES ('26', '1108', '鹰潭宾馆', '鹰潭 月湖区 林荫东路9号 ，近林荫东路和正大路交汇处。', '234234',
        '鹰潭宾馆是在原国民党海军司令桂永清官邸基础上创建的；濒临信江并与鹰潭市自然风景区区人民公园毗邻；距火车站、汽车站及船运码头、320、206国道，仅10分钟，距龙虎山国家级风景区16公里，交通便捷。鹰潭宾馆是市委、市政府重要的的接待场所，1973年邓小平同志曾下榻过本宾馆。',
        'area/1108/hotel/dda79eb6-970f-42db-82ea-57959f90fee9.png', '240', '1');
INSERT INTO `hotel`
VALUES ('27', '1108', '维也纳酒店', '鹰潭 月湖区 鹰西大道鹰潭国际眼镜城（君融国际）', '3423567',
        '　维也纳酒店(鹰潭国际眼镜城店)位于鹰潭国际眼镜城鹰西大道旁，是一家豪华商务度假型酒店。酒店毗邻信江，打造鹰潭全江景豪华商务度假型酒店，酒店主楼国际眼镜城双子楼是鹰潭市标志性建筑，鹰潭首家以欧陆式风情，巴洛克式设计风格商务度假型酒店，酒店邻信江河畔，入住酒店可尽览信江的秀丽风光。房间布置温馨典雅，设有愉梦之床、助眠音乐、百兆光纤、3秒速热时尚卫浴系统，使您尽享自由私密的淋浴按摩享受，为您提供超值、艺术、环保、健康、安全、助眠、便捷的360度全方位尊贵消费体验，让您感受到“绅士般品位，淑女般亲切”，维也纳家人期待您的莅临！',
        'area/1108/hotel/cfb8c9f8-b531-4fe7-9dd5-bedea2306857.png', '234', '1');
INSERT INTO `hotel`
VALUES ('28', '1109', '萍乡壹号公馆', '萍乡 安源中大道17号 ，近苏州东街。', '3423567',
        '萍乡壹号公馆地处安源中大道，近萍乡博物馆，周边娱乐、餐饮配套丰富，出行便利。\r\n　　\r\n　　公馆客房干净整洁，房内提供24小时热水、洗漱用品等，设施齐全，服务热情周到，为宾客提供便捷、轻松的住宿体验。\r\n　　\r\n　　公馆1层设有大堂和西餐厅，2至3楼有公馆包厢18个和大厅；9楼为多功能厅；附楼还有国际宴会厅，为公馆多功能厅，可容纳百桌酒席，1000余人同时用餐。',
        'area/1109/hotel/c3b389c2-94c5-44b6-8864-bf5105826723.png', '230', '1');
INSERT INTO `hotel`
VALUES ('29', '1109', '雅致精选酒店', '萍乡 安源区 萍安北大道萍乡学院旁', '2423434',
        '萍乡市雅致精选酒店位于萍乡市安源区萍安北大道，距火车站6分钟车程，距高铁站（北站）15分钟车程，5分钟即可到达市中心步行街；商务、购物、娱乐、休闲方便快捷。\r\n　　\r\n　　酒店集商务、会议、住宿、餐饮、康体及娱乐休闲于一体的大型服务业综合体，这里有各式高级客房、精选客房和豪华套房百余套，环境优雅，温馨舒适；客房均配备宽带端口，无线wifi，并且长话市话打。\r\n　　\r\n　　酒店内中、西餐厅提供琳琅满目的美食及各式早点佳肴，多功能会议中心可承接20-100人的各类会议，置身雅致精选酒店，既可领略酒店文化的独特魅力、又可享受方便、快捷的全方位贴心服务，是休闲度假、接待宾客和商旅出行的理想选择。',
        'area/1109/hotel/426b4f39-23bf-4cad-a8b9-20512ea4bbb1.png', '222', '1');
INSERT INTO `hotel`
VALUES ('30', '1109', '萍乡吉啡酒店', '萍乡 安源区 公园南路211号', '234234',
        '　萍乡吉啡酒店为商旅客人营造“旅途中的啡凡存在”，提供完美舒适、品位个性的服务，喆啡酒店独有的24小时的咖啡厅、咖啡式check in，开放式书吧、欧式火炉……咖啡馆文化与酒店的完美结合，为您创造出充满人文、社交、格调的氛围感受，我们诚挚地期待您来喆啡醇享生活！品牌宣传和分店Q+展示：24小时咖啡馆、文化书吧、艺术品长廊、自助餐厅、等多功能空间。',
        'area/1109/hotel/d67a51ed-8874-46ae-89ce-c35d175c2c23.png', '145', '1');
INSERT INTO `hotel`
VALUES ('31', '1110', '华瑞圣欧顿酒店', '新余 渝水区 抱石大道561号', '222222',
        '新余华瑞宾馆位于渝水区抱石大道，近抱石公园，地处市中心繁华商业地带，交通十分便利。\r\n　　新余华瑞宾馆建筑面积17000平方米，宾馆主楼19层。宾馆设计新颖，装饰温馨典雅，客房设备先进。宾馆设有大小会议室、中餐厅，4楼中餐厅承接各种婚宴，毕业宴等各类宴席、数十间宴会单间，是商业旅行、团队会议、公务接待的理想居停所。',
        'area/1110/hotel/1c1ba361-94c1-45ce-86b9-a183b1bf767a.png', '250', '1');
INSERT INTO `hotel`
VALUES ('32', '1110', '维也纳酒店', '新余 渝水区 渝州大道969号', '32221221', '新余 渝水区 渝州大道969号',
        'area/1110/hotel/5722c0c6-b08f-4575-9ff6-0e448c61e7f3.png', '222', '1');
INSERT INTO `hotel`
VALUES ('33', '1110', '尚客优酒店', '新余 渝水区 抱石大道710号', '43343434',
        '骏怡（尚客优）连锁酒店（新余抱石公园店）地处商业繁华地段及养生休闲中心，紧邻天然生态“氧吧”抱石公园东大门，交通十分便利。是您商务出差旅行约会的理想下榻之地，我们专业化的用心用情服务定能给您带来舒适温馨的感受。店门前即有直达新余高铁站及市内各处的公交专线，距新余火车站300米、长途汽车站150米、新余北站（高铁站）6公里、宜春机场60公里、昌北机场160公里、黄花机场350公理；距沃尔玛商城100米、步步高和新亚新商城150米，距新钢公司总部600米。',
        'area/1110/hotel/7e7a3cde-0496-4fb5-8084-3ec77495562a.png', '234', '1');
INSERT INTO `hotel`
VALUES ('34', '1111', '锦莱国际大酒店', '景德镇 昌江区 瓷都大道838号', '3423567',
        '景德镇锦莱国际大酒店是南昌大学后勤集团旗下的一家全功能型按四星级标准建造的高档商务酒店，位于千年瓷都景德镇市的政治金融中心（景德镇学院内），毗邻著名的AAAAA级陶瓷历史博览风景区，交通便捷，设施齐全。\r\n　　景德镇锦莱国际大酒店集商务、旅游、休闲、会议、食宿、娱乐、艺术瓷收藏为一体。酒店采用国际化标准的酒店管理理念模式，提供完善的综合服务设施以及完善自我的人性化服务。\r\n　　景德镇锦莱国际大酒店以诚信经营为本，积极树立酒店对外的品牌形象，以“团结敬业、求实创新、完善自我、争创一流”为企业精神，“亲切礼貌、安全周到、清洁舒适、温馨快捷”视为准则。\r\n　　锦莱国际大酒店以“星级有限，服务无限”的行动，迎接海内外宾朋！',
        'area/1111/hotel/9230c190-9e6f-4364-bb16-111f158c37f3.png', '145', '1');
INSERT INTO `hotel`
VALUES ('35', '1111', '景德镇朗逸酒店', '景德镇 昌江区 珠山西路5号 ，近市供电局。', '2342342',
        '景德镇朗逸酒店位于珠山西路，临近的商业、购物娱乐区等地方均可步行到达沿途可感受到景市的风貌。\r\n　　酒店由香港朗逸盛荟酒店集团投资和经营管理，于2016年7月已全面完成大堂、豪华客房及餐厅设施的更新装修工程；带给客人耳目一新的感觉，是市内提供客人住宿、餐饮的豪华商务酒店之一。\r\n　　酒店占地6000平方米，楼高10层，建筑面积15000平方米，拥有各式豪华客房、赣/粤风味餐厅、健身房、棋牌室及可容纳400人的会议厅，是党政机关出差、会议定点酒店之一，更荣获国家旅游局评为先进单位。\r\n　　本着“待客热诚开朗、客人舒适安逸”的经营理念，朗逸酒店期望成为您商务、会议、旅游之优选酒店。',
        'area/1111/hotel/6dc1a9cf-8fd8-4365-9bba-f667c3d9ede4.png', '235', '1');
INSERT INTO `hotel`
VALUES ('36', '1111', '景德镇昌飞宾馆', '景德镇 珠山区 朝阳路816号', '234234234',
        '景德镇昌飞宾馆座落于素有千年古陶瓷文化历史名城—瓷都景德镇市。宾馆设施完善，功能齐全，是一家集会议、商务、娱乐、餐饮、住宿于一体的综合性商务宾馆。宾馆拥有各类标准客房、豪华套房，风格迥异的中西餐厅可容纳700余人同时就餐，各类大小会议室7个。宾馆配备了领先的高清电视系统、无线wifi网络系统、中央空调系统和多媒体网络信息系统、康乐中心、商务中心、以及购物中心。\r\n　　景德镇昌飞宾馆内红花绿草，雕廊木榭，假山喷泉，风车流水，使人心旷神怡，美不胜收。古朴典雅、错落有致的建筑格局成为景德镇市的一道亮丽风景线。我们秉承个性化服务理念，让您在旅途中感受着细致入微的服务和家庭式的舒适与温馨；此时此刻，无论您身处商界的繁忙、旅途的奔波或是假日的小憩，这里始终是您最佳的选择。',
        'area/1111/hotel/eae129f9-beda-4fea-b848-4a3de39c7277.png', '124', '1');

-- ----------------------------
-- Table structure for hotelcomment
-- ----------------------------
DROP TABLE IF EXISTS `hotelcomment`;
CREATE TABLE `hotelcomment`
(
    `id`      int(11) NOT NULL AUTO_INCREMENT,
    `usernum` varchar(255) NOT NULL,
    `hid`     int(11) NOT NULL,
    `ptime`   datetime DEFAULT NULL,
    `content` text,
    `stage`   int(11) DEFAULT NULL,
    PRIMARY KEY (`id`),
    KEY       `hid` (`hid`),
    KEY       `num` (`usernum`),
    CONSTRAINT `hid` FOREIGN KEY (`hid`) REFERENCES `hotel` (`hid`),
    CONSTRAINT `num` FOREIGN KEY (`usernum`) REFERENCES `user` (`userId`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

