-- ----------------------------
-- Table structure for sccomment
-- ----------------------------
DROP TABLE IF EXISTS `sccomment`;
CREATE TABLE `sccomment`
(
    `cid`       int(11) NOT NULL AUTO_INCREMENT,
    `userNum`   varchar(255) NOT NULL,
    `secenyNum` int(11) NOT NULL,
    `ptime`     datetime DEFAULT NULL,
    `content`   text         NOT NULL,
    `stage`     int(10) DEFAULT NULL,
    PRIMARY KEY (`cid`),
    KEY         `secenyNum` (`secenyNum`),
    KEY         `userNum` (`userNum`),
    CONSTRAINT `secenyNum` FOREIGN KEY (`secenyNum`) REFERENCES `scenic` (`scenicId`),
    CONSTRAINT `unum` FOREIGN KEY (`userNum`) REFERENCES `user` (`userId`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8;

