INSERT INTO `sccomment`
VALUES ('1', '11', '1', '2018-04-02 14:46:02',
        '滕王阁位于沿江北路，赣江与抚河交汇处，高大约57.5米感觉比黄鹤楼和岳阳楼都要雄伟，传说中的江南三大名楼之一，唐代著名诗人王勃的一首《滕王阁序》，使之声名远播，如今我们带着怀古的情绪来到此地，时过境迁，古人描述的壮景已不复存在，还是文学作品都有其浪漫主义的风格，和实物是不严格匹配的。不过登阁远望，见赣水苍茫，江天辽阔，也是不错的风景，只是感叹自己没有王勃的胸怀和意境罢了，俗人就是俗人啊。',
        '1');
