-- ----------------------------
-- Table structure for scenic
-- ----------------------------
DROP TABLE IF EXISTS `scenic`;
CREATE TABLE `scenic`
(
    `scenicId`      int(11) NOT NULL AUTO_INCREMENT,
    `name`          varchar(255) NOT NULL,
    `scenicNum`     int(11) NOT NULL,
    `scenicAddress` varchar(255) NOT NULL,
    `pic1`          varchar(255) NOT NULL,
    `title1`        varchar(255) NOT NULL,
    `pic2`          varchar(255) NOT NULL,
    `pic3`          varchar(255) NOT NULL,
    `video`         text,
    `describle`     text,
    `cost`          double(10, 2
) NOT NULL,
  `time` varchar(255) DEFAULT NULL,
  `tel` varchar(255) CHARACTER SET ujis DEFAULT NULL,
  `category` int(11) DEFAULT NULL,
  `stage` int(11) DEFAULT NULL,
  PRIMARY KEY (`scenicId`),
  KEY `scenicNum` (`scenicNum`),
  CONSTRAINT `scenicNum` FOREIGN KEY (`scenicNum`) REFERENCES `area` (`areaNum`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of scenic
-- ----------------------------
INSERT INTO `scenic`
VALUES ('1', '滕王阁', '1101', '江西省南昌市西北部沿江路赣江东岸', 'area/1101/scenic/1.jpg',
        '滕王阁，江南三大名楼之一 ，位于江西省南昌市西北部沿江路赣江东岸，始建于唐朝永徽四年，因唐太宗李世民之弟——李元婴始建而得名，因初唐诗人王勃诗句“落霞与孤鹜齐飞，秋水共长天一色”而流芳后世。',
        'area/1101/scenic/1.jpg', 'area/1101/scenic/1.jpg',
        'https://v.qq.com/iframe/player.html?vid=n0344d5we2u&tiny=0&auto=0',
        '滕王阁，江南三大名楼之一 ，位于江西省南昌市西北部沿江路赣江东岸，始建于唐朝永徽四年，因唐太宗李世民之弟——李元婴始建而得名，因初唐诗人王勃诗句“落霞与孤鹜齐飞，秋水共长天一色”而流芳后世。贞观年间，唐高祖李渊之子、唐太宗李世民之弟李元婴曾被封于山东滕州故为滕王，且于滕州筑一阁楼名以“滕王阁”（已被毁），后滕王李元婴调任江南洪州（今江西南昌），因思念故地滕州修筑了著名的“滕王阁”，此阁因王勃一首“滕王阁序”为后人熟知，成为永世的经典。 滕王阁与湖北武汉黄鹤楼、湖南岳阳楼并称为“江南三大名楼”。历史上的滕王阁先后共重建达29次之多，屡毁屡建。\r\n\r\n2001年元月核准为首批国家AAAA级旅游景区。',
        '150.00', '8:00-22:00（双休日时间延长至晚上23:00）', '123456', '0', '1');
INSERT INTO `scenic`
VALUES ('2', '秋水广场', '1101', '江西省南昌市赣江中大道中段 ', 'area/1101/scenic/2.jpg',
        '秋水广场 ，位于江西省南昌市红谷滩新区的赣江之滨，紧邻行政中心广场，于2004年1月28日落成，是利用荒滩兴建，既防洪，又观景，与滕王阁隔江相望，再现了千古名篇《滕王阁序》中的\"落霞与孤鹜齐飞，秋水共长天一色\"的意境--秋水广场正是由此而得名。',
        'area/1101/scenic/2.jpg', 'area/1101/scenic/2.jpg',
        'https://v.qq.com/iframe/player.html?vid=l0550k66d6s&tiny=0&auto=0',
        '秋水广场 ，位于江西省南昌市红谷滩新区的赣江之滨，紧邻行政中心广场，于2004年1月28日落成，是利用荒滩兴建，既防洪，又观景，与滕王阁隔江相望，再现了千古名篇《滕王阁序》中的\"落霞与孤鹜齐飞，秋水共长天一色\"的意境--秋水广场正是由此而得名;是一座以喷泉为主题，集旅游、购物、观光为一体的大型休闲广场。红谷滩秋水广场上音乐灯光喷泉引人注目，是亚洲最大的音乐喷泉群，喷水池面积1.2万平方米，主喷高度达128米，是南昌市一靓丽景观。人们可一边欣赏音乐一边观看滕王阁美景。\r\n\r\n\"南昌秋水广场\"于2004年元月一日对外开放，广场总体平面为月牙形，依江而立。江岸线长1100米，最宽处110米。世界第二超级音乐喷泉 南昌秋水广场拥有目前全国最大的音乐喷泉群。 音乐喷泉位于秋水广场的中心地带，临江建有一个长280米、宽25米的大型江边悬挑平台。共配置了包括中心主喷、环形高喷、圆形摇摆喷泉和数控跑泉在内的共22个水景，水景长达800米，共装有636台水泵、1591个喷头，为亚洲最大的音乐喷泉群，是国际化观光旅游的大型休闲广场。',
        '0.00', '全天', '123456', '0', '1');
INSERT INTO `scenic`
VALUES ('3', '八一起义纪念馆', '1101', '南昌市西湖区中山路中段380号 ', 'area/1101/scenic/3.jpg',
        '南昌八一起义纪念馆成立于1956年，1959年10月1日正式对外开放，1961年被国务院公布为首批全国重点文物保护单位(所辖五处革命旧址--南昌起义总指挥部旧址、贺龙指挥部旧址、叶挺指挥部旧址、朱德军官教育团旧址和朱德旧居)，国家AAAA级旅游景区。',
        'area/1101/scenic/3.jpg', 'area/1101/scenic/3.jpg',
        'https://v.qq.com/iframe/player.html?vid=q05636blixj&tiny=0&auto=0',
        '南昌八一起义纪念馆成立于1956年，1959年10月1日正式对外开放，1961年被国务院公布为首批全国重点文物保护单位(所辖五处革命旧址--南昌起义总指挥部旧址、贺龙指挥部旧址、叶挺指挥部旧址、朱德军官教育团旧址和朱德旧居)，国家AAAA级旅游景区。\r\n\r\n南昌八一起义纪念馆位于江西省南昌市西湖区中山路，占地面积5903平方米。是江西省文化厅，南昌市文广新局共同管理的副处级文博事业单位。\r\n\r\n2016年12月，八一南昌起义纪念馆入选《全国红色旅游景点景区名录》。',
        '60.00', '8:00-20:00', '123456', '1', '1');
INSERT INTO `scenic`
VALUES ('4', '南昌万达海洋乐园', '1101', '江西省南昌市红谷滩九龙湖新区 ', 'area/1101/scenic/4.jpg',
        '南昌万达海洋乐园 坐落于南昌市万达文化旅游城里面的万达茂内，是由美国著名设计公司TVS担纲设计，韩国第一海洋馆经营公司--韩华集团和万达集体携手打造的中国内陆地区的顶级海洋馆。',
        'area/1101/scenic/4.jpg', 'area/1101/scenic/4.jpg',
        'https://v.qq.com/iframe/player.html?vid=d01909clyso&tiny=0&auto=0',
        '南昌万达海洋乐园 坐落于南昌市万达文化旅游城里面的万达茂内，是由美国著名设计公司TVS担纲设计，韩国第一海洋馆经营公司--韩华集团和万达集体携手打造的中国内陆地区的顶级海洋馆。场馆建筑面积近四万平米，水体量超过了两个\"水立方\";另外海洋生物300多种，鱼类数量2万8千尾。',
        '100.00', '早上10：00-晚上22:00点 ', '123456', '0', '1');
INSERT INTO `scenic`
VALUES ('5', '南昌之星摩天轮', '1101', '南昌市红谷滩新区红角洲赣江边上的赣江市民公园', 'area/1101/scenic/5.jpg',
        '\"南昌之星\"摩天轮，又名南昌之星，位于江西省南昌市红谷滩新区红角洲赣江边上的赣江市民公园。高160米，同时在世界上仅次于拉斯维加斯摩天轮(167米)和新加坡飞行者(165米)摩天轮，为南昌市标志性建筑。',
        'area/1101/scenic/5.jpg', 'area/1101/scenic/5.jpg',
        'https://v.qq.com/iframe/player.html?vid=a0527g9uwuo&tiny=0&auto=0',
        '\"南昌之星\"摩天轮，又名南昌之星，位于江西省南昌市红谷滩新区红角洲赣江边上的赣江市民公园。高160米，同时在世界上仅次于拉斯维加斯摩天轮(167米)和新加坡飞行者(165米)摩天轮，为南昌市标志性建筑。原为世界第一高摩天轮高度高于英国伦敦的伦敦之眼，现为世界第三高，国内第一高的摩天轮。\r\n\r\n\"南昌之星\"摩天轮由国家煤科研究总院上海分院设计，轮面彩灯由六七千根LED半导体照明器件构成，全部亮灯后除了可以显示一座直径为153米的巨型彩色时钟，还可根据需要设定不同图案，建筑动工于2005年，于2006年竣工，投资总额达1亿元人民币，诠释着南昌作为世界动感都会的昂首姿态，成为南昌的重要地标之一。',
        '150.00', '早上10：00-晚上22:00点 ', '123456', '0', '1');
INSERT INTO `scenic`
VALUES ('6', '梅岭', '1101', '江西省南昌市湾里区 ', 'area/1101/scenic/6.jpg',
        '梅岭风景名胜区最高点海拔841米（洗药湖），主要景观有：洗药湖避暑度假区、江南最大尼姑庵之一的天宁古寺、中国音乐鼻祖伶伦修炼地、古南昌十景之一的洪崖丹井、江西省最大的皇室墓葬皇姑墓以及狮子峰、脚鱼潭瀑布区、翟岩寺、紫阳官、邓仙洞、真君墓、方志敏烈士墓等。',
        'area/1101/scenic/6.jpg', 'area/1101/scenic/6.jpg',
        'https://v.qq.com/iframe/player.html?vid=a0315jn2o4t&tiny=0&auto=0',
        '梅岭风景区位于南昌市西郊。山势嵯峨，层峦叠翠，四时秀色，气候宜人。素有\"小庐山\"之称。 梅岭原名飞鸿山，早在汉朝初年，就辟有驿道。西汉末年，南昌县尉梅福为抵制王莽专政，退隐西郊飞鸿山。后人纪念他的高风亮节，在岭上建梅仙坛，岭下建梅仙观，改飞鸿山为梅岭。唐朝以来，有张九龄、张商英、周必大、王安石、张位五位丞相，欧阳修、曾巩、黄庭坚、陈师道、汤显祖等文学家，岳飞等武将，慕名而来，留下了许多名诗佳作和文人景观。',
        '80.00', '全天', '123456', '0', '1');
INSERT INTO `scenic`
VALUES ('7', '朱德旧居', '1101', '南昌市花园角2号', 'travel\\2e6dc756-02b1-4cde-904c-b52f990af212.jpg',
        '位于南昌市花园角2号。这是一所砖木结构两层楼房，坐西\r\n朱德旧居朱德旧居\r\n朝东，雕花飞檐悬于门楣，两扇大门，内有天井，是典型的江南民居。',
        'travel\\52e4bbc9-03fe-4215-9c3c-2d36a2f70731.jpg', 'travel\\0b9269d0-c057-4447-b92c-027d755f9e22.png',
        'https://v.qq.com/iframe/player.html?vid=l0544nd63wq&tiny=0&auto=0',
        ' 朱德旧居位于南昌市花园角2号。这是一所砖木结构两层楼房，坐西朝东，雕花飞檐悬于门楣，两扇大门，内有天井，是典型的江南民居。\r\n　　1926年12月，朱德受党的指示来到南昌，包租了花园角2号，利用国共合作的形势；创办了军官教育团，并担任国民革命军第五路军总参议兼第三军军官教育团团长和南昌警备司令、公安局长。在此期间，朱德为发展和保护革命力量做了大量的工作。\r\n　　1927年7月下旬，周恩来肩负着组织和领导起义的重任，从武汉来到南昌的第一天起，就住在这所住宅的厅堂里。旧居的一楼北侧是朱德的卧室，南、北前房及正房是警卫员的住房。二楼北侧是郭沫若的住房，他曾在此写下《请看今日蒋介石》。现已按原貌恢复了朱德卧室及周恩来住过的厅堂。',
        '70.00', '全天', '123456', '1', '1');
INSERT INTO `scenic`
VALUES ('8', '八一起义纪念塔', '1101', '江西省南昌市区中心的人民广场南端', 'area/1101/scenic/8.jpg',
        '八一南昌起义纪念塔坐落在南昌市区中心的人民广场南端。1977年为纪念\"八一\"起义五十周年而建。塔呈长方体，总高45.5米。正北面是叶剑英元帅题写的\"八一南昌起义纪念塔\"九个铜胎鎏金大字，下\r\n八一南昌起义纪念塔八一南昌起义纪念塔\r\n嵌\"八一南昌起义简介\"花岗石碑。',
        'area/1101/scenic/8.jpg', 'area/1101/scenic/8.jpg',
        'https://v.qq.com/iframe/player.html?vid=b0160upsrwx&tiny=0&auto=0',
        '八一南昌起义纪念塔坐落在江西省南昌市区中心的人民广场南端。1977年“八一南昌起义继承孙中山的兰花革命起义”五十周年时破土兴建，1979年1月8日落成。塔为长方体，总高45.5米。正北面是叶剑英元帅题写的“八一南昌起义纪念塔”九个铜胎鎏金大字，下嵌“八一南昌起义简介”花岗石碑。其他三面是“宣布起义”、“攻打敌营”、“欢呼胜利”三幅大型花岗石浮雕。塔身两侧各有一片翼墙，嵌有青松和万年青环抱的中国工农红军旗徽浮雕。塔顶由直立的花岗石雕“汉阳造”步枪和用红色花岗石拼贴的八一军旗组成。 ',
        '0.00', '全天', '123456', '1', '1');
INSERT INTO `scenic`
VALUES ('9', '革命烈士纪念堂', '1101', '江西省南昌市八一大道北段', 'area/1101/scenic/9.jpg',
        '江西省革命烈士纪念堂 是江西省民政厅直属的全额拨款优抚事业单位，机构建制县级，下设4个科，定编47人。全堂占地面积1.7万余平方米，陈列大楼建筑面积5千余平方米。绿化覆盖率达60%，是省级园林化单位。',
        'area/1101/scenic/9.jpg', 'area/1101/scenic/9.jpg',
        'https://v.qq.com/iframe/player.html?vid=t0024x5c0tb&tiny=0&auto=0',
        '江西革命烈士纪念堂位于江西省南昌市八一大道北段，建于1953年。纪念堂前的广场中心有一座红军战士塑像。堂的前厅有毛泽东手迹：“共产主义是不可抗拒的！”、“星星之火，可以燎原！”、“死难烈士万岁！”。正厅祭坛上立有纪念碑，由朱德题字：“革命烈士永垂不朽！”\r\n　　陈列柜中，珍藏25万余革命烈士名册。两边厅陈列第二次国内革命战争时期的10个战斗史迹。二、三楼陈列300多位烈士生平斗争事迹、遗著和遗物等。\r\n　　位于八一大道北段，建于1953年。纪念堂前的广场中心有一座红军战士塑像。堂的前厅有毛泽东手迹：“共产主义是不可抗拒的！”“星星之火，可以燎原！”、“死难烈士万岁！”正厅祭坛上立有纪念碑，由朱德题字：“革命烈士永垂不朽！”陈列柜中，珍藏25万余革命烈士名册。两边厅陈列第二次国内革命战争时期的10个战斗史迹。二、三楼陈列300多位烈士生平斗争事迹、遗著和遗物等。\r\n　　江西革命烈士纪念堂位于八一大道北段，建于1953年。纪念堂前的广场中心有一座红军战士塑像。\r\n　　江西革命烈士纪念堂\r\n　　堂的前厅有毛泽东手迹：“死难烈士万岁！”正厅祭坛上立有朱德题写的“革命烈士永垂不朽！”纪念碑：陈列柜中，珍藏有25万余革命烈士名册；两边厅陈列了第二次国内革命战争时期的10个战斗史迹；二楼和三楼陈列着300多位烈士生平斗争事迹、遗著和遗物等。\r\n　　交通：南昌市公共交通车１、２、７、１１路可以到达。\r\n　　江西革命烈士纪念堂位于八一大道北段，建于1953年。纪念堂前的广场中心有一座红军战士塑像。堂的前厅有毛泽东手迹：“死难烈士万岁！”正厅祭坛上立有纪念碑，由朱德题字：“革命烈士永垂不朽！”陈列柜中，珍藏25万余革命烈士名册；两边厅陈列第二次国内革命战争时期的10个战斗史迹；二楼和三楼陈列300多位烈士生平斗争事迹、遗著和遗物等。\r\n　　烈士墓区坐落在陵园的西北侧，由烈士墓、烈士纪念堂和无名烈士陵三部分组成。这里长眠着近1 700位牺牲于不同时期的革命烈士。无名烈士陵的正前方是长明火坛，昼夜燃烧的熊熊火焰象征着烈士们生命之火不熄，革命精神永存。陵园东侧是碑苑。这里的碑亭、碑壁和碑石上镌刻着烈士们的诗文。碑文字体各异，都出自国内一流名家之手笔，堪称当代书法之集成。',
        '40.00', '10:00-20:00', '12143242', '1', '1');
INSERT INTO `scenic`
VALUES ('10', '新四军军部旧址', '1101', '南昌市西湖区友竹花园7号', 'area/1101/scenic/10.jpg',
        '新四军军部旧址，是建于1915年，是北洋军阀张勋的公馆，内有两栋砖木结构楼房和一栋平房，属中西合璧的古建筑。 1938年1月6日新四军军部驻扎于此，完成了组建新四军的艰巨任务。',
        'area/1101/scenic/10.jpg', 'area/1101/scenic/10.jpg',
        'https://v.qq.com/iframe/player.html?vid=c0619w9pefd&tiny=0&auto=0',
        '新四军军部旧址，建于1915年，是北洋军阀张勋的公馆，内有两栋砖木结构楼房和一栋平房，属中西合璧的古建筑。\r\n　　1938年1月6日新四军军部驻扎于此，完成了组建新四军的艰巨任务。由八省红军游击健儿组成的新四军走出深山老林，路上了北上抗日征途，成为华中抗日的中流砥柱，为中华民族的解放事业建立了不朽功勋。\r\n　　南昌新四军军部旧址因其历史价值重大面被定为省级文物保护单位、省级爱国主义教育基地。目前旧址内设有4个展览：\r\n　　一是新四军军部旧址复原展，展出新四军领导人叶挺、项英、曾山、黄道、周子昆、邓子恢、陈丕显、赖传珠办公和居住的房间及他们的生平图片展。\r\n　　二是《铁的新四军》大型展览，分为四个展厅：铁流滚滚出深山；群英聚集南昌城；大江南北抗敌寇；铁军精神万代传。全面、系统地展现了新四军从1937年成立至1947年改编为华东野战军十年间的光辉战斗历程和英雄业绩。\r\n　　三是《新四军木刻展》展出创作于抗战时期保存至今的新四军木刻作品60幅。\r\n　　四是张勋公馆建筑艺术作品长廊展。\r\n　　位于临沂市河东区九曲镇前河湾村。1945年10月，陈毅、粟裕指挥新四军北进，将军部设在此处长达一年之久，并在这里指挥了著名的宿北战役、鲁南战役、召开了华野前委会议。会上，陈毅作了《一面打仗，一面建设》的重要报告。1947年2月，我军撤出后，敌机对其进行了轰炸，房屋大部被毁。现仅存8间，为市级重点文物保护单位。陈毅、粟裕、张云逸等老一辈革命家用过的桌椅、书橱、马槽等文物保护完好。\r\n　　南昌新四军军部旧址原为北洋军阀张勋的公馆（建于1915年）。1938年1月6日，国发革命军陆军新编第四军军部从汉口迁驻于此，开始正式办公。相继设在这里的还有中共中央东南分局和新四军驻赣办事处。军长叶挺、副军长兼东南分局书记项英、东南分局副书记兼江西省委书记曾山、新四军驻赣办事处主任黄道等曾在此居住、办公。\r\n　　军部在南昌期间，除了组织改编南方八省红军、游击队为新四军，指挥各部队开赴皖南集结处，还广泛开展抗日民族统一战线和抗日救亡活动，恢复和发展中共党的基层组织，输送大批青年奔赴抗日前线，使沉寂的南昌变成了南方的延安。4月4日，军部离开南昌开赴抗日前线。新四军驻赣办事处从东书院街移驻张勋公馆，与留在南昌的中共中央东南分局坚持工作，直到1939年3月26日南昌沦陷前夕。',
        '70.00', '10:00-20:00', '2423434', '1', '1');
INSERT INTO `scenic`
VALUES ('11', '绳金塔', '1101', '江西省南昌市西湖区绳金塔街东侧，原古城进贤门外', 'area/1101/scenic/11.jpg',
        '绳金塔，位于江西省南昌市西湖区绳金塔街东侧，原古城进贤门外，始建于唐天佑年间(公元904~907年)，相传建塔前异僧惟一掘地得铁函一只，内有金绳四匝，古剑三把(分别刻有\"驱风\"、\"镇火\"、\"降蛟\"字样)还有金瓶一个，盛有舍利子三百粒，绳金塔因此而得名。',
        'area/1101/scenic/11.jpg', 'area/1101/scenic/11.jpg',
        'https://v.qq.com/iframe/player.html?vid=u053404nu2g&tiny=0&auto=0',
        '绳金塔，位于江西省南昌市西湖区绳金塔街东侧，原古城进贤门外，始建于唐天佑年间(公元904~907年)，相传建塔前异僧惟一掘地得铁函一只，内有金绳四匝，古剑三把(分别刻有\"驱风\"、\"镇火\"、\"降蛟\"字样)还有金瓶一个，盛有舍利子三百粒，绳金塔因此而得名。\r\n\r\n绳金塔古朴秀丽，具有中国江南建筑的典型艺术风格，自唐代始建至今，已有1100多年的历史，是历史信息的载体，是古代汉族劳动人民智慧的结晶。绳金塔素有\"水火既济，坐镇江城\"之说，是南昌人的镇城之宝。',
        '67.00', '10:00-20:00', '2423434', '0', '1');
INSERT INTO `scenic`
VALUES ('12', '二十军指挥部旧址', '1101', '南昌市子固路８５号', 'area/1101/scenic/12.jpg',
        ' 二十军指挥部旧址即贺龙指挥部旧址系“南昌八一起义”旧址群之一，坐落在南昌市子固路８５号，院内有两幢楼房，一幢是二十军军部办公室，另一幢是贺龙的办公室兼卧室，以及刘伯承、恽代英等的住房。 ',
        'area/1101/scenic/12.jpg', 'area/1101/scenic/12.jpg',
        'https://v.qq.com/iframe/player.html?vid=y0542xvv2p2&tiny=0&auto=0',
        '   二十军指挥部旧址即贺龙指挥部旧址系“南昌八一起义”旧址群之一，坐落在南昌市子固路８５号，院内有两幢楼房，一幢是二十军军部办公室，另一幢是贺龙的办公室兼卧室，以及刘伯承、恽代英等的住房。\r\n　　贺龙指挥部旧址原为中华圣公会宏道堂，是一座中西合璧的古建筑，1927年7月下旬，贺龙率领国民革命军第二十军进驻南昌，参加南昌起义，军指挥部设在宏道堂，部分起义领导人住在宏道堂后小楼寓所，并在此举行过多次策划起义行动的重要会议，1927年8月1日凌晨，贺龙、刘伯承、周逸群在此指挥了武装起义。大楼前三楼窗口仍留有当年敌方炮弹弹痕，为起义时激战景况的实录。旧址小楼有军官会议室的复原陈列，大楼大厅还设有《贺龙元帅生平展》。',
        '45.00', '10:00-20:00', '34534543', '1', '1');
INSERT INTO `scenic`
VALUES ('13', ' 庐山风景名胜区', '1102', '九江市庐山市南36公里鄱阳湖畔(近九江星子)', 'area/1102/scenic/1.png',
        '　庐山无数次出现在文人墨客的诗词歌赋中，其中以苏轼一句“不识庐山真面目，只缘身在此山中”最为著名。如今庐山已是一处很成熟的旅游景区，山中的青山绿水、飞瀑、日出，还有近代别墅，引得不少游人来此一窥“庐山真面目”。',
        'area/1102/scenic/2.png', 'area/1102/scenic/3.png',
        'https://v.qq.com/iframe/player.html?vid=j05249ntost&tiny=0&auto=0',
        '庐山为国家级风景名胜区、国家AAAAA级旅游景区、国家地质公园、国家级自然保护区、世界文化景观遗产。\r\n　　庐山位于中国中部江西省九江市南，北濒长江，东接鄱阳湖，面积302平方公里，全山共有90多座山峰，最高峰大汉阳峰，海拔1473.4米。群峰间散布有许多壑谷、岩洞、瀑布、溪涧，地形地貌复杂。\r\n　　庐山无数次出现在文人墨客的诗词歌赋中，其中以苏轼一句“不识庐山真面目，只缘身在此山中”最为著名。如今庐山已是一处很成熟的旅游景区，山中的青山绿水、飞瀑、日出，还有近代别墅，引得不少游人来此一窥“庐山真面目”。\r\n　　庐山，位于中国江西省的北部，东经115度52分—116度零8分,北纬29度26分—29度41分，面积302平方公里,外围保护地带面积五百平方公里。北濒一泻千里的长江，南襟烟波浩渺的鄱阳湖，大江、大湖、大山浑然一体，险峻与秀丽刚柔相济，素以“雄、奇、险、秀”闻名于世。\r\n　　早在一千二百多年前，唐代著名诗人李白便这样赞美庐山：“予行天下，所游山水甚富，俊伟诡特，鲜有能过之者，真天下之壮观也。”有“匡庐奇秀甲天下”之美誉，历代诗人墨客，更是慕名而来，纷纷赋诗填词，历史上李白、白居易、岳飞、文天祥、苏东坡、李时珍、徐霞客等均到过此处。庐山寺、庙、宫、观多达三百处，是我国佛教和道教的中心之一。庐山以避暑和冬游胜地享誉海内外，其各国风格的别墅为中国名山所罕见。\r\n　　庐山，作为著名的世界遗产地，历经多年的开发建设，其在地理、资源和服务功能上的三大优势进一步显现。庐山是一座地垒式断块山，外险内秀。具有河流、湖泊、坡地、山峰等多种地貌。主峰------大汉阳峰，海拔1474米；庐山自古命名的山峰便有171座。群峰间散布冈岭26座，峡谷20条，岩洞16个，怪石22处。水流在河谷发育裂点，形成许多急流与瀑布，瀑布22处，溪涧18条，湖潭14处。著名的三叠泉瀑布，落差达155米。庐山奇特瑰丽的山水景观具有极高的科学价值和旅游观赏价值。\r\n　　庐山生物资源丰富。森林覆盖率达76.6%。高等植物近3000种，昆虫2000余种，鸟类170余种，兽类37种。山麓鄱阳湖候鸟保护区是“鹤的王国”，有世界最大的白鹤群，被誉为中国的“第二座万里长城”。\r\n　　庐山地处中国亚热带东部季风区域，面江临湖，山高谷深，具有鲜明的山地气候特征，年平均降水1917毫米，年平均雾日191天，年平均相对湿度78%，每年7月--9月平均温度16.9摄氏度，夏季极端最高温度32摄氏度。良好的气候和优美的自然环境，是庐山成为世界著名的避暑胜地。\r\n　　庐山地区早在六千年前，便有人类活动。长久以来，人们在这里创造了内涵丰富、影响深远的庐山文化。作为大禹时所做的《禹贡》及稍后的《上海经》，均有庐山古称的记载。公元前126年，司马迁“南登庐山”，并将“庐山”载入了中国第一部纪传体史书《史记》。东晋陶渊明、谢灵运、宗炳等一大批文化名人，陆续来到庐山，进行了杰出的文化艺术创作，使庐山成为中国田园诗的诞生地、中国山水诗的策源地、中国山水画的发祥地。此后，李白、白居易、苏轼、王安石、黄庭坚、陆游、康有为、陈三立、胡适、徐志摩、郭沫若等1500余位诗人相续登山，写诗4000余首，其中名篇佳作灿若珠玑。\r\n　　庐山“道释同尊”。公元四世纪，高僧慧远在庐山建东林寺，首创观像念佛的净土法门，开创中国化佛教，代表佛教中国化的大趋势；禅师竺道生在庐山精舍，开创“顿悟说”。天师张道陵，一度在庐山-；道教禅师之一的陆道静，在庐山建简寂观，编撰藏道经1200卷，奠定了“道藏”基础，并创立了道教灵宝派。',
        '110.00', '10:00-20:00', '343456', '0', '1');
INSERT INTO `scenic`
VALUES ('14', '三叠泉', '1102', '江西省九江市的庐山东南九叠谷', 'area/1102/scenic/4.png', '三叠泉在全国有几处，最为出名为位于江西省九江市的庐山东南九叠谷',
        'area/1102/scenic/5.png', 'area/1102/scenic/6.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '三叠泉在全国有几处，最为出名为位于江西省九江市的庐山东南九叠谷，是国家AAAAA级旅游景区。经环庐山旅游公路可达。距九江市区26公里。是集险峰怪石、飞瀑流泉为一体的山丘型旅游地。\r\n宋绍熙二年（1191）始被樵者发现，故有“一朝何事失扃钥，樵者得之人共传”的诗句。20世纪80年代初期开始进行旅游开发，2001年11月经国家林业局批准为国家森林公园。另有安徽黟县三叠泉和珠海横琴三叠泉。三叠泉又名三级泉、水帘泉，位于江西省著名风景区庐山风景区中。\r\n古人称“匡庐瀑布，首推三叠”，誉为“庐山第一奇观”，由大月山、五老峰的涧水汇合，从大月山流出，经过五老峰背，由北崖悬口注入大盘石上，又飞泻到二级大盘石，再喷洒至三级盘石，形成三叠，故名；\r\n立于泉下盘石向上仰观，但见抛珠溅玉的三叠泉宛如白鹭千片，上下争飞；又如百幅冰绡，抖腾长空；万斛明珠，九天飞洒。经阳光折射，五光十色，瑰丽夺目，恰似银河九天飞来。立于”观瀑亭”可又俯视三叠。听瀑鸣如击鼓，吼若轰雷；见瀑像喷晶抛珠，水洒溅玉，连垂素练，落入深谷。仰看与俯视各蔚壮观，自成美趣，故有“不到三叠泉，不算庐山客”之说。\r\n有趣的是，历史上，三叠泉曾长期未被发现，隐居在它上源屏风叠的李白和在它下游白鹿洞讲学的朱熹都不知咫尺之地有此胜境。直到南宋绍熙辛亥年（公元1191年），三叠泉才被人发现，渐渐赢得庐山第一景观的美誉。自此往后，各代诗家名流皆竞相前来观赏，并留下诸多名篇佳作。\r\n宋绍熙二年（1191）始被樵者发现，故有“一朝何事失扃钥，樵者得之人共传”的诗句。20世纪80年代初期开始进行旅游开发，2001年11月经国家林业局批准为国家森林公园。飘如雪、断如雾、缀如流、挂如帘”！它随着季节和雨水多寡的变化而不同，暮春初夏季节，飞瀑如发怒的玉龙，轰然疾下，震天动地；仲夏严冬，雨水较少，则水帘如丝，轻盈柔美，春夏秋冬，各有千秋。三叠泉的壮丽，曾引起当时已离任的朱熹的向往，请人将“三叠新泉”绘成一图，挂在堂上时时欣赏，以弥补他“未能一游其下，一快心目”的愿望。诗人曾为之讴歌赞美，宋代诗人白玉蟾《三叠泉》诗云：“九层峭壁划青空，三叠鸣泉飞暮雨”、“寒入山谷吼千雪，派出银河轰万古”。\r\n立于泉下盘石向上仰观，但见抛珠溅玉的三叠泉宛如白鹭千片，上下争飞；又如百幅冰绡，抖腾长空；万斛明珠，九天飞洒。经阳光折射，五光十色，瑰丽夺目，恰似银河九天飞来。立于“观瀑亭”可又俯视三叠。听瀑鸣如击鼓，吼若轰雷；见瀑像喷晶抛珠，水洒溅玉，连垂素练，落入深谷。仰看与俯视各蔚壮观，自成美趣，故有“不到三叠泉，不算庐山客”之说。',
        '60.00', '10:00-20:00', '345567', '0', '1');
INSERT INTO `scenic`
VALUES ('15', '浔阳楼', '1102', '江西省九江市浔阳区滨江东路', 'area/1102/scenic/7.png',
        '浔阳楼，江南十大名楼之一，位于江西省九江市浔阳区滨江东路，紧靠长江之滨。因九江古称浔阳而得名，初为民间酒楼，至今已有1200年的历史，是溶名楼、名著、名酒为一体的景点。',
        'area/1102/scenic/8.png', 'area/1102/scenic/9.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '浔阳楼，江南十大名楼之一，位于江西省九江市浔阳区滨江东路，紧靠长江之滨。因九江古称浔阳而得名，初为民间酒楼，至今已有1200年的历史，是溶名楼、名著、名酒为一体的景点。\r\n浔阳楼之名最早见之于唐代江州（今江西九江）刺史韦应物的诗中。随后，白居易在《题浔阳楼》诗中又描写了它周围的景色，而真正使浔阳楼出名的是古典名著《水浒传》。小说中的宋江题反诗、李逵劫法场等故事使浔阳楼名噪天下。\r\n现在的浔阳楼，为1987年由九江市人民政府重建。总体占地2000平方米。主楼占地300平方米，高21米，外三层内四层，九脊层顶，龙檐飞翔，瓦朱栏，四面回廊，古朴凝重，建筑面积为1000平方米。\r\n浔阳楼曾由设计过黄鹤楼的工程师向欣然设计。在设计过程中，他参照舆刊本《水浒传》插图和宋代《清明上河图》的建筑风格，进行设计。楼北两面顶檐下各悬有由赵朴初题写的《浔阳楼》巨幅匾额.浔阳楼因九江古称浔阳而得名，初为民间酒楼，至今已有1200年的历史。由于九江自古以来就是长江南岸一座交通要道和经济发达的城市，所以，雄距长江畔的浔阳楼也历来是名人云集之地。如：白居易、韦应物、苏东坡等等，都曾登楼题咏。留下许多脍炙人口的佳话。更因施耐庵写下一部《水浒传》，其中一位主要人物--宋江曾经在浔阳楼醉酒题反诗，随着名人名著流芳百世，浔阳楼也蜚声海内外，吸引着社会各届人士到此参观。浔阳楼是一座具有典型宋代建筑风格的楼宇，又是一个集游览参观、喝茶听书和餐饮为一体的旅游景点。由全国佛教协会会长、书法家赵朴初题写的\"浔阳楼\"匾额赫然高悬，\"世间无比酒，天下有名楼\"这幅古老的楹联则吸引着无数游客驻足。\r\n一楼大厅东西两壁镶嵌了两幅大型瓷板画，彩绘着“宋公明发配江州城”、“浔阳楼宋江题反诗”、“黄文炳设计害宋江”、“梁山泊好友劫法场”等栩栩如生的画面，为浔阳楼增加了传奇色彩。大厅内还陈列着全国旅游景点上唯一的一套《水浒》一百单八将的人物瓷像，这一作品把一个英雄好汉的形象栩栩如生地展现在游客面前。爱好书画的朋友还可在楼内欣赏到多幅出自名家之手的书画作品和诗词楹联。\r\n二楼忠义堂为当年宋江醉酒题诗处，现仍备有宋江当年喝过的那种酒（称作\"蓝桥风月\"美酒），以及独具特色的\"水浒宴\"。在此可体会一番把酒临风的雅兴。 二楼还展出了介绍浔阳楼历史的书籍和地方名胜风光图片。\r\n三楼是回廊，主要陈列字画。\r\n四楼为茶室，也是赏景的最佳处，摆满了仿古桌椅。浔阳楼是溶名楼、名著、名酒为一体的名胜。游客在此可远眺庐山、近观长江、鄱阳湖，再听导游将各个景点细细道来，真是饶有趣味。身居室内，喝茶乘凉，再听上一段精彩的北京评书，你可体会到一种特殊的韵味。',
        '0.00', '10:00-20:00', '123243', '0', '1');
INSERT INTO `scenic`
VALUES ('16', '南昌起义旧址', '1102', '南昌起义庐山会议旧址位于庐山东谷中五路336号', 'area/1102/scenic/10.png',
        '起义庐山会议旧址位于庐山东谷中五路336号，原为庐山仙岩饭店，建于1910年，占地面积336平方米，砖木结构，三层楼房。', 'area/1102/scenic/11.png',
        'area/1102/scenic/12.png', 'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '  南昌起义庐山会议旧址位于庐山东谷中五路336号，原为庐山仙岩饭店，建于1910年，占地面积336平方米，砖木结构，三层楼房。1927年7月21日，鲍罗廷、瞿秋白、李立三、聂荣臻、邓中夏、林伯渠、叶挺、彭湃、郭亮9人在仙岩饭店厨房里召开会议（因当时林伯渠的叔叔在仙岩饭店当厨师），进一步分析了形势，研究了南昌起义的纲领、计划，对领导机构的安排以及起义时间等都作出了决定。26日，周恩来、彭湃、陈赓等人上庐山，下榻于此，于27日下山步行到德安，坐火车赶到南昌，当天成立前敌委员会。1937年6月4～15日和1946年8月5日，周恩来两度到庐山与蒋介石会商国共合作、和谈事宜，均下榻此处。',
        '40.00', '10:00-20:00', '2343243', '1', '1');
INSERT INTO `scenic`
VALUES ('17', '工农革命军旧址', '1102', '修水县城凤凰山路，名冷家祠', 'area/1102/scenic/13.png',
        ' 工农革命军第一军第一师师部旧址全国重点文物保护单位。位于修水县城凤凰山路，名冷家祠，原为修水商会钱业会馆，占地面积1200平方米，建筑面积350平方米，坐北朝南，一进二重，分别为一幢砖木结构的平房和一幢混凝土二层楼房，麻石门框，小青瓦屋面，四水归内，中有天井。',
        'area/1102/scenic/14.png', 'area/1102/scenic/15.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '工农革命军第一军第一师师部旧址全国重点文物保护单位。位于修水县城凤凰山路，名冷家祠，原为修水商会钱业会馆，占地面积1200平方米，建筑面积350平方米，坐北朝南，一进二重，分别为一幢砖木结构的平房和一幢混凝土二层楼房，麻石门框，小青瓦屋面，四水归内，中有天井。1927年8月7日，中共中央决定在湘鄂赣边区组织秋收起义；12日，由共产党员卢德铭任团长的国民革命军第四集团军第二方面军总指挥部警卫团和湖南平江工农义勇军攻克修水县城；当月下旬，与湖南浏阳和湖北崇阳、通城的农民自卫军在修水山口统编为工农革命军第一军第一师，师部驻在县城当时为县商会的冷家祠。何长工等人奉命设制工农革命军第一军第一师军旗。9月9日，秋收起义第一枪在修水打响，以镰刀斧头为标志的五星红旗高高飘扬在修河上空。该旧址上堂前为工农革命军第一面军旗设计、制作的地方，左右房间为师长、副师长办公与居住；下堂前左、右各2间房，为师部八大处办公室；天井为青石铺砌，两侧厢房为警卫战士陈士榘、韩伟（解放后分别被授予上将、中将军衔）值班室。师部旧址正门前为练兵场。在离师部旧址大约200米处，还存有秋收起义誓师大会旧址以及师直特务连驻地旧址。师部旧址于1987年12月被江西省人民政府公布为省级文物保护单位，2006年5月被国务院公布为全国重点文物保护单位。',
        '34.00', '10:00-20:00', '2343434', '1', '1');
INSERT INTO `scenic`
VALUES ('18', '\"马垱守卫战\"遗址', '1102', '彭泽县马垱镇境内的长江南岸', 'area/1102/scenic/16.png',
        '抗日战争“马垱守卫战”遗址位于彭泽县马垱镇境内的长江南岸。1930年10月，周建屏率红十军组织“彭泽暴动”，先攻下彭泽县城，随后占领马垱要塞，达到了“切断长江交通”的预期目的。从1937年冬开始，江西省江防委员会调集9个县的民工，在马垱修筑和加固防御工事。',
        'area/1102/scenic/17.png', 'area/1102/scenic/18.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '抗日战争“马垱守卫战”遗址位于彭泽县马垱镇境内的长江南岸。1930年10月，周建屏率红十军组织“彭泽暴动”，先攻下彭泽县城，随后占领马垱要塞，达到了“切断长江交通”的预期目的。从1937年冬开始，江西省江防委员会调集9个县的民工，在马垱修筑和加固防御工事。1938年6月，国民党53师驻守马垱要塞；24日，守军与日军发生一昼夜的激战，击沉敌舰4艘，击伤2艘，击毙日军500余人；26日，经过激烈的争夺，马垱要塞失守。现正在进行全面修复和扩建。\r\n马当要塞炮台的由来。\r\n\r\n    马当炮台是抗日战争紧急关头临时建起来的，它基本上分为三部分，在马当娘山、牛山、鸡公嘴各地都筑起钢筋水泥为主体的炮台，主阵地由海军炮队8门舰炮组成，它射程很远，从2000米可打到15000米之间，并配有陆战队，约200余人。其它附属炮阵地上，主要是高炮和陆军野战炮，约有50余门，火力相当凶猛。第二部分是在马当前水域里，布上600余枚水雷，并毁掉江面上一切航标设施。第三部分是海军舰艇配备在封锁线以内，一是随时机动布雷，二是用舰炮和岸炮配合，补充岸炮某些死角的不足。保护岸炮阵地。\r\n\r\n    为什么要组建马当要塞炮台呢?这对今天年轻人来说是不可理解的。当时中国海军很弱小，跟日本强大海军相比，不到三分之一，要跟日军在海上作战是不可能的，只能是近岸利用地形地物来建立封锁线、岸炮台，配有舰艇来阻挡日本海军的进攻，减缓日军进攻的速度．粉碎日军“3个月灭亡中国”的神话。中国海军主要退守在长江里，阻止日本海军溯江而上配合陆军作战的计划，因此，中国海军抗日战争的血战主要在长江里进行。\r\n\r\n    建立第一道封锁线是江阴，马当要塞可以说是第二道封锁线。\r\n\r\n    1938年6月15日\r\n\r\n    日军华中派遣军直辖的最凶悍的波田支队，在海军舰艇和航空部队的支援下，同中国川军20军和50军，在安庆地区的长江两岸，反复争夺，鏖战近半个月，日军才取得该地，打开了长江通向武汉的第一道大门。\r\n\r\n    6月8日，日军第6师团，从安徽舒城地区南下，于6月18日攻占了潜山，加强了波田支队的攻势，同时也稳住了该支队的后方。\r\n\r\n    安庆失守，马当、湖口等地就成为武汉的前哨阵地。\r\n\r\n    国民党海军总司令陈绍宽上将，根据蒋介石的命令，会同第九、第五战区部队，在马当至汉口间构筑若干要塞炮台，设立江防要塞司令部，以谢刚哲为司令官。要塞司令部将所属军舰炮艇和海军陆战队，统编为3个总队。\r\n\r\n    第1总队：驻防九江上游的田家镇、葛店一线；\r\n\r\n    第2总队：驻防马当要塞；\r\n\r\n    第3总队：驻防湖口要塞。\r\n\r\n    为便于海、陆军协同作战，防守长江水域，在马当、湖口要塞司令部之上，又设马湖要塞指挥部，由陆军第16军军长李温衍兼任指挥。马当要塞由陆军王锡焘充任要塞司令，海军炮队的队长由陈永钦担任。湖口要塞由陆军第77师师长彭位仁充任要塞司令。\r\n\r\n    这就是武汉战区长江地区的中国军队兵力情况。\r\n\r\n ',
        '23.00', '10:00-20:00', '354345', '1', '1');
INSERT INTO `scenic`
VALUES ('19', '红井', '1103', ' 赣州沙洲坝', 'area/1103/scenic/1.png',
        '  旧社会，瑞金沙洲坝流传一首民谣：“有女莫嫁沙洲坝，天早无水洗头帕。”说的是沙洲坝是个干早缺水的地方。住在沙洲坝的人吃的是又脏又臭的塘水，吃了容易生病。也有人想过要挖井，可是一来穷，没人提得起头，二来又信迷信，听风水先生说过，沙洲坝的龙脉是条旱龙，不能打井，打井会坏龙脉，所以更没有人敢去冒这个风险。',
        'area/1103/scenic/2.png', 'area/1103/scenic/3.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        ' 旧社会，瑞金沙洲坝流传一首民谣：“有女莫嫁沙洲坝，天早无水洗头帕。”说的是沙洲坝是个干早缺水的地方。住在沙洲坝的人吃的是又脏又臭的塘水，吃了容易生病。也有人想过要挖井，可是一来穷，没人提得起头，二来又信迷信，听风水先生说过，沙洲坝的龙脉是条旱龙，不能打井，打井会坏龙脉，所以更没有人敢去冒这个风险。于是沙洲坝的人也只得祖祖辈辈到塘里去挑水喝。\r\n　　一九三一年，瑞金成立了中华苏维埃中央政府。政府起初设在叶坪，后来因为白匪军狂轰滥炸，为了安全防空，从叶坪迁到沙洲坝。毛泽东主席便住在沙洲坝的村子里。\r\n　　有一天傍晚，毛主席办完公事从外面回来，一下马，看见乡亲们在塘里挑水，便问：“这水挑去做什么用？”乡亲们回答说：“吃呀！”毛主席说：“水这么脏，能吃吗？”乡亲们苦笑着说：“有什么办法，再脏也得吃啊！”毛主席说：“不会打井么？”乡亲们摇摇头说：“沙洲坝人喝不得井水，这是天命！”毛主席哈哈大笑说：“不要信天命，要信革命！还是打口井吧！”说罢，牵马进村去了。大家也就散了，谁也没有把这话记在心里。仍然每天到塘里挑水吃，毛主席也每天早出晚归忙他的公事。\r\n　　有一天，天刚朦朦亮，起来挑水的人，看见有两个人影在村头走来走去。一个拿着锄头，一个拿着锹，这里锄锄，那里铲铲。谁这么早起来干什么呢？走前一看，原来的毛主席和他的警卫员。挑水的乡亲问：“毛主席，你这是干什么？”毛主席说：“找水源、挖井。”说完，便和警卫员在一块长满油草地地方铲开地皮挖了起来。挖到约有两三尺深，毛主席抓起一把泥土在手里捏了捏，对警卫员说：“行，井位就定在这里，你去叫人来挖。”于是毛主席叫挖井的事，立即传遍了全村，众人都自动的找着锄头铁锹，齐集到村头和毛主席一道挖井。毛主席一边和大家挖井，一边对乡亲们说：“这几天忙，没工夫找大家商量打井的事，今天有半天空，我先替大家找个有水源的地方，定个位，破个土。我知道，你们信风水，怕得罪旱龙王，我不怕，如果旱龙王怪罪下来，让它来找我算帐好了！”逗得大家都笑了。大家说说笑笑，不到一天工夫，一口井便挖成了。从此，沙洲坝的乡亲喝上了井水，结束了祖祖辈辈挑塘水喝的历史。因为井是红军来了以后毛主席亲手挖的，所以乡亲们给这井起了个名字叫“红井”。\r\n　　一九三四年，红军北上抗日走了，反动派杀了回来，听说这井是毛泽东亲手挖的，恨得把井填了。乡亲们只好又去挑那塘水喝了。喝过了又清又甜的井水，再喝这又脏又臭的塘水，那苦味就更难受了。越难受，便越想红军和毛主席。白天不敢到井边来，乡亲们只好等到夜深人静，悄悄来到村头，默默的围坐在井边。抬头看看天上的北斗星，低头摸摸倒塌的井垣，心里念着毛主席，念着红军，只盼红军能早点打回来。\r\n　　有一天晚上，几个靖卫团狗子到村里来查户口，发现村里竟是空空的，来到村头，才发现 全村男女老幼都在井边，靖卫狗子以为又要聚众闹事，胆战心惊地喝问众人：“三更半夜，你们跑到这里来干什么？”一连问了几声，没有人回答，吓得靖卫狗子屁滚尿流回去报告团总。团总一听心里直发毛，起了一身的鸡皮疙瘩。他明白：井是毛泽东挖的，三更半夜再到井边去，分明是在想毛泽东、想红军嘛，这还了得，分明是反心不死啊？于是下了一道禁令：“谁要三更半夜再到井边去，抓住就杀头。”并在井边钉了一块木板牌子，牌子上写着：“违禁者杀”四个大字。\r\n　　可是沙洲坝的乡亲看也不看它一眼，一到夜晚，仍然不约而同的来到村头，默默地围坐在井边，抬头看看北斗星，低头摸摸倒塌的井垣，心里念着红军、念着毛主席，只盼红军能早点打回来。\r\n　　反动派恼了，杀是杀不了那么多人的，只得抓几个人去坐班房，心想这下可能不会再有人敢到井边去了。谁知道，沙洲坝的乡亲，竟当没有那回事一样，一到晚上，还是不约而同的来到村头，默默地围坐在井边，抬头看看天上的北斗星，低头摸摸倒塌的井垣，心里念着红军、念着毛主席，只盼红军能早点打回来。\r\n　　反动派一连抓了几次人，乡亲们一到夜晚，照样不约而同，不声不响的到井边来默默地坐着，反动派看看没办法，只好算了。于是乡亲们不分春夏秋冬，每天晚上都要到村头的井边坐上一 坐。一夜不去，心里便象少了点什么一样，睡在床上也不自在。就这样，一直坐到一九四九年全国解放。\r\n　　解放了，沙洲坝乡亲头一件大事，便是挖井，把填了十五年的井重新挖开、砌好，并在井边也钉上一块木牌，木牌上写上十四个大字：',
        '0.00', '10:00-20:00', '345345', '1', '1');
INSERT INTO `scenic`
VALUES ('20', '毛泽东旧居', '1103', '沙洲坝456路', 'area/1103/scenic/4.png',
        '  这栋房子原是苏区时期破产地主杨衍兰的私祠，称为元太屋，建于1876年，是典型的客家民居。1933年4月，中央执行委员会从叶坪迁驻这里。', 'area/1103/scenic/5.png',
        'area/1103/scenic/6.png', 'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        '这栋房子原是苏区时期破产地主杨衍兰的私祠，称为元太屋，建于1876年，是典型的客家民居。1933年4月，中央执行委员会从叶坪迁驻这里。\r\n\r\n    中央执行委员会产生于1931年11月召开的第一次全国苏维埃代表大会，由63名委员组成，毛泽东当选为主席，项英、张国焘为副主席。在1934年1月召开的第二次全国苏维埃代表大会上，选举175人为正式中央执行委员，36人为候补委员，组成新的中央执行委员会。毛泽东继续当选为主席，项英、张国焘为副主席。中央执行委员会是全苏大会闭幕后的最高权力机关，不仅要负责法律的制订和颁发，还要监督各项法律的实施执行。中央执行委员会在成立的三年时间里，共颁发了《土地法》、《劳动法》、《婚姻条例》、《征税条例》等上百个法律条例，为中华苏维埃共和国建设和健康发展，发挥了重要作用。\r\n\r\n    在这里居住和办公的有毛泽东、何叔衡、徐特立、谢觉哉等领导人，除毛泽东之外，其他三位与林伯渠、董必武一起被人们尊称为“苏区五老”。毛泽东在此居住期间，经常深入群众，关心群众生活和生产，并作了长冈乡和才溪乡调查。还先后写下了《必须注意经济工作》、《怎样分析农村阶级》、《我们的经济政策》、《关心群众生活，注意工作方法》等光辉著作。 1934年7月，中央执行委员会离开沙洲坝迁往云石山。该旧址现为全国重点文物保护单位。',
        '0.00', '10:00-20:00', '4354343', '1', '1');
INSERT INTO `scenic`
VALUES ('21', '红军长征纪念馆', '1103', '赣州市长虹区', 'area/1103/scenic/7.png',
        '中央红军长征出发纪念馆以中央红军集结于都及中央红军长征历程为主线，以长征前夕毛泽东、朱德、周恩来等老一辈革命家在于都开展系列革命活动为载体，采用丰富、翔实的革命历史文物资料和沙盘、图表、画、实物及声、光、电等现代化手段，再现了中央红军被迫实行战略转移的历史背景，体现了中央红军气吞山河，勇往直前的革命英雄主义，讴歌了伟大的长征所凝聚的伟大的长征精神。',
        'area/1103/scenic/8.png', 'area/1103/scenic/9.png',
        'https://v.qq.com/iframe/player.html?vid=t0552mgg8kq&tiny=0&auto=0',
        ' 中央红军长征出发纪念馆以中央红军集结于都及中央红军长征历程为主线，以长征前夕毛泽东、朱德、周恩来等老一辈革命家在于都开展系列革命活动为载体，采用丰富、翔实的革命历史文物资料和沙盘、图表、画、实物及声、光、电等现代化手段，再现了中央红军被迫实行战略转移的历史背景，体现了中央红军气吞山河，勇往直前的革命英雄主义，讴歌了伟大的长征所凝聚的伟大的长征精神。\r\n\r\n   中央红军长征出发纪念馆始建于1986年10月，馆址原设在于都县城谢家祠，原馆名叫中央红军长征出发地纪念馆。2005年5月1日，在国家计委、国家旅游局、省委宣传部的关心支持下，在中央红军长征第一渡东侧建设新馆，根据有关领导和专家的建议，更名为中央红军长征出发纪念馆。',
        '0.00', '10:00-20:00', '12133', '1', '1');
