-- ----------------------------
-- Table structure for specialty
-- ----------------------------
DROP TABLE IF EXISTS `specialty`;
CREATE TABLE `specialty`
(
    `id`        int(11) NOT NULL AUTO_INCREMENT,
    `tNum`      int(11) NOT NULL,
    `tCategory` varchar(255) NOT NULL,
    `tName`     varchar(255) NOT NULL,
    `tPrice`    double(10, 2
) NOT NULL,
  `tPic` varchar(255) NOT NULL,
  `pic2` varchar(255) DEFAULT NULL,
  `pic3` varchar(255) DEFAULT NULL,
  `tDescrible` text NOT NULL,
  `stocknum` int(11) DEFAULT NULL,
  `stage` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `tNum` (`tNum`),
  CONSTRAINT `tNum` FOREIGN KEY (`tNum`) REFERENCES `area` (`areaNum`)
) ENGINE=InnoDB AUTO_INCREMENT=55 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of specialty
-- ----------------------------
INSERT INTO `specialty`
VALUES ('1', '1101', '2', '大唐东坡肉', '100.00', 'area/1101/specialty/1.jpg', 'area/1101/specialty/x2.jpg',
        'area/1101/specialty/x3.jpg',
        '大塘东坡肉是江西省南昌市新建县大塘坪乡的特产。大塘东坡肉具有肥而不腻，酥而不烂，鲜香味美，入口即化的特点，千百年来，一直是当地百姓婚丧嫁娶，风俗节日，筵宾待客席上必备的特色菜肴，具有广泛的影响力。\r\n\r\n　　大塘东坡肉是江西省新建县大塘坪乡一带的传统特产，历史悠久，距今已有一千多年。大塘东坡肉分布范围主要在新建县大塘坪乡、铁河乡、金桥乡、象山镇一带，以及毗邻的永修县部分乡镇。\r\n\r\n　　　　\r\n\r\n　　大塘东坡肉起源于宋代文豪苏东坡一次途经新建大塘，救治一名患中暑的小孩，小孩父母为感激苏东坡的救命之恩，从集上买来整块肉在征求苏东坡如何制作时，误会意思，弄拙成巧，烧制成的一道有独特风味的菜肴。\r\n\r\n　　　　\r\n\r\n　　大塘东坡肉制作工艺独特，它用一斤左右的整块五花肉首先煮熟，然后放入沙锅，加上姜、葱、酱油、冰糖、黄酒、葱结等佐料，用文火焖烧两小时，再将肉块装入陶罐中，加盖，以桃花纸密封，上蒸笼蒸透。\r\n\r\n　　　　\r\n\r\n　　大塘东坡肉，固其制作工艺独特，保持了原汁原味，具有丰富的营养价值，长期食用，能强身健体，保持旺盛的精力。增强人体的免疫力。\r\n\r\n　　　　\r\n\r\n　　大塘东坡肉是新建饮食文化的典型代表，大塘东坡肉的制作工艺是我国民间众多的烹调工艺中的一种独特工艺。它集历史文化名人的轶事和独特的制作工艺为一身，具有深厚的历史渊源和广泛的影响力。是历史遗留下的独特而宝贵的烹饪文化遗产。 ',
        '493', '1');
INSERT INTO `specialty`
VALUES ('2', '1101', '3', '大塘清明酒', '120.00', 'area/1101/specialty/2.jpg', 'area/1101/specialty/y2.jpg',
        'area/1101/specialty/y3.jpg',
        '大塘清明酒是江西省南昌市新建县大塘坪乡的特产。大塘清明酒因在清明节期间酿造而得名。采用优质糯米酿造，工艺独特，富含糯米的营养物质，有甜酒娘的营养，又有白酒的酒劲。\r\n\r\n　　大塘坪乡地处鄱湖之滨，虽然不是名乡名镇，但酿造的清明酒醇香可口，是家乡的名优特产，吸引了不少海内外的客人。\r\n\r\n　　清明酒是用当地的优质糯米酿制的。它的颜色与其他酒不同，储藏时间的长短决定深浅，先是白色，然后变成淡红色，再成暗红色，最后褐色、黑色，就是陈年老酒了。\r\n\r\n　　清明酒的酿制分两道工序；一道工序是在清明节前后酿制甜酒酿，和糟一起用坛装好密封；二道工序是下半年或来年再酿造糯米高度白酒，按一定比例兑入发酵后的甜酒坛中密封。这样，清明酒就制作完毕了。做工地道的清明酒不仅可口，还有药理作用，可以治疗胃肠炎和关节炎等。传说这酒还是康熙皇帝命名的呢。早年，康熙微服私访来到大塘坪一户人家，正是清明时节，主人看看康熙不像一般人，就用自己酿制多年的清明酒款待他。康熙一喝，连称：“好酒！好酒！”主人滔滔不绝地向康熙介绍这酒的酿造过程，但说不出酒的名字。康熙听后道：“此时正是清明节，何不就叫‘清明酒’。”从此，清明酒的美称流传至今。\r\n\r\n　　经江南大学检测分析，“大塘清明酒”中含有丰富的营养成份，如糖、蛋白质、氨基酸、酯类物质、微量的高级醇及多种维生素等。特别是本酒所含有的二十一种氨基酸，其中四种是未知氨基酸，八种是人体必须氨基酸，而且含量居各种酿造酒类之首，被誉为“液体蛋糕”。酒内还含有各种维生素如B1，B2，B6，C及烟酸；微量元素Zn、Fe、Mn、K、Ca、Se等，是一款营养性地方特色酒。 ',
        '695', '1');
INSERT INTO `specialty`
VALUES ('3', '1101', '2', '三杯鸡', '70.00', 'area/1101/specialty/3.jpg', 'area/1101/specialty/z1.jpg',
        'area/1101/specialty/z2.jpg',
        '因烹调鸡块时加入甜米酒、猪油、酱油各一小杯，不放汤水，用炭火将鸡块炖熟，故名。“三杯鸡”的来源，据说是厨师在外地偶然发现，回昌后，烹制方法又作了改进，调料中增加了香葱、生姜等，用小砂锅加盖放在炭炉上慢火炖制。上桌后满座飘香，砂锅内鸡块色泽发红，鸡肉鲜美，汤汁香醇，且为原　味，深受欢迎。\r\n\r\n　　 特点\r\n\r\n　　 色泽金黄、味道香浓\r\n\r\n　　 原料\r\n\r\n　　 嫩仔鸡1只（重约1250克）、生姜10克、香葱10克、猪油100克、酱油100克、客家米酒100克、香菇10克\r\n\r\n　　 制作方法\r\n\r\n　　\r\n\r\n　　 1、将鸡宰杀去毛，剖腹去内脏，洗净，剁下头、脚，鸡身剁成1.5厘米见方的块。将鸡肫、鸡肝洗净，与鸡头、脚一同切成1.5厘米见方的块，全部用砂钵装好。\r\n\r\n　　 2、将香葱打结，姜块拍松和猪油、酱油、甜米酒（各一杯）一起倒入砂钵内先用中火烧沸。再用文火炖20分钟，至卤汁收浓，拣去葱、姜，连锅端上餐桌即可。\r\n\r\n　　 文化背景\r\n\r\n　　 三杯鸡是江西传统名菜，为北京奥运会指定菜品，起源于宁都。相传有一户农家，姐弟二人相依为命。因生活贫困，弟弟要外出谋生。临行前，姐姐将家中仅有的一只母鸡杀了，剁成块，连同内脏装在砂钵里，苦于无调料，只放些米酒、猪油和酱油与鸡同煮。不料烧沸时香气四溢，惊动了在官府做厨的熊某。他登门拜访品尝了一块鸡肉，其味甚美。熊某当即求教，并加以改进和完善，用于酒宴，深受赞誉。从此，三杯鸡名扬四方。 ',
        '-2', '1');
INSERT INTO `specialty`
VALUES ('4', '1101', '2', '匡庐石鸡腿', '110.00', 'area/1101/specialty/4.jpg', 'area/1101/specialty/c1.jpg',
        'area/1101/specialty/c2.jpg',
        '匡庐石鸡腿是江西南昌的特色菜，造型别致，喷香嫩滑。制作方法：先将炒锅上火放入油将泡司炸好，倒入沥油。取鱼盘一只，锅留少许油，将白糖放入熬至带粘性的糖液，用泡司醮上液在鱼盘的一端垒成假山一座。姜切片，葱切段。再将炒锅上火放入油，烧热至四成油温里，将用蛋清、湿淀粉浆好的石鸡腿下锅滑油。锅内留油少许将葱姜下锅煸出香味，捞起葱姜，放入蒜及高汤，将蒜烧熟后，投入滑好油的石鸡腿，放料酒、精盐、味精稍炒，勾稀芡，淋明油，撒上胡椒粉，颠翻均匀，出锅装在盘子的另一端即成。 ',
        '-1', '1');
INSERT INTO `specialty`
VALUES ('5', '1101', '2', '米粉蒸肉', '30.00', 'area/1101/specialty/5.jpg', 'area/1101/specialty/a1.jpg',
        'area/1101/specialty/a2.jpg',
        '南昌人每年立夏前后都喜欢蒸上一碗米粉蒸肉，据说立夏日吃了不会生痱子。人们把大米加八角、桂皮等香料炒熟后研磨成粉，将五花肉切成厚片浸渍上辣椒油、酱油，再加白糖、料酒、味精等调味　品，然后倒入米粉拌匀，再将粘满米粉的肉一片片叠在碗内，上笼蒸熟至烂，吃时将肉扣在盘内即可。有的在肉内又拌入适量豌豆，使菜有粉香，又有碗豆清香，荤素鲜腻，别有风味。',
        '-3', '1');
INSERT INTO `specialty`
VALUES ('6', '1101', '2', '瓦罐汤', '50.00', 'area/1101/specialty/7.jpg', 'area/1101/specialty/d1.jpg',
        'area/1101/specialty/d2.jpg',
        '瓦罐汤，又名民间瓦罐汤，是民间传统的煨汤方法，是赣菜的代表。《吕氏春秋·本味篇》记载了煨汤的真谛：“凡味之本，水最为始，五味三材，九沸九变，则成至味”，几千年的历史记载和沉淀，使其独具特色。今天的瓦罐汤以江西南昌地区的闻名全国。\r\n瓦罐汤，又名民间瓦罐汤，是民间传统的煨汤方法，是赣菜的代表。《吕氏春秋·本味篇》记载了煨汤的真谛：“凡味之本，水最为始，五味三材，九沸九变，则成至味”，几千年的历史记载和沉淀，使其独具特色。今天的瓦罐汤以江西南昌地区的闻名全国。\r\n瓦罐汤，又名民间瓦罐汤，是民间传统的煨汤方法，是赣菜的代表。《吕氏春秋·本味篇》记载了煨汤的真谛：“凡味之本，水最为始，五味三材，九沸九变，则成至味”，几千年的历史记载和沉淀，使其独具特色。今天的瓦罐汤以江西南昌地区的闻名全国。\r\n瓦罐汤，又名民间瓦罐汤，是民间传统的煨汤方法，是赣菜的代表。《吕氏春秋·本味篇》记载了煨汤的真谛：“凡味之本，水最为始，五味三材，九沸九变，则成至味”，几千年的历史记载和沉淀，使其独具特色。今天的瓦罐汤以江西南昌地区的闻名全国。\r\n瓦罐汤，又名民间瓦罐汤，是民间传统的煨汤方法，是赣菜的代表。《吕氏春秋·本味篇》记载了煨汤的真谛：“凡味之本，水最为始，五味三材，九沸九变，则成至味”，几千年的历史记载和沉淀，使其独具特色。今天的瓦罐汤以江西南昌地区的闻名全国。民间饭庄瓦罐汤的是采用古来的煨制工艺，在专业瓦缸内进行煨制而出，以土质瓦罐为容器，加以食物配以纯净水为原料，以硬质木炭火恒温六面体受热，煨制出的瓦罐煨汤原汁原味，而且更含有很高的营养价值。瓦罐汤已在民间\r\n瓦罐\r\n瓦罐\r\n广泛传播，同时因为其不伤食材营养结构，已是营养汤膳的最佳煨制方式，配于多种食材和名贵药材，真正做到了营养复合和膳食功效。是补充营养、强体补虚、益智提神、养生保健的最佳饮食选择。',
        '-4', '1');
INSERT INTO `specialty`
VALUES ('7', '1101', '2', '豆耙', '10.00', 'area/1101/specialty/6.jpg', 'area/1101/specialty/e1.jpg',
        'area/1101/specialty/e2.jpg',
        '豆粑的原材料以大米为主，豆类为附，也有掺小麦和荞麦的。掺和的豆子可以是绿豆、红豆、豌豆、黄豆、蚕豆等食用豆子的一种或几种。\r\n豆粑的制作工艺很特别，主要包括把各种豆类大米等浸泡，再磨成浆，再在锅里烫成薄饼，然后将饼切成丝，晾晒干等步骤。每一个环节都很有讲究，比如各种原料的比例啦，磨浆的粗细啦，以及在锅里烫时的火候等等。\r\n由于原材料中多豆类，而且在锅中烫成了饼（江西方言中叫粑），所以豆粑的名字由此而来。\r\n制作\r\n豆粑的制作工艺较复杂，制作豆粑是农村冬季的一件大事。选质量上乘的籼米制出的豆粑才有韧性，先把精选好的原料添水浸泡14小时左右，按10斤籼米配3斤杂粮（如绿豆、黄豆、荞麦、芝麻等）的比例，\r\n合并图册\r\n合并图册(4张)\r\n 拌和均匀后按5：1兑好水磨浆。 豆粑浆磨成了，接着就是烫豆粑了。烫豆粑约需五、六个人配合才行，首先要安排一个有经验的人，站在锅边，等锅烧红后用丝瓜莆蘸油擦锅，然后用长柄木瓢或铁瓢舀半瓢豆粑浆，沿锅边均匀倒上薄薄一层，有空缺的地方用河蚌壳或刷子迅速刷匀，使薄饼厚度一致。约五分钟，一张豆粑饼就烫好了。之后把烫好的豆粑饼铺在倒放过来的竹筲箕上，由等候在旁边的人快速端走，把它放到一个直径约五尺的扁平晒筐中冷却。豆粑饼冷却后便折成长一尺五寸宽一寸半的条状，交给另一个人切成条状豆粑。接下来就把切好的细条状豆粑放到晒筐里晾晒，直到晒干为止。',
        '409', '1');
INSERT INTO `specialty`
VALUES ('8', '1102', '1', '庐山云雾茶', '80.00', 'area/1102/specialty/ee966d4b-7271-4ea9-9e61-0ba4046d4af1.png',
        'area/1102/specialty/cde2adbd-6f05-4770-b6d4-1899e0b097df.png',
        'area/1102/specialty/710d9b3d-70c1-421f-8f6a-aad13d18ad9b.png',
        '人文历史简介 庐山云雾茶，系我国十大名茶之一，始产于汉代，已有一千多年的栽种历史，宋代列为“贡茶”，是有着悠久历史具有独特内在品质和内在浓郁文化氛围的地方特色产品，以原产地终年云雾弥漫而得碧绿明亮、叶底嫩绿匀齐的品质特点，在历届茶叶评比中获得多次殊荣，1959年被评为“中国十大名茶”，1971年被列为中国绿茶之“特种名茶”，庐山云雾茶以10.99亿元的品牌评估价值位居2010年发布的全国83个茶叶区域公用品牌价值第13位。庐山种茶，历史悠久，远在汉代，这里已有茶树种植。据《庐山志》记载，东汉时，佛教传入我国，当时庐山梵宫寺多至300余座，僧侣云集。他们攀危崖，冒飞泉，竞采野茶；在白云深处，劈崖填峪，栽种茶树，采制茶叶。东晋时庐山已成为佛教中心之一，据载，当时名僧慧远，在山上居住三十余年，聚集僧徒，讲授佛学，在山中发展种茶。唐朝时庐山茶已很著名。唐代诗人白居易也曾在庐山香炉峰结庐而居，挖药种茶，并写下了诗篇：“长松树下小溪头，斑鹿胎巾白布裘，药圃茶园为产业，野麋林鹳是交游”。庐山到宋代已有洪州鹤岭茶、洪州双井茶、白露、鹰爪等名茶。到了明代，庐山云雾茶名称已出现在明《庐山志》中，距今已有300余年。\r\n　　庐山云雾茶，系我国十大名茶之一，始产于汉代，已有一千多年的栽种历史，宋代列为“贡茶”。庐山云雾茶以“味醇、色秀、香馨、液清”而久负盛名。畅销国内外。仔细品尝，其色如沱茶，却比沱茶清淡，宛若碧玉盛于碗中。它的味道，类似“龙井”，却比龙井更加醇厚，若用庐山的山泉沏茶焙茗，就更加香醇可口。\r\n　　地域范围\r\n　　九江，位于长江中下游结合部的南岸。东枕鄱阳，与安徽东至县毗邻；南界南昌、宜春，与本省新建、安义、靖安、奉新、铜鼓诸县相连；西连幕阜，与湖南平江县和湖北崇阳、通城、通山、阳新多县接壤；北濒长江，与湖北武穴、黄梅及安徽宿松、望江县隔江相望；居鄂、湘、皖、赣四省交界处，为江西的北大门。庐山云雾茶农产品地理标志地域保护范围为庐山及鄱阳湖（九江管辖范围）、庐山西海及周边区域，涉及海会镇、岷山乡、赛阳镇等185个乡镇（办事处）。保护范围位于东经113°56′30″-116°54′01″，北纬28°41′12″-30°04′29″，保护面积60000公顷，年产量1.6万吨。',
        '344', '1');
INSERT INTO `specialty`
VALUES ('9', '1102', '2', '彭泽鲫', '8.00', 'area/1102/specialty/4.png', 'area/1102/specialty/5.png',
        'area/1102/specialty/6.png',
        '彭泽鲫，因其常栖于湖中的芦苇从中，体侧有5－7条灰黑色的芦苇似的斑纹（池塘中饲养一段时间后，斑纹会逐渐消失）而被称为芦花鲫；它以个体大（已知一尾最大个体体重6.5千克）著称，所以称它为彭泽大鲫。彭泽鲫原产于江西省彭泽县丁家湖、太泊湖、芳湖、芸湖等天然水域，是江西省水产研究所和九江市水产研究所从野生彭泽鲫中，自1983年起经7年多6代的精心选育而筛选出的优良品种。\r\n　　经选育后的彭泽鲫.生产性能发生明显改观，生长速度比选育前快50%，比普通鲫的生长速度快249.8%，并成为我国第一个直接从二倍体野生鲫鱼中选育出的优良养殖品种。由于彭泽鲫具有繁殖简易、生长快、个体大、抗逆性强、营养价值高等优良性状，现已在全国大部分地区推广养殖，并形成了完整配套的鱼苗繁殖、苗种培育及成鱼养殖技术，获得了明显的经济、社会效益。 彭泽鲫背部呈深灰黑色，腹部灰色，各鳍条呈青黑色，为纺锤形。头短小，吻钝，口端位呈弧形、唇较厚，无须，下颌稍向上斜。从下颌底部至胸鳍基部呈平缓的弧形， 彭泽鲫\r\n　　.尾柄高大于眼后头长。背鳍外缘平直，尾鳍分叉浅。雄性个体胸鳍较尖长，末端可达腹鳍基部。雌性个体胸鳍较圆钝，不达腹鳍基部。 彭泽鲫为广温、杂食性的湖泊定居性鱼类，行底栖生活，喜在底质较肥活且水草繁茂的浅水区栖息和摄食。它对水温的适应范围广，能终年正常摄食和生长，最佳生长水温为25－30℃。其对水质变化及低溶氧等理化因子有很强的忍耐能力。它在鱼苗阶段以浮游动植物为食，在鱼种和成鱼阶段可摄食有机碎屑、人工饲料、水生植物碎片、水生昆虫等。在自然水域中，彭泽鲫以当年生长最快，体重可达128克左右，第二年体重增长为上年增长速度的50%左右。在人工养殖下，北方地区当年可达150克左右，南方地区可达200克左右。 彭泽鲫1冬龄可达到性成熟，能在河溪、湖泊、池塘中自然繁殖，其卵为单精虫受精，是正常的二倍体有性生殖，卵具粘性。属多次产卵类型，每年3―7月繁殖，4月为繁殖盛期。在南方，一般3月中旬以后，水温上升到17℃左右时，彭泽鲫即开始繁殖，20―24℃时繁殖活动最盛。降雨、微流水和闷热的气候对繁殖期的彭泽鲫有诱发产卵作用。',
        '400', '1');
INSERT INTO `specialty`
VALUES ('10', '1102', '2', '瑞昌山药', '7.00', 'area/1102/specialty/7.png', 'area/1102/specialty/8.png',
        'area/1102/specialty/9.png',
        '　瑞昌山药历史悠久，据明代隆庆年间的《瑞昌县志》就有记载。据民间流传，瑞昌山药早在唐朝就成为了朝廷的贡品。相传，唐明皇为给杨贵妃美容，健身丰乳，搜觅天下灵丹妙药、山珍海味。时浔阳赤乌场的地方官速选送数箱，写明烹调之法，报呈进贡。食之，肤色白里透红，容光青春焕发，精力充沛旺盛，龙心大悦，对瑞昌山药赞不绝口，赐其雅号“南参”。从此瑞昌山药便列为皇家贡品，享誉皇家，名扬天下，成为当地的传统名优特产。\r\n　　瑞昌山药块茎长棒形，表皮淡黄或浅棕黄色，长25-60㎝，粗3-7㎝，上部毛孔多，须根密，一般长5-7㎝，多呈水平方向生长，中下部表皮光滑，毛孔较稀，须根少而短，毛孔下陷。颈部灰棕色，长约8.5-12.8㎝，粗0.8-1.5㎝，密生棕褐色细根。肉质洁白，组织细密，粘液多，烹调时久煮不糊。无明显缺陷、机械损伤、斑痕、萎缩、腐烂、异味和病虫害等。\r\n　　瑞昌山药其块茎含淀粉16%、蛋白质2.7%、脂肪0.2%、碳水化合物14%、Vc5㎎/100g、皂甙50ug/100g，以及消化酶、胆碱、碘质、钙质和多种氨基酸、维生素、微量元素等。\r\n　　瑞昌山药产于瑞昌市辖区内21个乡镇场，位于海拔60-600米之间，紧靠北回归线，产地中心地理坐标为东经115.38度，北纬29.4度。生产地域主要分布在瑞昌市行政区域内，全市各乡镇场均有种植，其中主要集中在南阳、高丰、桂林、横立山、白杨、洪下、夏畈等乡镇。保护面积20000亩。年产量2万吨。',
        '695', '1');
INSERT INTO `specialty`
VALUES ('11', '1102', '1', '西港化红', '10.00', 'area/1102/specialty/10.png', 'area/1102/specialty/11.png',
        'area/1102/specialty/12.png',
        '因西港地势平坦，土地肥沃，四周水体环绕，多年形成了特殊的小气候，尤其是夏季高温和冬季的冰冻，均因水体的调节作用，高温日和冰冻日都减少。经过四百多年的种植及品种改良，现在的西港化红味道鲜美、甜而略酸，香气浓郁，每100克果汁含维生素C59.6毫克，食之有止咳、化痰、散气、健胃、消食、燥湿、醒酒、消油腻、宽中、触蟹毒等，皮可入药。西港化红具有广阔的发展前景，更是种植化红的最佳地，修水冠之以“西港化红”之乡是当之无愧的。 “西港化红”属亚热带常绿果树中橙类的一个品种，果实甜酸适度，含有多种维生素及对人类有利的多种微量元素，经医学验证“西港化红”有止咳化痰、滋阴降火、强身健体等<<本草纲目>>中称奇的医疗保健作用，是我县乃至江南都是难得的特产保健水果。',
        '566', '1');
INSERT INTO `specialty`
VALUES ('12', '1102', '2', '山药炖排骨', '20.00', 'area/1102/specialty/13.png', 'area/1102/specialty/14.png',
        'area/1102/specialty/15.png',
        '山药炖排骨是一道食补汤品，主要食材是山药和排骨，主要烹饪工艺是炖。成品脆滑爽口，营养丰富，宜多食用。\r\n山药炖排骨作为冬季一道滋补药膳，富含丰富的营养价值，这道药膳含有人体必需的多种氨基酸及优质蛋白质。多种矿物质和维生素能满足人体基本需求。山药\r\n1. 健脾益胃、助消化：山药含有淀粉酶、多酚氧化酶等物质，有利于脾胃\r\n山药炖排骨\r\n山药炖排骨\r\n消化吸收功能，是一味平补脾胃的药食两用之品。不论脾阳亏或胃阴虚，皆可食用。临床上常用治脾胃虚弱、食少体倦、泄泻等病症；\r\n2. 滋肾益精：山药含有多种营养素，有强健机体，滋肾益精的作用。大凡肾亏遗精，妇女白带多、小便频数等症，皆可服之；\r\n3. 益肺止咳：山药含有皂甙、黏液质，有润滑，滋润的作用，故可益肺气，养肺阴，治疗肺虚痰嗽久咳之症；\r\n4. 降低血糖：山药含有粘液蛋白，有降低血糖的作用，可用于治疗糖尿病，是糖尿病人的食疗佳品；\r\n5. 延年益寿：山药含有大量的黏液蛋白、维生素及微量元素，能有效阻止血脂在血管壁的沉淀，预防心血疾病，取得益志安神、延年益寿的功效；\r\n6. 抗肝昏迷:研究发现山药具有镇静作用，可来抗肝昏迷。排骨\r\n猪肉为人类提供优质蛋白质和必需的脂肪酸。猪肉可提供血红素（有机铁）和促进铁吸收的半胱氨酸，能改善缺铁性贫血。排骨除含蛋白、脂肪、维生素外，还含有大量磷酸钙、骨胶原、骨粘蛋白等，可为幼儿和老人提供钙质',
        '567', '1');
INSERT INTO `specialty`
VALUES ('13', '1102', '2', '柘林湖蕨菜', '4.00', 'area/1102/specialty/16.png', 'area/1102/specialty/17.png',
        'area/1102/specialty/18.png',
        '蕨菜是一种野菜，即蕨的嫩茎或嫩叶，亦称“乌糯”。蕨是一种多年生草本植物，生于山下阳坡或疏林下。蕨菜味道独特，性凉，食之有去热，利湿功效。柘林湖蕨菜为野生，分布在西部山区和北部高丘地区，面积约3000亩，富含粗纤维，蛋白质，有防病、保肝功效，无污染，营养价值高，为餐桌上佳品，是珍贵的天然的绿色食品，可炒吃、凉拌和腌制，速冻后包装可销往境外。蕨菜是一种野菜，即蕨的嫩茎或嫩叶，亦称“乌糯”。蕨是一种多年生草本植物，生于山下阳坡或疏林下。蕨菜味道独特，性凉，食之有去热，利湿功效。柘林湖蕨菜为野生，分布在西部山区和北部高丘地区，面积约3000亩，富含粗纤维，蛋白质，有防病、保肝功效，无污染，营养价值高，为餐桌上佳品，是珍贵的天然的绿色食品，可炒吃、凉拌和腌制，速冻后包装可销往境外。蕨菜是一种野菜，即蕨的嫩茎或嫩叶，亦称“乌糯”。蕨是一种多年生草本植物，生于山下阳坡或疏林下。蕨菜味道独特，性凉，食之有去热，利湿功效。柘林湖蕨菜为野生，分布在西部山区和北部高丘地区，面积约3000亩，富含粗纤维，蛋白质，有防病、保肝功效，无污染，营养价值高，为餐桌上佳品，是珍贵的天然的绿色食品，可炒吃、凉拌和腌制，速冻后包装可销往境外。',
        '556', '1');
INSERT INTO `specialty`
VALUES ('14', '1102', '2', '江州水浒肉', '20.00', 'area/1102/specialty/19.png', 'area/1102/specialty/20.png',
        'area/1102/specialty/21.png',
        '将猪脊肉切成小块，撒上细盐，沾上生粉，拖过蛋清，入油锅稍炸后扣碗上笼蒸片刻，浇上料汁即可。相传北宋时，宋江被囚江州（九江古名），常呼二三兄弟赴浔阳楼聚饮，每每以此菜下酒，故得是名。历史典故：传说，宋代及时雨宋江杀死阎婆惜，被发配到了江州，戴宗、张顺、李逵等好汉仰慕他的英名，打通了衙门的关节，才使得宋江免受苦刑，宋江心中十分感激。英雄们相见恨晚，情投意合，今日你请，明日我要，日日相聚，临江观景，饮酒叙旧，好不痛快。江州城(今九江市)内有一著名酒楼浔阳楼，此楼背倚延支山，面临长江，一半在岸上，一半在江上，许多英雄豪杰、骚人墨客常在吃饭。这一天，大家欢聚浔阳酒楼，店小二上了一道菜，只觉香气扑鼻，众人低头一看，红润色鲜，晶莹透亮，宋江夹起一尝，觉得美味可口，麻辣适中，连呼\"好菜!好菜!\"\r\n“怎的菜，也值得大哥如此称赞。管它是好是歹，弟兄们难得一聚，来它个猛吃猛喝，一醉方休!\"李逵大声地叫道。\"愚兄虽不及诸位弟兄走南闯北，见多识广，但也到了一些地方，像这样烹调得法色香味美的菜，还是头一次，不知那位兄弟知道此菜何名?如何制法否?\"见宋江动问，张顺拱手答道：\"小弟稍知一二。此菜唤家乡肉，是饮酒的佳肴名菜。做法极易，取瘦肉浸汁上浆，放入油锅稍炒，切片上碟，撒上葱段姜末，花椒辣椒，然后拌入适当的酱油、麻油即成。\"李逵听着毫无兴趣，连声嘟哝道：\"管它怎地做法，大哥欢喜，再弄它个十盘八盘，吃个饱就是。喝酒!喝酒!\"从此，宋江爱上浔阳楼这道名菜了。后来，宋江浔阳楼上题反诗，连累了戴宗，又被奸臣蔡京的第九个儿子蔡得章知府判处死刑。开斩的这天，李逵与梁山英雄好汉一道大闹江州，劫了法场，救出了宋江、戴宗，一起上了梁山。次日，晁盖、吴用等首领在聚义厅大摆酒宴，一来庆祝众弟兄上山结义，二来为宋江、戴宗洗尘压惊，特地吩咐厨房专门做这道宋江等人爱吃的浔阳名菜\"家乡肉\"，流传到今天就成了【水浒肉】咯!',
        '453', '1');
INSERT INTO `specialty`
VALUES ('15', '1102', '2', '武宁酸辣粉', '4.00', 'area/1102/specialty/22.png', 'area/1102/specialty/23.png',
        'area/1102/specialty/24.png',
        '说起酸辣粉，大家都以为是四川、贵州等地的传统名小吃，其实不然，武宁县多为山区，红薯从古在该县就广为种植，红薯粉、薯粉丝一直是当地的饮食主料，酸辣粉也是当地人十分喜爱的一种民间小吃。与四川等的酸辣粉不同，武宁的酸辣粉不具麻味，其特点是“辣、鲜、香、酸且油而不腻”。“酸辣粉”系纯然绿色食品，主粉是由产于当地的红薯经粉碎、过滤、晾晒打浆后，再由农家用传统手工漏制而成。说起酸辣粉的来历，广泛流传着这样的典故。可以追溯到三国时期，当时是三国时期刘备、关羽、张飞在桃园三人结义后, 桃园的主人专门为他们做的饭菜，选了当时比较为人喜欢的红苕粉做主料，寓意这三人的友情像这粉条一样绵长，又加了一种特辣的小尖椒,和老坛酸菜以及红糖与黄莲在里面，刘备的意思是要让三人明白酸甜苦辣都不怕，著名的重庆酸辣粉，还有贵州和四川的酸辣粉、由此演变而来。后人因为其味酸辣入味，渐渐就改成了“酸辣粉”了。重庆酸辣粉特点是“麻、辣、鲜、香、酸且油而不腻”。',
        '555', '1');
INSERT INTO `specialty`
VALUES ('16', '1103', '2', '南安板鸭', '34.00', 'area/1103/specialty/1.png', 'area/1103/specialty/2.png',
        'area/1103/specialty/3.png',
        '南安板鸭是江南省著名的特产。外形美观，色泽白净，皮薄肉嫩，尾油丰满，骨脆可嚼，味香可口，诱人食欲。成为国内外市场上的腊味珍品。\r\n　　早在清朝(1850年)时，南安府(即面在的大余县南安镇方屋矿一带)就有板鸭生产。当时是一家一户加工，名曰：“泡腌”。为了提高加工质量和出口价值，对“泡腌”在色、香、味，型上不断摸索，总结经验，对生产工艺进行改革，重视对毛鸭的育肥，并用辅板造形等，使鸭身成为桃圆形，平整干爽，因而得名：“板鸭”。由于板鸭生产发源于南安府，故定名为南安板鸭，至今已有近百年的历史了。南安板鸭是江西著名传统特产，与福建建瓯板鸭、江苏南京板鸭、四川建昌板鸭并列中国传统四大板鸭之一。\r\n南安板鸭历史悠久，距今500多年，并于1911年获得巴拿马世界博览会金奖。南安板鸭外形美观，皮色奶白、瘦肉酱色，肉嫩骨脆，尾油丰满、味香可口，是腊味中的上品.南安板鸭为江西省的地方名优产品。外形美观，色泽白净，皮薄肉嫩，尾油丰满，骨脆可嚼，味香可口，诱人食欲。成为国内外市场上的腊味珍品。早在清朝(1850年)时，南安府(即面在的大余县南安镇方屋矿一带)就有板鸭生产。当时是一家一户加工，名曰：“泡腌”。为了提高加工质量和出口价值，对“泡腌”在色、香、味，型上不断摸索，总结经验，对生产工艺进行改革，重视对毛鸭的育肥，并用辅板造形等，使鸭身成为桃圆形，平整干爽，因而得名：“板鸭”。由于板鸭生产发源于南安府，故定名为南安板鸭，至今已有近五百年的历史了.南安板鸭在1915年首届巴拿马太平洋万国博览会获得金奖。中国驰名商标。\r\n',
        '343', '1');
INSERT INTO `specialty`
VALUES ('17', '1103', '2', '瑞金荸荠', '12.00', 'area/1103/specialty/4.png', 'area/1103/specialty/5.png',
        'area/1103/specialty/6.png',
        '　荸荠俗称马荠。瑞金荸荠，果皮薄，汁多，味甜，无渣，可生食，也可熟食，还可加工制作罐头。\r\n　　荸荠适应于阳光充足，排灌方便，中等肥力的水田种植。7月上旬育苗，下旬移栽，12月开始收获。全市种植面积2000多亩，年产800多万斤。除本地内销外，大量销往广东、福建、上海、南昌等地，制作的荸荠罐头亦有出口创汇。\r\n　　瑞金荸荠	瑞金市荸荠种植专业技术协会',
        '345', '1');
INSERT INTO `specialty`
VALUES ('18', '1103', '2', '赣南脐橙', '11.00', 'area/1103/specialty/7.png', 'area/1103/specialty/8.png',
        'area/1103/specialty/9.png',
        '赣南脐橙果大色艳、香气浓郁、甘甜味鲜、营养丰富，堪称果中佳品。在全国橙类评比中多次名列榜首。名优品种“朋娜”、“纽贺尔”、“奈沃足娜”等被评为国优产品，远销港澳。日本、法国、俄罗斯等国。信丰被评为“中国脐橙之乡”、“脐橙出口基地县”和全国唯一的“脐橙标准化示范区”。(赣州)\r\n　　地域范围\r\n　　2003年2月12日，农业部正式发布《优势农产品区域布局规划（2003—2007年）》，将赣南列入赣南湘南桂北优势产业区，将成为我国重要的鲜食脐橙生产基地。\r\n　　特定品质\r\n　　赣南脐橙的起名有两个原因：一是赣南由于其得天独厚的土壤条件，生产的脐橙果大形正，橙红鲜艳，光洁美观，可食率达74%，肉质脆嫩、化渣，风味浓甜芳香，含果汁55%以上，可溶液性固形物含量14%以上，最高可达16%，含糖10.5%-12%，含酸0.8-0.9%，固酸比15-17:1。\r\n　　文化典故\r\n　　与美国脐橙相比，可溶性固形物含量高1-2个百分点，与日本脐橙相比可溶性固形物含量高1-3个百分点，由于品质优良，风味浓郁，富有香气，深受欢迎，无形之中消费者把赣南产的脐橙称为之为赣南脐橙。二是由于广大农民种植脐橙获得了较好的经济效益，找到了一条脱贫致富奔小康的好路子。此外，境外客商看好赣南脐橙，进一步带动了产业，为了便于销售，进入大市场，参与国际国内市场竞争，自发地将本地脐橙起名为“赣南脐橙”。',
        '345', '1');
INSERT INTO `specialty`
VALUES ('19', '1103', '1', '上犹绿茶', '56.00', 'area/1103/specialty/10.png', 'area/1103/specialty/11.png',
        'area/1103/specialty/12.png',
        '　经国家工商总局商标局核准，上犹县申报的“上犹绿茶”成功注册地理标志证明商标。这是该县获批的首件农产品地理标志证明商标，上犹也成为全国少数拥有地理商标的县市之一。\r\n　　　　上犹县是全省有名的“茶叶之乡”，因其独特的气候和地理条件，所产茶叶具有“香高、味醇、色翠、汤绿、形美”的独特风味。近年来，该县按照“三年搭框架，五年打基础，十年成体系”的发展战略构想，着力壮大“两茶一苗”产业（茶叶、油茶和桂花苗木）。经过多年的努力，茶叶在上犹县已形成了强劲的产业优势。目前，该县拥有茶园7.2万亩，年产茶叶达1500多吨。该商标的成功注册，将进一步提升上犹县茶叶品牌知名度，形成“上犹绿茶”的核心竞争力，促进广大茶农种茶积极性和增收增效。',
        '456', '1');
INSERT INTO `specialty`
VALUES ('20', '1103', '1', '赣南茶油', '78.00', 'area/1103/specialty/13.png', 'area/1103/specialty/14.png',
        'area/1103/specialty/15.png',
        '　赣南有着悠久的种植油茶、食用茶油的历史，现有油茶林面积为170万亩，2012年产油量约8500吨，预计今年超万吨，油茶种植面积居全国首位，技术研究和育苗单位有赣州市林业科学研究所等5个，油茶加工企业主要有江西仰山园油茶开发有限公司、江西科泰生物技术有限公司、兴国山村油脂食品有限公司等。\r\n　　据介绍，茶油是用油茶的果实精炼出来的油，因富含不饱和脂肪酸、甘油酯及各种维生素，具有清热降火、清肝明目、延年益寿之功效。近年来，国际粮农组织已将茶油列为重点推广的健康型食用油。在中国，茶油被誉为“东方橄榄油”，具有广阔的市场前景。\r\n　　近年，赣州市已将目光瞄向潜力无限的油茶产业：2010年4月，赣州市将已被抢注的“赣南茶油”商标购回；2011年，“赣南茶油”申报注册国家地理标志证明商标；2012年，“赣南茶油”质量检测中心成立；2013年，各加工、销售企业统一使用“赣南茶油”品牌启用。“赣南茶油”发展，正掀开抱团闯市、集合发展的新篇章。\r\n　　产地范围。\r\n　　赣南茶油产地范围为江西省赣州市行政管辖区域内的赣县、兴国县、瑞金市、于都县、宁都县、会昌县、石城县、崇义县、上犹县、信丰县、大余县、龙南县、安远县、定南县、全南县、寻乌县、南康区、章贡区以及赣州经济技术开发区（赣州经济技术开发区在章贡区和南康区行政区划范围内）。\r\n　　信息来源：赣南日报',
        '566', '1');
INSERT INTO `specialty`
VALUES ('21', '1103', '1', '于都梾木油', '67.00', 'area/1103/specialty/16.png', 'area/1103/specialty/17.png',
        'area/1103/specialty/18.png',
        '于都梾木油\r\n　　梾木油是于都县特有的传统食用油，有200多年历史，以光皮梾木果实为原料生产加工制成，富含月桂酸、肉豆蔻酸等，脂肪酸结构均衡。近年来，事过于都县委县政府提出打造“高端食用油之乡”的发展战略，将光皮梾木产业作为地方经济发展的重点之一。目前，于都县已建设光皮梾木果树基地30万亩，建成的梾木油现代化生产线获得国家发明专利。\r\n　　江西省于都县现辖行政区域\r\n　　《关于划定于都于都梾木油地理标志产品保护范围的请示》，于府文〔2015〕32号\r\n　　江西省地方标准：\r\n　　《地理标志产品 于都梾木油》（草案）',
        '677', '1');
INSERT INTO `specialty`
VALUES ('22', '1103', '2', '南康汤皮', '12.00', 'area/1103/specialty/6496b0b8-235f-4ef3-b379-367725bd3ac7.png',
        'area/1103/specialty/8da26343-9e8f-4f3a-844e-30ddfd82140a.png',
        'area/1103/specialty/bdfbccee-7e4b-4bff-aef9-f83a20a0d1d0.png',
        '汤皮，是一种用米浆做成的糕，晒干后可储藏，特好吃。汤皮有两种，一种是汤皮丝，它一丝一丝，绕成一团；另一种是汤皮片，可用来沙炒和油炸，十分香脆。现在客家人把汤皮糕当早点卖，把蒸熟的汤皮糕切成丝，配上大蒜、辣椒、酱油、生姜、盐等作料做成的引（囱）子，十分好吃。\r\n做汤皮的工序很复杂，也很累人，因此，客家妇女一般都会互相帮助。头天要先将米放在水里浸泡一天一夜，然后用石磨碾成米浆，放上切得很碎的葱、蒜、姜之类的香料搅匀，浇在竹做的小簸箕中，再将小簸箕放于锅里烧开了的水面上蒸（不直接放入水中）不大一会儿功夫就拿起来。如此反复，一次次蒸成一张张薄薄的米糕，便成了可以吃的汤皮糕。如果要做成汤皮丝，便要用上好的刀工切成丝；绕成团去晒干。如做成汤皮片，便晾在竹竿上晒干，然后用剪刀剪成片。不管是汤皮丝还是汤皮片，都是一种上好的食品，也是客家人才特有。',
        '25', '1');
INSERT INTO `specialty`
VALUES ('23', '1103', '2', '南康辣椒酱', '23.00', 'area/1103/specialty/6495d8fa-6f40-484b-9bff-56a1fde9c7f8.png',
        'area/1103/specialty/1686f511-a236-42c6-b6a4-561061639eb6.png',
        'area/1103/specialty/c21fe952-7028-4cea-8b7b-5908879fef14.png',
        '南康辣椒酱，是南康市的传统特产，创始于明末清初，迄今已有300多年的产销历史。南康辣椒酱以色泽红亮油润，酱香浓郁，甜辣味美，营养丰富，经久耐藏而著称。南康辣椒酱具有增食欲、助消化的功效。\r\n  最早此品为民间自制自食，加工方法也比较简单。后经不断改进和提高，遂成为一种味美可口的“咸辣椒酱”，在县内外广为流传。但由于多为家庭制作，只有少量咸辣椒酱拿到市场销售。到清代中叶，南康县“三元斋酱园”根据民间工艺，结合酱园条件加以总结提高，进行商品化生产。到清末，产品已销赣州、南安等地。后来，三元斋停业，由“德福斋”酱园继续生产此品。\r\n  南康辣椒酱的主要原料是红辣椒、大豆、大米、糯米、食盐和白糖等。采用传统工艺须经腌制椒坯、晒制酱坯、配制成品三大工序制成。腌制椒坯是选用红亮肉厚的鲜辣椒，洗净、去蒂、切碎后加20%的食盐，腌渍四天，中间翻拌二、三次，然后撒盐密封，可供数年之用；晒制酱坯则以炒熟的大豆一百八十斤，大米三百八十斤，糯米四百八十斤磨粉、拌和后加水揉匀，做成长十二厘米，宽六厘米，厚三厘米的坯块，蒸熟后入霉房发酵制曲，然后再加入浓度为10%的食盐水，入缸晒制成酱坯；配制成品，将四份晒制成熟的酱坯与一份腌制椒坯混和拌匀，上石磨反复磨二、三次，装进缸中日晒，待晒至半干时，再加入为辣椒酱坯总30%的白糖，继续日晒，晒至手捏成团时，即为成品辣椒酱。',
        '673', '1');
INSERT INTO `specialty`
VALUES ('24', '1103', '2', '牛皮糖薯干', '35.00', 'area/1103/specialty/09732345-3db9-4b8e-b7bd-42cc413c85b7.png',
        'area/1103/specialty/2db5d2be-8d40-4615-9e9c-515bed547e7a.png',
        'area/1103/specialty/aac9a176-b9c9-4236-9902-b3920d215298.png',
        '牛皮糖薯干，是江西兴国传统的汉族小吃，色泽红润透明，味甜爽口。\r\n  挑选每个重约3两左右的迟栽红薯，要求含淀粉少、含糖分高。选好薯块后，不必对其加工、洗涤，只需晾在通风处，使失水至薯软为止。应该选择能稳定几天的晴天，将薯块摘下洗净，投入锅内，煮熟为度。接着切片，煮熟后捞起剥皮，用或用刀，切成薄片，及时摊在蔑簟上曝晒，不可太干或太湿，至适度为止。再将晒至干燥适度的薯干叠贴起来，用棕叶丝捆扎住，扎的过程中要务必保持薯片的平整。最后添香暴蒸，将捆扎好的薯干放入蒸笼中，放点橙皮之类的香料用猛火暴蒸，软熟后解去棕叶丝，摊晒在蔑簟上，晒至适度就可收藏。',
        '560', '1');
INSERT INTO `specialty`
VALUES ('25', '1104', '1', '婺源绿茶', '145.00', 'area/1104/specialty/1aacd860-1fa5-421a-8358-e02063aa4c6e.png',
        'area/1104/specialty/e0ac369d-1e0f-4414-beb8-9a73026e7fbb.png',
        'area/1104/specialty/bfec0fc9-83d7-4453-a815-32d3513aff91.png',
        '婺源绿茶，简称“婺绿”，为全国六种名茶“绝品”之一，历史悠久，明清时代，曾列为向朝廷进献的“贡茶”。婺源绿茶历史悠久，唐代著名茶叶专家陆羽在《茶经》中就有“歙州茶生于婺源山谷”的记载。\r\n《宋史---食货》婺源的谢源茶列。婺源县地处赣东北山区，为怀玉山脉和黄山山脉环抱，地势高峻，峰峦耸立，山青水秀，土壤肥沃，气候温和，雨量充沛，终年云雾缭绕，最适宜栽培茶树。这里“绿丛遍山野，户户有香茶”，是中国著名的绿茶产区。\r\n  婺源茶叶，品种繁多，质量上乘。著名品种有“上梅州”、“大叶种”、“小叶种”、“圆叶种”、“长叶种”等等。\r\n\r\n叶质柔软，持嫩性好，芽肥叶厚，有效成分高，宜制优质绿茶。选用“上梅州”良种茶叶为原料，精心制作而成的“茗眉”茶，香气清高持久，有兰花之香，滋味醇 厚鲜爽，汤色碧绿澄明，芽叶柔嫩黄绿，条索紧细纤秀，锋毫显露，色泽翠绿光润。',
        '562', '1');
INSERT INTO `specialty`
VALUES ('26', '1104', '2', '江湾雪梨', '7.00', 'area/1104/specialty/c57e138f-c1fd-4260-9d40-3d78f412f0f7.png',
        'area/1104/specialty/48fd5f87-d9d2-42ef-939d-dbe6a1092b02.png',
        'area/1104/specialty/64eceb91-ac5d-409c-afcb-9017bb9a4ecc.png',
        '简介\r\n\r\n江湾雪梨，是婺源的传统特产，历史悠久。品种有“六月雪”、“西降坞”、“白梨” 、“苏梨”、“马铃梨” 等，龙以“西降坞”梨为上品。 以个大皮薄、肉厚核小、松脆化渣而著名，一般是果重300－400克，最大800克，果实阔瓢形，果皮绿色套袋后呈白色，富含多种氨在酸、维生素及矿物质，色香味形俱佳，并具有明显的生津、止咳、滋阴润肺，退热消喘等医疗保健功效。\r\n历史文化\r\n\r\n据《婺源县志》载：“明代，婺人从歙县丁字桥引进梨苗与当地野生棠梨嫁接，并以杮油渍纸套袋，成熟时果实雪白，故谓之雪梨”，历史上曾一度作为贡品，颇负盛名，民间还常作为中秋佳节馈赠亲朋好友的上等礼品。',
        '566', '1');
INSERT INTO `specialty`
VALUES ('27', '1104', '2', '荷包红鲤鱼', '23.00', 'area/1104/specialty/ed1eac79-386f-4c92-91c7-d328f28af16e.png',
        'area/1104/specialty/5ab2f74e-0cf5-47ac-bcd1-890eaaa7205d.png',
        'area/1104/specialty/2eb08c16-af65-492a-a9b5-44c931b2a04d.png',
        '简介\r\n\r\n荷包红鲤鱼，产于江西省婺源县，是当地独有的传统养殖鱼类，据《徽州府志》载已有三百多年的养殖历史，因色泽鲜红、头小尾短、背高体宽、背部隆起、腹部肥大、形似荷包而得名。 荷包红鲤和婺源绿茶、龙尾砚（歙砚）、江湾雪梨一起被称为“婺源四色”，与兴国红鲤、玻璃红鲤并列为“江西三红”。\r\n历史文化\r\n\r\n据史料记载，明朝万历年间，时任户部右侍郎、总理漕储的婺源人余懋学（死后追封为工部尚书）将荷包红鲤献给明神宗，以示家乡“物华天宝”。明神宗目睹荷包红鲤雍容华贵之体态，鲜妍吉庆之色彩而龙颜大悦，称之为“圣鱼”，奉养于故宫御范池中，使之得以“长伴君侧”。 婺源籍户部尚书余樊学告老还乡，将皇帝御赐的红鲤鱼带回放养，后逐步繁殖，流传民间。荷包红鲤鱼不仅肉质肥美，营养丰富，且具药用价值。清《医林篡要探源》称其“安妊孕，好颜色，止咳逆，疗脚气，消水肿，治黄疸……”，具有和脾、滋肝、补心的功能。 婺源县于1958年成立了荷包红鲤鱼研究所，搜集种鱼17条，经20多年的系统选育、7代提纯复壮，培养出“荷源鲤”、“芙蓉鲤”等优良品种。1980年通过鉴定，成为中国第一个人工选育淡水养殖的优良鱼种，并按照无公害模式进行养殖。',
        '451', '1');
INSERT INTO `specialty`
VALUES ('28', '1104', '2', '横峰葛', '7.00', 'area/1104/specialty/a0c4706d-3375-46e8-9437-b42511fac716.png',
        'area/1104/specialty/8b25baa4-4e2b-418d-82a3-1acd7e86154e.png',
        'area/1104/specialty/a4fdeb16-0fa4-418d-aa6a-69afceb01c15.png',
        '简介\r\n\r\n横峰葛，是江西省横峰县特产中药材。横峰县葛生产加工始于晋代，历史悠久。横峰葛源葛粉因品质优良、清热降火、生津解酒功效显著而闻名遐迩。宋朝以来葛粉一直是横峰进贡朝庭的珍品，尤以明、清为盛。 葛，也称野葛，属于多年生豆科藤本植物，原生长于高山野地。别名鹿藿、黄斤、鸡齐，多年生野外豆科藤本植物，药、食两用，盛产于江西省横峰县葛源镇山区。葛源之葛，由于生长环境独特，品质最佳，闻名于世。 葛具有极高的营养价值、医药价值，在我国素有“南葛北参”之美称。葛花，紫红色，葛谷，形如豆荚，均是理想的解酒之药。葛叶，富含蛋白质，可做中药材，也是上等的畜禽饲料。葛藤，既可编篮做绳、纺纱织布，又可做中药材。葛根，形肥大，味甘辛,平,无毒，提制淀粉供食用，也做药用。\r\n历史文化\r\n\r\n在“中国葛之乡”横峰，葛根加工史最早可溯源至西晋。西晋著名的道学家、药物学家葛玄云游至横峰县北部山区，在一处环境优美且物产丰富的盆地驻足，发现满山遍野分布着一种植物，这种植物生命力很强，根茎可以食用，又可以入药，还可以用于浣纱、酿酒，便一边炼丹采药，一边研究这种植物的根、藤、叶、花等食用和药用价值，后来他便以自己的姓氏将之命名为“葛”。由于葛的药用价值给当地的经济带来很在的影响，后人便称葛玄洗葛之溪称为“葛溪”，取此地名为葛源，后葛源凭葛而闻名遐迩。',
        '438', '1');
INSERT INTO `specialty`
VALUES ('29', '1104', '1', '万年贡米', '67.00', 'area/1104/specialty/7f8b51d7-92ab-4e61-91d4-9f22a34c7180.png',
        'area/1104/specialty/cf598b2b-0b2e-49b2-a75a-f335b820f5ba.png',
        'area/1104/specialty/cec4db86-0f22-40fc-b868-075bd3279608.png',
        '简介\r\n\r\n万年贡米，原产于江西万年县裴梅镇东南部山区荷桥龙港一带，距今已有1000多年种植历史，属野生稻，一年栽一季，全生育期175天。因只有龙港荷桥两村特定的山区水田种植出来才是真正的万年贡米，因此产量稀少，但米质独特，杂交水稻之父袁隆平曾来实地考察过。万年贡米颗粒大，体细长，颗形如梭，米色似玉。用其做饭，质软不腻，味道浓香；以贡米为原料酿酒，浓而不烈。其米、其酒都品质优良，别具风味。 食材特点：万年贡米，吸取四季清泉，根植水土特异，营养丰富，颗粒大，体细长，颗形如梭，米色似玉。用其做饭，质软不腻，味道浓香；以万年贡米为原料酿酒，浓而不烈。其米、其酒都品质优良，别具风味。誉盖五谷之首。经测定，万年贡米蛋白质含量比普通大米高1-2倍，且含丰富的维生素B族和一定数量的微量元素，堪称米中珍品。\r\n历史文化\r\n\r\n相传，早在南北朝时期（公元420-589年），皇帝南巡，途中梦见江南有“千斤冬瓜，寸长大米”，便差人到南方查访。当差人查访至江西万年县（明置县）荷桥、尤港一带时，果然在野生稻谷中发现有“三寸粒”稻米，而且米质非同一般，便速回朝呈报皇帝。皇帝听后大喜，当即传旨江西荷桥、尤港“代代耕食，岁岁纳贡”。',
        '578', '1');
INSERT INTO `specialty`
VALUES ('30', '1104', '1', '上饶白眉', '134.00', 'area/1104/specialty/7025c8ad-2979-4f92-9ecc-ad396a809630.png',
        'area/1104/specialty/56dfdefa-f7df-42a7-80ba-2432f99cefed.png',
        'area/1104/specialty/6bbbb134-fd7b-40eb-8a64-240369746b30.png',
        '简介\r\n\r\n是江西省上饶县创制的特种绿，它满披白毫，外观雪白，外形恰如老寿星的眉毛，故而得此美名。其鲜叶采自大面白茶树品种。白眉茶外形壮实，条索匀直，白毫特多，色泽绿润，香气清高；滋味鲜浓，叶底嫩绿，由于鲜叶嫩度不同，白眉茶分为银毫，毛尖和翠峰三个花色。各具风格，品质皆优，总称上饶白眉。 上饶白眉总的特征：外形壮实，条索匀直，白毫满披，色泽绿润，香高持久，滋味鲜浓，汤色明亮，叶底嫩绿，品质极佳，为绿茶珍品，由于鲜叶嫩度不同，加工的白眉可分为白眉银毫、白眉毛尖、白眉翠峰。品质除具有共同特点外，各具独特风格。白眉银毫为一芽一叶初展加工，近似银针，外形雪白，香气清高具有浓厚的熟栗香，味鲜美回甜，茶叶沏泡杯中，朵朵茶芽犹如雀舌，婷婷玉立。白眉毛尖，芽叶肥壮，白毫显露，香高持久，滋味鲜醇。白眉翠峰，外形半实多毫，滋味鲜爽浓厚，汤色碧绿清澈。\r\n历史文化\r\n\r\n上饶县产茶历史悠久。唐代诗人孟郊在《题陆鸿渐上饶新开山舍》一诗中说：“惊彼武陵状，移归此岩边；开亭拟贮云，凿石先得泉”。说明茶圣陆羽曾在上饶建屋居祝据明代《广信府志》记载，陆羽的山居，就筑在上饶城北的茶山寺。其凿石开出的泉水，后人称之为陆羽泉，“以土色赤，又名胭脂井”；其所建小亭，名为“观泉亭”。',
        '456', '1');
INSERT INTO `specialty`
VALUES ('31', '1105', '1', '靖安白茶', '230.00', 'area/1105/specialty/e144a09f-4f00-4773-884d-82f8c6fdccf5.png',
        'area/1105/specialty/ee435faa-ed10-402b-bdb4-1a964febe0d3.png',
        'area/1105/specialty/67ed936e-0538-426c-9254-e68ff168bf53.png',
        '靖安县鑫农茶厂参评的“霞峰山”牌靖安白茶以“味甜香浓、色泽白嫩、汤色明亮、外形匀整”等特点荣获金奖。这是靖安白茶在参加各种国际国内名优茶评比活动中又一次获殊荣。\r\n\r\n　　白茶原属深山野生异型茶株，它不仅珍稀、营养丰富，而且市场价格非常高。靖安县森林覆盖率高达82%以上，冬季低温时间长，空气相对湿度78%，直射的蓝紫光较少，土壤富含微量元素，这些特定的条件为靖安白茶返白过程和物质代谢提供了独特的生态环境，有利于靖安白茶中氨基酸等氮化合物及营养物质的形成和积累，为靖安白茶香浓味鲜的品质奠定基础。\r\n\r\n　　经过该县农业科研人员10余年的刻苦攻关和精心培育，终于使白茶在靖安开花结果，成为该县的富民产业之一。短短3年时间，该县开发白茶万余亩，苗木基地3个，科技示范基地4个。同时被列为“江西省白茶产业化县”，“江西省白茶苗木基地县”。靖安白茶先后在2006年第三届中国国际茶博会、第六届国际名茶评比，2007年江西婺源首届茶博会，2008年第七届国际名茶评比，2009年-上海国际茶文化节“中国名茶”评选、第二届江西省茶博会上荣获金奖。靖安白茶已成为该县农业产业结构调整中逐步发展壮大的一个独具地方特色的朝阳产业。\r\n\r\n　　 白茶是绿茶中的珍稀品种，须在特定优越的生态环境中才可生长,对生态条件要求严格,其成品茶多为二叶一芯嫩芽，在春季发芽时，新梢嫩叶的叶色呈可逆性白化现象，满披白毫，外形条素天然，色为玉霜，光亮油润，肉质自然展开颇具高雅特色。其氨基酸含量是高档绿茶的6-8倍，对人体具有清肝明目、补肾、增白、抗衰老和抗癌等效果，倍受人们喜爱。经专家实地考证,靖安三爪仑国家森林公园内有着得天独厚的白茶生长环境,目前已开发优质白茶生产基地一万余亩。',
        '342', '1');
INSERT INTO `specialty`
VALUES ('32', '1105', '2', '奉新猕猴桃', '35.00', 'area/1105/specialty/6779f164-1467-4604-ab3d-057e93ef4fd6.png',
        'area/1105/specialty/f087d1ae-f9b2-40c3-9fe0-f9d3a037926f.png',
        'area/1105/specialty/263d2e9f-7259-4ea1-9bc0-13545ccbf370.png',
        '奉新猕猴桃为农产品地理标志产品。\r\n　　奉新，座落在江西省西北部，距省会南昌仅63公里。这里山清水秀，自然条件优越，素有\"仙源灵境\"的美称。独特的水、土、光、肥等自然条件为猕猴桃生长营造了得天独厚的生态环境，成就了全国最大的集中连片中华猕猴桃商品基地。1978年，奉新县对猕猴桃的良种选育、人工栽培及加工等进行了科研攻关，选育出了\"早鲜\"(采摘期8月下旬—9月上旬)、\"魁蜜\"(采摘期9月中旬—10月上旬)、\"金丰\"(采摘期9月下旬—10月上旬)三个中华猕猴桃品种，赢得了\"中华猕猴桃故乡\"的美誉。近年来又引进了美味系列品种\"金魁\"(采摘期11月上旬)。全县现有猕猴桃种植面积3万亩，万亩集中连片猕猴桃基地1个，千亩基地5个，百亩基地20多个，2001年产量达4000万斤以上，2002年产量可达6000万斤。\r\n\r\n　　由国家环保总局有机食品发展中心（ofdc）通过近三年的考察和认证工作，在江西省奉新县果业局和基地猕猴桃协会成员的共同努力下，江西省奉新县124.4亩优质猕猴桃基地，经过三年的艰苦奋斗，其基地的有机转换工作达到了国家环保总有机食品发展中心认可，基地完全按照有机食品发展中心的各项规定，严格按国际上有机食品生产转换要求，经检验生产中的各种定量定性指标均达到ofdc的规定要求，近期（2003）经国家环保总局有机食品发展中心确认和认证，按要求颁发了（ofdc）有机农场转换证书。\r\n\r\n　　该基地距离县城8公里，紧傍省道两侧，生态优良,土壤适宜，交通便利。从96年开始建设至今，果园面积已达10000多亩。早鲜、魁蜜、金丰三个中华猕猴桃品种占60%,其中魁蜜等品种具有绒毛少、出汁高、香味浓、果肉金黄等特点，是加工的优良品种。从湖北农科院引进的综合性能优良的美味系列品种\"金魁\"占40%,该品种经上海农学院\"上海猕猴桃栽培优良品种选育研究课题组\"选优结果表明，其突出的特点是糖度高、风味浓、耐贮藏(可贮藏至翌年3月),其可溶性固形物含量为人工栽培品种最高，特别适合消费者的口味。\r\n\r\n　　在生产管理上，奉新县狠抓了深施重施农家有机肥，增加土壤有机质，使猕猴桃成为真正的\"营养金矿\"；建立了严格的质量认证监控体系，按照有机农业生产要求，禁止使用化学肥料，化学农药、化学除草剂和激素；狠抓了采摘时间、采收方法、分级包装和冷藏保鲜四个环节的科学管理，严禁次品果入市，确保果品风味纯正，使\"VC之冠，水果之王\"的猕猴桃成为名符其实的绿色食品。奉新县猕猴桃产业有机食品申报工作正在进行，前期准备工作已完成，年底有望获得有机转换证。\r\n\r\n　　奉新县热忱欢迎客商到奉新县独资或合作兴办猕猴桃基地和深加工企业及贩运猕猴桃鲜果，奉新县将确保优质猕猴桃鲜果的供应并提供各种便利。\r\n\r\n　　国家林业局公布的第三批中国名特优经济林之乡名单中，奉新县被命名为中国猕猴桃之乡。\r\n\r\n　　地域范围\r\n\r\n　　奉新东与安义交界，南与高安接壤，西与宜丰、修水毗邻，北与靖安相连。奉新猕猴桃地域保护范围为赤岸镇、澡下镇、会埠镇、罗市镇、上富镇、澡溪乡、仰山乡、甘坊镇、柳溪乡、石溪办事处、百丈山管委会、东垦场共12个乡（镇、场），保护范围位于东经114045\'28〞—115033\'55〞、北纬28034\'45〞—28052\'16〞之间。保护面积5000公顷，年产量8万吨。\r\n\r\n　　产品品质特性特征\r\n\r\n　　1、外在感官特征：奉新猕猴桃果实中等大小（单果重80克—110克），果面茸毛少，果皮浅褐至褐色，果实椭圆形至2、内在品质指标：可溶性固形物17%—21%，总酸≤1.3%，粗纤维0.4%—0.7%，维生素C 90毫克/100克—160毫克/100克。 3、安全要求： 奉新猕猴桃质量安全符合GB2762-2012《食品安全国家标准食品中污染物限量》和GB 2763-2014《食品安全国家标准食品中农药最大残留限量》的要求。',
        '564', '1');
INSERT INTO `specialty`
VALUES ('33', '1105', '1', '宜春苎麻', '8.00', 'area/1105/specialty/072b39ca-4642-4f67-8596-eba9de3e6355.png',
        'area/1105/specialty/727e2a8a-7f93-4cc4-8e9e-327c5d52c88f.png',
        'area/1105/specialty/29467185-c3dd-4c75-9187-3f48b5c7a67c.png',
        '宜春苎麻为农产品地理标志产品。\r\n　　中国麻纺行业协会授予我市“中国苎麻历史名城”称号，使我市成为全国第一个获此殊荣的城市。同时，我市江西井竹实业有限公司被授予“中国苎麻袜生产基地”和“中国苎麻文化博物馆”称号。\r\n\r\n　　苎麻俗称“中国草”，其纤维是一种优质的天然纤维。苎麻纺织品具有导汗、透湿、快干、抗菌、穿着凉爽舒适等特点。宜春苎麻的种植和生产有2600多年的历史，袁州区的夏布（以苎麻为主要原料）以“柔软润滑，平如水镜，轻如罗绡”饮誉海内外，是全国著名的“夏布之乡”。\r\n\r\n　　近年来，我市高度重视苎麻产业的发展。目前，全市苎麻种植面积达5万亩，有苎麻加工企业51家，行业从业人员20余万人。“中国苎麻历史名城”这一金字招牌，对进一步推动我市苎麻产业种植基地化、生产规模化、产品高端化、市场多元化，提高农民收入、打响苎麻品牌具有积极作用。\r\n\r\n　　宜春苎麻 种植（棉麻蚕桑）\r\n\r\n　　地域范围\r\n\r\n　　宜春西与萍乡市交界，北与湖南浏阳接壤，南与吉安市的安福县相邻。宜春苎麻地域保护范围包括袁州区的金瑞镇、三阳镇、慈化镇、天台镇、洪塘镇、渥江镇、竹亭镇、水江镇、楠木乡、辽市镇、寨下镇、芦村镇、柏木乡、湖田镇、新田镇、西村镇、新坊镇、南庙镇、彬江镇、洪江乡、温汤镇、飞剑潭乡18个镇4个乡；万载县的株潭镇、黄茅镇、潭埠镇、双桥镇、高村镇、罗城镇 、三兴镇、鹅峰乡、马步乡、高城镇、赤兴乡、岭东乡、白水乡、仙源乡、白良镇、茭湖乡9镇7乡；上高县的田心镇、翰堂镇、泗溪镇、徐家渡镇、新界埠镇、蒙山镇、南港镇、芦洲乡、锦江镇、野市乡、塔下乡、镇渡乡、敖山镇、墨山乡9镇5乡；以及宜丰县的石市镇等共53个乡（镇）。保护范围位于东经113°53′04〞—115°02′14〞，北纬27°48′38〞—28°06′03〞之间。保护面积5000公顷，年产量11250吨。\r\n\r\n　　产品品质特性特征\r\n\r\n　　1、外在感观特征：宜春苎麻属多年生、丛生型、株高150㎝－250㎝、茎粗0.8㎝－1.30㎝、鲜皮厚0.75㎜－0.90㎜；茎杆有绒毛、富弹性、抗倒性强、工艺成熟期茎杆黄褐色，麻骨绿白色；叶片近圆形，绿色，叶背有白色毡毛。鲜麻淡清香、果绿色。原麻刮制质量上乘，青白色，手感柔软且富有弹性，不皱不缩，斑疵少，锈脚短，无麻骨麻壳附着。 2、内在品质指标：原麻纤维支数1900m/g－2450m/g，单纤维强力28cN－3０cN， 含胶率26%－32%。 3、质量安全要求：产品质量符合GB/T7699《苎麻》的规定要求。',
        '552', '1');
INSERT INTO `specialty`
VALUES ('34', '1105', '2', '宜春扎粉', '4.00', 'area/1105/specialty/03247e1c-a0b6-4ae7-8c1c-978c56ac0d88.png',
        'area/1105/specialty/f9da19f9-3bf2-496d-a3bb-b60cfd9fd047.png',
        'area/1105/specialty/c3f7d55a-f18d-4fed-8e56-50150f72c31b.png',
        '宜春扎粉，是江西省著名的小吃，以色白如银，条索柔韧，入口爽滑著称。手工扎粉生产可上溯至明朝，可谓历史悠久。说起扎粉，许多去过宜春的人都吃过，南方富庶的鱼米之乡，自古以来就有将富余的米做成米粉的传统。\r\n晚米收割上来之后，经过浸米、磨浆、滤干、采浆等工序，做成一摞一摞干的米粉，晒干后的米粉捆扎成一扎扎，故称扎粉，这样也方便了携带。干的米粉煮后，沥干变成湿的米粉，就可以开始了第二轮的加工，而炒扎粉最为风行。\r\n炒扎粉前，将干扎粉用温水浸发至手捏不易断，抓一把竖起来不易软倒为止。然后，洗清沥干待用。铺料有瘦肉丝、不白菜、嫩蒜苗，均切成寸长段。火候要适中，将猪油烧至六成热时，倒入肉丝煸炒，依次加入肉丝、蒜苗、小白菜，再加入少许汤料、酱油、红椒末，成金黄色后，再放进扎粉拌炒。最后放入姜丝、胡椒粉、味精，浇上少许香油拌匀起锅。家乡的扎粉，炒法看似简单，但其水土所孕育出的独特的韧性和香气，却是其它的米粉所绝不能有的。\r\n扎粉已经成了宜春小吃的一部分，家乡人食之，好之，习之，那从滋滋的锅里冒出的香气，不知让多少飘泊异乡的游子千万次梦回乡里，思之，念之，心向往之。',
        '453', '1');
INSERT INTO `specialty`
VALUES ('35', '1105', '2', '冻米糖', '7.00', 'area/1105/specialty/5fc6a652-6eb3-4e36-8799-2ba704c221de.png',
        'area/1105/specialty/13ac089d-ab79-4926-a0ee-291a3c4a5983.png',
        'area/1105/specialty/2b7abcfc-90a4-415e-b134-5dd9ad4b6dbf.png',
        '每年中秋一过，农村家家户户把糯米饭凉干，炒成爆花米，用米糖拌粘，切成块块，待客解茶。这种糕点叫做\"炒米糖\"、\"米花糖块\"。更讲究者，用上等糯米饭做成冻米\"干饭\"，再用清茶油煎泡\"干饭\"，使\"干饭，变成爆花米，再用饴糖粘结，冷却后用薄刀切成小块，前后经过半成品和成品共20个工序加工而成，俗称\"小切\"。解放初，改名为\"冻米糖\"、丰城冻米糖自农历八月十六日上市，到次年二、三月为止，一直行销本省及泸、港、南洋，是茶会中的应时细点，又是旧历年间拜年送礼的佳品。\r\n  据1949年2月4日《民国日报》载:\"丰城冻米糖自农历八月十六日上市至二、三月为止，一直行销本省及沪、港一带，是茶会中的应时细点，又是旧时拜年送礼的佳品。\"\r\n\r\n丰城冻米糖是江西名产和国家优质食品。2003年，已向美国纽约出口\"蔬菜型\"、\"黑芝麻型\"、\"桂花型\"等七个品种5000箱(件)，创外汇5万多美元。\r\n\r\n在中国，春节前，农村里有切糖制作冻米糖的风俗。传说此俗已流传了近千年，至今仍在延续着。 冻米糖是将上乘糯米蒸熟后放在露天晒、冻，然后下锅炒，使之膨化，再拌和白糖、饴糖做成。然后用刀切出一片片印痕，食用时用手分片即可。制作再讲究些的可加上红、绿米和桂花、芝麻、桔片米等天然香料，做成色、香、味俱全的糕点。\r\n\r\n每年中秋一过，农村家家户户把糯米饭凉干，炒成爆花米，用米糖拌粘，切成块块，待客解茶。这种糕点叫做\"炒米糖\"、\"米花糖块\"。更讲究者，用上等糯米饭做成冻米\"干饭\"，再用清茶油煎泡\"干饭\"，使干饭，变成爆花米，再用饴糖粘结，冷却后用薄刀切成小块，前后经过半成品和成品共20个工序加工而成，俗称\"小切\"。',
        '556', '1');
INSERT INTO `specialty`
VALUES ('36', '1105', '1', '上高紫皮大蒜', '6.00', 'area/1105/specialty/3fbf383e-ea8e-4eaa-b853-d04b8b007beb.png',
        'area/1105/specialty/779b2014-f049-417c-8d7d-1dbd62ed5358.png',
        'area/1105/specialty/327a193d-9d2f-4913-9a36-ab213b6de3c5.png',
        '上高紫皮大蒜为农产品地理标志产品。\r\n　　类别：蔬菜 产品名称：上高紫皮大蒜 登记证书编号:AGI01111 农业部公告号/时间:第1925号(2013.4.15) 人文历史简介 上高紫皮大蒜是上高传统的优良地方品种，种植历史悠久，清康熙《上高县志》有大蒜种植的记载。1937年《江西农业统计》“上高种植大蒜500亩，总产蒜头7500担。”1985年生产面积达到了644公顷，总产大蒜头1620吨。早在清同治四年（1858年），上高就开始从事商品大蒜生产，所生产的大蒜主要有新余、樟树、丰城、高安等地客户上门采购。而且上高紫皮大蒜获得国家农产品地理标志登记后，品牌知名度和产品附加值明显提升，无论青蒜还是蒜籽平均高出同类大蒜1.5元/斤，直接带动蒜农亩增收1200多元。据统计，上高紫皮大蒜年产值6825万元，其产品远销南昌、上海等大中城市，且供不应求，发展潜力巨大。\r\n\r\n　　上高县的紫皮大蒜是江西省的名产。上高大蒜有独特的风味，浓香清甜，辛辣而不刺舌，捣碎成汁，呈粘液状，久放也不变馊。有强烈的杀菌功效，是美味佳肴中极好的调味品。\r\n\r\n　　上高县紫皮大蒜在全省乃至全国久负盛名，早在康熙年间就有记载。其鳞茎扁园紫红色，具有瓣大蒜薄，叶肉肥厚，纤维少，浓香清甜，有特殊的辛辣味的特点。\r\n\r\n　　上高县主要品种有上高紫皮大蒜，四川二水早等品种。其中上高紫皮大蒜是上高的名特优产品，其鳞茎扁园紫红色，具有瓣大蒜薄，叶肉肥厚，纤维少，汁液粘滑，味香浓郁，有特殊的辛辣味，可增进食欲，并有抑菌和杀菌作用。全县种植面积达3万亩，产蒜籽12000吨，产蒜苔6000吨。\r\n\r\n　　上高县绿野紫皮大蒜专业合作社申报的“上高紫皮大蒜”通过农业部农产品质量安全中心审查和组织专家评审，实施国家农产品地理标志登记保护。\r\n\r\n　　地域范围\r\n\r\n　　上高位于江西西北部，锦江中游。上高紫皮大蒜地域保护范围包括塔下乡、蒙山乡、芦洲乡、南港镇、翰堂镇、上甘山林场6个乡镇（场）；保护范围为东经114°48′46″—115°00′00″，北纬28°07′30″—28°07′32″之间。保护面积2000公顷，年产量9750吨。\r\n\r\n　　产品品质特性特征\r\n\r\n　　1、外在感官特性：上高紫皮大蒜，其鳞茎呈扁圆形或短圆锥形，球茎3cm左右。外面有紫红色膜质鳞皮，剥去鳞叶，内有6—10个蒜瓣，蒜瓣紧凑，叶肉肥厚，纤维少，汁液粘滑，味香浓郁，口感爽脆，辛辣而不刺舌。 2、内在品质指标：上高紫皮大蒜含17种氨基酸总量4.50g/100g—4.90g/100g，粗纤维0.56%—0.60%，蛋白质6.2%—6.8%，维生素C 6.95mg/100g—7.15mg/100g，磷0.11%—0.15%，钾0.38%—0.44%。 3、质量安全要求： ①产地环境质量符合《无公害食品 蔬菜产地环境条件》（NY5010—2002）要求。 ②产品质量符合《无公害食品 蒜类蔬菜》（NY5001—2007）要求。',
        '561', '1');
INSERT INTO `specialty`
VALUES ('37', '1106', '2', '安福火腿', '56.00', 'area/1106/specialty/cb99449f-5db6-40f3-b2f8-c59fcb091f62.png',
        'area/1106/specialty/7f1b2dc2-ba85-4a64-b2ee-b48a5525cd39.png',
        'area/1106/specialty/d855ecd8-9d93-48fc-a260-c0633a4386a9.png',
        '　产地：中国江西安福。\r\n\r\n　　特点：其形如柳叶，脚蹄短小，腿身饱满，皮薄肉嫩，精多肥少，色红味香，经久不变质。 只要保管得当，存放二、三年仍色味如初，精肉鲜红似火，肥肉洁白晶莹，是烹制 佳肴的上乘原料。而且含有的蛋白质、脂肪、钙、铁及多种氨基酸等营养成分，既 是补益身体的营养食品，也是赠送亲友的上等礼物。\r\n\r\n　　工艺：安福火腿选料讲究，加工精细。腿料选自本县东北一带农村饲养的一种“安福米猪”。 这种猪体形小，饲养期短，骨细肉嫩，适宜腌制火腿。收购时，严格按照 标准规格 验收，对公猪、母猪、病残猪之腿一律不用。加工时，首先把住季节关，“立冬”开 始收腿，“立春”结束，其它季节，概不加工。其次是加工工艺上，清腿、配料、入 盐、磨皮、打签、入缸、翻缸、出缸、发酵、洗晒、发酵、贮存等工艺方面，安福 火腿还有独到的工夫，保证了产品的风味特色。 名称：清炖荷包红鲤鱼 产地：中国江西。\r\n\r\n　　历史：清炖荷包红鲤鱼，是婺源县闻名全国的“池中芳贵，席上佳肴”。据有关方面考察，这种鱼原是明代宫中的观赏鱼，神宗皇帝朱翊钧在位时，祖籍婺源沱川的户部尚书余懋学告老回乡，神宗御赐鲜鱼一对。尚书遂将鱼投入池中，以后衍生繁殖，世代延传，由于这种鱼背宽、头小、尾短、腹部肥大，放在桌上似荷包，故名。据《微州府志》载，荷包红鲤鱼在婺源养殖三百多年的历史。1979年荷 包红鲤鱼被列为全国鲤鱼良种。该鱼形雅、色美、肉嫩、味鲜、营养丰富。清炖“荷包红鲤鱼”一菜，盛名江西。 特点：用荷包鲤鱼炖制而成。成菜肉质细嫩，汤清而粘唇，味醇鲜美。 工艺：将鱼宰杀，去鳞除鳃，取出内脏洗净。将鱼放在盘中，加笋片、火腿片、精盐、味精、葱、姜、酒，入锅清炖至熟，淋上猪油少许即成。',
        '445', '1');
INSERT INTO `specialty`
VALUES ('38', '1106', '2', '井冈豆皮王', '23.00', 'area/1106/specialty/d064b00b-915e-4ddb-b5c8-3c54eafeb1ec.png',
        'area/1106/specialty/a54563f6-7602-42c5-8848-ee11aceeff4d.png',
        'area/1106/specialty/6bdf24ad-ab1c-4bde-a4fb-adf819470641.png',
        '嘎格耶”豆皮王产自遂川县西溪乡，它以井冈天然山泉和当地优质黄豆为原料，经多道独特传统工艺精心制作而成。明时，豆皮列为贡品，盛名于世。它之所以品质超群，特色鲜明，一是遂川县西溪乡的山泉源于五指峰，终年水清如镜，富含矿物质，内聚力强，是豆皮最理想的制作用水；二是工艺独特，在西溪乡，制作豆皮的绝活--“三口风”从不外传，仍沿袭“父传子，兄传弟”的传统，且一代更比一代强。\r\n\r\n　　“嘎格耶”豆皮王不含任何添加剂，形如卷叶，薄如蝉翼，润滑白净，味道鲜美，蛋白质含量高达48—54%，相当于瘦猪肉、牛肉的1—2倍，且各种氨基酸齐全，有健脾宽中，润燥祛火，止咳化痰的食用效果。数百年来，一直被当地山民作为馈赠亲友、病人康复、产妇滋补的首选佳品。\r\n\r\n　　',
        '436', '1');
INSERT INTO `specialty`
VALUES ('39', '1106', '2', '井冈山烟笋', '56.00', 'area/1106/specialty/8e72d950-f86b-45b7-bcff-68525839b4ac.png',
        'area/1106/specialty/931db8d2-55eb-44de-97a3-af5bc60b8bc2.png',
        'area/1106/specialty/9eb98edb-7fe1-4c6f-a3d6-ccc2688d7ea9.png',
        '　井冈山烟笋是江西省吉安市井冈山市的特产。烟笋受人青睐，“美味”只是原因之一，“健康”则是原因之二： 烟笋富含的长纤维对人类的肠胃内壁具有极好的“清扫”功能，是消化道的优秀“清道夫”，长期时用烟笋 ，对减少肠胃癌症的发病率具有明显的作用。\r\n\r\n　　八百里井冈山，素有“革命摇篮”红色美誉，同时又是闻名中华的天然竹林氧吧。这里盛产竹笋，而烟笋则是其中最具代表性的竹味珍馐，被誉为“竹1参”。井冈山竹林广袤，竹笋丰富。新鲜竹笋有一股浓烈山土气味，尤其山里空气湿度大，保鲜期短，为了不让竹笋失其山里原生态味道，聪慧的井冈山人便将新鲜竹笋用烟熏火烤。鲜嫩爽脆的一棵棵大竹笋被采集到一起，将笋箨一层一层剥去后用清澈甘润的井冈山泉水煮软，再用木炭文火焙烤至干，熏制成黑褐色的笋干。\r\n\r\n　　烟熏火烤后的笋干看起来黑不溜秋，吃起来则更香更鲜，这样做不仅仅是为了便于储存和运输，更主要是因为经过这些工序加工而成的笋干具备了一种独有的大山原生态灵韵，令人一尝就能味觉出深山竹海里的苍翠风味。',
        '456', '1');
INSERT INTO `specialty`
VALUES ('40', '1107', '2', '南丰蜜桔', '13.00', 'area/1107/specialty/332bf950-d9ac-47f1-bbf3-739aa1011497.png',
        'area/1107/specialty/1d09def2-d18a-4ef5-a4b4-5bac8a6edcfd.png',
        'area/1107/specialty/faf045ae-872b-4c8d-a337-0668b7bb65b9.png',
        '南丰蜜桔为我国古老柑桔的优良品种之一，是江西省的名贵特产之一。历史上就以果色金黄、皮薄肉嫩、食不存渣、风味浓甜、芳香扑鼻而闻名中外。据古籍《禹贡》记载，早在两千多年以前，南丰一带所产的柑桔，就已列为“贡品”。唐宋八大家之一，南丰籍的曾巩，曾写诗赞美家乡的柑桔：“鲜明百数见秋实，错缀众叶倾霜柯。翠羽流苏出天仗，黄金戏球相荡摩。入苞岂数桔柚贱，宅鼎始足盐梅和。江湖苦遭俗眼慢，禁御尚觉凡木多。谁能出口献天子，一致大树凌沧波。”由此可见，当时蜜桔已能献给天子，故南丰蜜桔又有“贡桔”的美誉。在中外友好交往中，曾被斯大林同志誉为“桔中之王”。\r\n\r\n　　200多年前，南丰蜜桔就已开始专业化生产了。清朝鲁琪光在《南丰风俗物产志》记载：“蜜桔，四方知名。秋末，篱落丹碧累累。闽广所产逊其甘芳。近城水南杨梅村人，不事农功，专以为业。”随着社会的发展，蜜桔生产现已实现科学育种栽培。南丰蜜桔食用价值和药用价值都很高，全身都是宝。鲜果营养丰富，在100毫升果汁中含糖11.3克，柠檬酸约1克，维生素C19.5毫克，溶性固性物14%(主要是糖、氨基酸、磷、铁、钙等元素)。桔皮有理气健脾、祛湿化痰的作用；桔络有通络舒筋、顺气活血的功效；桔核能理气、散结、止痛。南丰蜜桔还可以作为加工业的原料，如用桔皮提炼的橙皮甙能制成防治动脉粥样硬化、心肌梗塞的药品；用果肉酿酒、酿醋，制作出蜜桔香槟酒、蜜桔可乐等，具有独特的香味。用桔皮桔花可提炼出天然果胶和香精，安全、广泛地应用于食品工业和医药工业。\r\n\r\n　　南丰蜜桔源出乳桔。南丰县四周环山，地处洪州(今南昌)以南，冬季比洪州暖，夏季比洪州凉，适宜乳桔生长。唐代开元以前，距今已有1300多年，乳桔落户南丰，由于获得优异的生态条件，很快就成为农家主要果树栽培品种。经过长期繁育，虽出自乳桔，却具有新的性状，味更甜美，高糖低酸，香气浓郁，在桔中独具特色。人们以其味似蜜便称之为“蜜桔”，销往外地冠以产地名称称之为“南丰蜜桔”。唐宋以来，南丰蜜桔均被历代朝廷列为贡品，故有“贡桔”之称。清末民初是南丰蜜桔生产发展的鼎盛时期，最高年产曾达12万担之多。到民国时期，因战乱和灾害的影响，生产由盛趋衰，桔树被大量砍伐，桔园荒芜，产量急剧下降，一般年产只有1—2万担。到建国前夕，南丰蜜桔面积不到3000亩，年产蜜桔17789担。南丰蜜桔栽培地区集中在城区及县城附近的瑶浦、水南、杨梅、茅店等乡村，离城5公里外则少有栽培。建国后，党和人民政府十分重视发展蜜桔生产，采取了一系列行之有效的措施，从政策上、技术上、经济上、管理上帮助和扶持桔农恢复和发展蜜桔生产。',
        '313', '1');
INSERT INTO `specialty`
VALUES ('41', '1107', '2', '临川藕丝', '7.00', 'area/1107/specialty/fafce967-956c-4ba5-be8a-d4b3820d9ac8.png',
        'area/1107/specialty/b681584d-9a65-4e5e-987a-a83759f4782f.png',
        'area/1107/specialty/5bbbddf7-6b7a-4f59-a641-02e230c0e67a.png',
        '蒋经国任江西保安司令部督练处处长时，在临川温泉训练新兵。蒋经国初到温泉饭馆吃饭，当地为他上一盘银鱼藕丝。他望着色泽银灰、成粘糊状的藕丝问店老板：“老表，这是什么菜？”“这就是我们临川有名的银鱼藕丝”，老板自豪地答道。蒋经国平日只见藕块藕片，从没见过这等细如发丝、制作精细的藕丝，他夹起一尝，藕质粘滑，味美可口，果真不同凡响。因祖传绝活不以外传，店老板告诉蒋经国：“这就是市上的藕，只是刀工特别一些。”从此，蒋经国经常命自己厨师专做，但总觉得饭馆里的好吃。\r\n\r\n　　 一天，蒋经国心血来潮，又想吃藕丝，命侍从召店老板到督练处为他炒藕丝，并要自己厨师留意绝活。店老板得传后，是又喜又愁，喜的是当今“太子”召自己做菜，不仅脸上风光，店名响亮，利润也会更大；愁的是做这道菜原料讲究，刀工精细，配料种类繁多，手艺人的看家本事上绝不外传的，否则砸了自家的招牌。但不做或做的味道不纯，惹蒋太子生气，那可吃不了兜着走。店老板思量片刻，计上心来，他将生姜、葱白、白醋等调料剁成末，又将店里存有的鸡汤和鸽子汤合二为一，拿来一条新毛巾，将各种调粉末撒在毛巾上，再卷成条状，放在汤里浸透，装在自己平日喝茶的大茶缸里，带着配料，坐上蒋经国派来的专车进了新兵督练处。蒋经国和店老板见面次数多也就很熟了，相互寒喧后就进了厨房，蒋经国的厨师也跟进了厨房。店老板不动声色，先将菜刀磨得飞快，精选几段塘藕，将藕段切成头发丝样嫩细，接着切好肉丝泡好银鱼，将肉丝、银鱼炒得半熟，倒入藕丝边擂边拨边放油，趁对方没注意，店老板拧出毛巾里的汤汁，配上佐料，一会，一盘香喷喷的银鱼炒藕丝端上来，蒋经国品尝后仍赞不绝口，因厨师没看出门道，一做味道又不纯正。打这以后，蒋经国凡要吃炒藕丝就命店老板来，直至出任赣州专员。据说，蒋经国离开温泉后，再没有机会吃上纯正的临川银鱼藕丝，常叹：“药不过樟树不灵，藕丝离开临川不香”。',
        '456', '1');
INSERT INTO `specialty`
VALUES ('42', '1107', '2', '全桔宴', '34.00', 'area/1107/specialty/a427e63e-c46f-44d0-8d5d-f3c071d4f246.png',
        'area/1107/specialty/0aa7eb20-308b-40b3-9d70-28c54d4be000.png',
        'area/1107/specialty/f049f007-d303-4938-9743-ceeed1508f3b.png',
        '　南丰蜜桔一身皆是宝，具有润肺、止咳、化痰、健脾、顺气、止渴的功效效，可供作为菜品的部位较多。在地方历久不衰的餐饮文化中，经过不断地研制、改进，形成了独具特色的南丰“全桔宴”。\r\n\r\n　　品尝地址：观必上乐园景区游客服务中心餐饮部\r\n\r\n　　自驾游客路线：济广高速公路南丰出口——环城南路——昌厦公路（南丰方向）——傩乡大道西延线——观必上乐园\r\n\r\n　　动车游客路线：乘5路公交车抵达县城，换乘2路公交车到达\r\n\r\n　　联系电话：0794—7965999\r\n\r\n　　桔韵三彩\r\n\r\n　　主料：桔瓣\r\n\r\n　　辅料：黄瓜、火腿肠\r\n\r\n　　做法：火腿肠、黄瓜切块，加入桔瓣清油小炒\r\n\r\n　　功效：提高免疫力，美容养颜，降低血糖\r\n\r\n　　桔子三元炖乳鸽\r\n\r\n　　主料：乳鸽、桔瓣\r\n\r\n　　辅料：龙眼、红枣、枸杞\r\n\r\n　　做法：小火清蒸三小时以上\r\n\r\n　　功效：益气健脾、补血健胃、醒脑养神、去火御寒\r\n\r\n　　桔乡鱼丝\r\n\r\n　　主料：草鱼肉丝\r\n\r\n　　辅料：桔皮、豆芽、青椒丝、红椒丝、榨菜丝、肉丝\r\n\r\n　　做法：各种原料切丝，清油小炒\r\n\r\n　　功效：理气化痰、健胃除湿\r\n\r\n　　全桔宴\r\n\r\n　　桔汁红松肉排\r\n\r\n　　主料：精瘦猪肉、桔汁\r\n\r\n　　做法：猪肉切块，包裹面粉大火油炸、桔汁勾芡\r\n\r\n　　功效：开胃果脯，桔香四溢\r\n\r\n　　拔丝桔瓣\r\n\r\n　　主料：桔瓣\r\n\r\n　　辅料：白糖、面粉\r\n\r\n　　做法：白糖与面粉按比例调和，包裹桔瓣拔丝\r\n\r\n　　功效：清火润肺、补肾安神\r\n\r\n　　凉拌桔皮\r\n\r\n　　主料：桔皮\r\n\r\n　　辅料：大蒜、麻油、陈醋、青椒\r\n\r\n　　做法：桔皮抄水，加入麻油、陈醋、大蒜等辅料凉拌\r\n\r\n　　功效：清热解毒、化痰止咳\r\n\r\n　　金丝肉丸\r\n\r\n　　主料：猪肉\r\n\r\n　　辅料：咸蛋黄、桔皮\r\n\r\n　　做法：将猪肉碾碎包裹咸蛋黄，撒上紫菜、桔皮大火清蒸\r\n\r\n　　功效：开胃健脾\r\n\r\n　　蜜桔披萨\r\n\r\n　　主料：南丰蜜桔瓣、精品面粉、鸡蛋\r\n\r\n　　辅料：优质火腿肠、精瘦猪肉\r\n\r\n　　做法：面粉与鸡蛋调和制成披萨，撒上火腿肠、桔瓣烤制\r\n\r\n　　功效：健脾养胃\r\n\r\n　　桔乡菊花鱼\r\n\r\n　　主料：草鱼肉、桔汁\r\n\r\n　　辅料：鸡蛋清、生粉\r\n\r\n　　做法：上好刀工切出鱼肉，过油，糖醋桔汁勾芡\r\n\r\n　　功效：益气健脾、补血健胃\r\n\r\n　　金丝手撕鸭\r\n\r\n　　主料：湖鸭\r\n\r\n　　辅料：橙皮、尖椒\r\n\r\n　　做法：湖鸭沸水煮熟，过油，加入辅料爆炒\r\n\r\n　　功效：干香四溢、健脾养胃\r\n\r\n　　冰糖蜜桔炖雪梨\r\n\r\n　　主料：雪梨、蜜桔\r\n\r\n　　辅料：冰糖\r\n\r\n　　做法：大火清蒸\r\n\r\n　　功效：清热解毒、养心消暑\r\n\r\n　　全桔宴\r\n\r\n　　吉祥如意\r\n\r\n　　主料：蜜桔、猕猴桃、龙眼、葡萄、圣女果\r\n\r\n　　做法：原果切片、摆盘\r\n\r\n　　功效：丰富维生素、开胃畅心',
        '304', '1');
INSERT INTO `specialty`
VALUES ('43', '1108', '2', '上清豆腐', '4.00', 'area/1108/specialty/b935f998-6f45-45cf-9954-236cf2896c38.png',
        'area/1108/specialty/027088da-b49a-47e4-b22b-1170bfaa41ac.png',
        'area/1108/specialty/9303087e-17ee-41fc-840e-0d94b0bd2e3a.png',
        '鹰潭上清豆腐历史悠久，为上清古镇的一大特色菜肴。这里的豆腐行销四方，在龙虎山中可称一绝。无论是豆腐还是豆腐干，都独具风味。不光营养丰富，还具有多种医疗效用。\r\n\r\n　　　制作豆腐在上清有着悠久的历史，至今镇上仍是作坊林立。这里的豆腐因水质好，加上传统的手工工艺十分地道，过滤精细，含水适度，具有白、嫩、香、滑的特点，无论是煎、炸、煮、炖、焖、凉拦，都清香鲜美，柔滑润喉，如果佐以黄鱼角、鲜猪肉、香菇、豆豉、香葱或辣椒，都是各有特色，风味十足。而做成油豆腐、霉豆腐、豆腐干、豆腐皮等，也是十分爱欢迎的菜肴和小吃。\r\n\r\n　　 民间传说\r\n\r\n　　　　“黄袍拜君王”好吃，关键是上清豆腐细嫩香滑、沁人心脾。据说在上清镇建镇前的西晋永嘉年间，当地有上清古镇\r\n\r\n　　一户农家，小两口勤俭持家且感情甚笃。丈夫早晨下地前，妻子常将黄豆腐磨成豆浆放些盐给丈夫充饥。有一次妻子要回娘家，就多磨了一些，将剩余的装入一个坛子里盖上以备丈夫第二天吃。可丈夫第二天下地时却忘了吃。妻子回家时，见坛了里的豆浆结成了块，硬要丈夫尝尝。丈夫为说：“你这不是在逗我吗？”没办法只好用勺子舀出一块尝尝，觉得味道很好，接着一口气吃完了，赞叹道：“太好吃了！”妻子打趣地说，“不是我逗你，你有这口福吗？”丈夫一高兴说：“那就管这结冻的‘豆浆’叫‘逗夫’吧！”后来夫妻俩便天天做起了“逗夫”当菜吃。妻子把“逗夫”的做法传授给邻里，经过不断改进，便有了今天的上清豆腐。',
        '328', '1');
INSERT INTO `specialty`
VALUES ('44', '1108', '2', '板栗烧土鸡', '34.00', 'area/1108/specialty/660070e2-73e6-47e9-a935-4e3da0d4aaba.png',
        'area/1108/specialty/7879bf8c-0f99-41fd-bb98-4dff28e2da0b.png',
        'area/1108/specialty/6267d9a4-007a-429f-82ff-cbd5b14f3499.png',
        '龙虎山出产的天师板栗，个大香甜，淀粉丰富，是历代天师特别喜爱的果品和滋补品，素有“人间仙果”之称。而这钵天师板栗与当地农家喂养的土鸡相配，在文火中慢慢地烧出来的“天师板栗烧土鸡”，油光发亮，清香思溢，是这里很有名的美味佳肴，能吃这道菜，可谓“国 宾”的待遇啦。这道菜的形成还有一个很有趣的故事。说是有一次天师在家宴请宾客，家厨用一般的烹调方式制作，当厨师配好料，将鸡块装入砂钵放炉灶上烧制时，天师的儿子乘厨师不注意，调皮地将正吃的去了壳的板栗放入砂钵内。烧熟后，整钵端上了桌，拿掉钵盖后，一股清香扑鼻，只见鸡块色泽淡黄，栗香酥烂，客人一品尝赞不绝口，天师也欣然得意。席后，命家厨进一步改进，使之成为安宴中必备的菜。而且，这道菜可以滋阴补阳，健脾益肾，具有很高的营养价值。',
        '455', '1');
INSERT INTO `specialty`
VALUES ('45', '1108', '2', '牛肉丸子汤', '23.00', 'area/1108/specialty/ce085e4c-bfb7-474c-870f-fb4027fec1a2.png',
        'area/1108/specialty/e79a66bd-cfc7-4d0c-aaf8-9fe3404299bf.png',
        'area/1108/specialty/69b8ef33-e676-4b5d-b7a2-eff0822efc4c.png',
        '　　配料：牛腿肉750克，鸡蛋150克，葱头400克，胡萝卜300克，土豆500克，豌豆50克，鸡油400克，精盐15克，牛肉清汤2。5公斤，香叶半片，干辣椒1个。胡椒粉、味精各适量。\r\n\r\n\r\n\r\n制作方法：1．将牛肉洗净，切成块，100克葱头去皮，和牛肉入在一起用绞刀绞两遍，再用刀剁一遍，放人盆内，加入5克精盐，50克鸡油、胡椒粉、味精、鸡蛋搅拌均匀，加水150毫升，随倒随搅，和成肉泥。 2．向汤锅内放水烧开，用于把肉馅挤成i20个丸于，放入开水锅内，烧一个开，丸子漂起，把原汤滗出，将丸子放入冷水里洗去浮沫，调入漏勺内，控净水。 3．把胡萝F、葱头去皮，洗净，切成小方丁，将土豆去皮，洗净，切成 40块。 4.往锅内放入200克鸡油，烧至五成热。先把胡萝卜丁、葱头丁放入，随之放入香叶，胡椒粉、干辣椒，用半炒半炯的方法，将蔬菜炯到成熟，放入牛肉汤、味精、精盐，调好口味，把土豆放入场锅煮到九成熟把丸于放入，烧两个开，改微火稍煮，起锅装盆。 5．食用时，分10客，每份放豌豆5克，牛肉丸子12个和汤，即可。',
        '344', '1');
INSERT INTO `specialty`
VALUES ('46', '1109', '2', '广昌白莲饮料', '13.00', 'area/1109/specialty/69ada833-0bf1-424d-ba10-7350e5b82390.png',
        'area/1109/specialty/05266eea-1d11-4414-8181-510f92f22e2d.png',
        'area/1109/specialty/79f6a9ca-ebef-47f4-954b-fe2b36401af6.png',
        '莲子汁是以白莲为主要原料加工饮料，利用高科技食品工技术精制而成，该 产品保持了广昌白莲特有的清香和功能，具有口感细腻、味道醇正、冰凉爽口、温热清香等特 点，不加任何化学防腐剂，在同类产品中口感和色彩属上乘，是现代理想的天然饮品，曾获第 二届中国国际食品博览会国际名牌饮品，第二央中国农业博览银奖，&127;\'94中国国际饮料及 饮料加工设备展览会金奖。\r\n产品主要在本县、本区、本省及附近地区销售，省外市场主要在武 汉、上海、广州、深圳等城市。今年也开始进军北京、天津等北方市场。去年生产白莲饮料 300吨，产值270万元，实现利税25万元。今年计划生产白莲饮料600吨，销售收入480万元，税 利35万元。', '345', '1');
INSERT INTO `specialty` VALUES ('47', '1109', '2', '手撕狗肉', '150.00', 'area/1109/specialty/bc907f9b-5fb5-484c-b889-8c72e7e61886.png', 'area/1109/specialty/07fd858d-a170-4f6a-bcd2-28423fc51140.png', 'area/1109/specialty/d1b9a3e1-d749-4402-8895-a672a82ba1f2.png', '上栗手撕狗肉是江西省萍乡市上栗县的特产。上栗的手撕狗肉很有名气，把大块狗肉剔骨手撕后，和红薯粉一起煮着，味道很辣。既有南方狗肉的鲜辣，又有北方吃肉的那种大快朵颐。\r\n　　寒冬，正是吃狗肉的好时节。狗肉，味道醇厚，芳香四溢，所以有的地方叫香肉，它与羊肉都是冬令进补的佳品。狗肉营养价值很高，而且含有钾、钙、磷、钠及多种维生素和氨基酸，是理想的营养食品。　　\r\n　　上栗的手撕狗肉实属一绝，汤浓味美，肉质看上去纤维较粗实则鲜嫩易嚼。与其他地方的狗肉相比，上栗的手撕狗肉独具风格，源于做法的精细和选料的讲究。　　\r\n　　在狗肉的烹制上，先将狗肉处理后小火炆，炆好的狗肉是不主张用铁器的刀子来裁切的，就徒手向着骨头上来撕，因此名之为“手撕狗肉”。\r\n　　这地方炆好的狗肉是不主张用铁器的刀子来裁切的，就徒手向着骨头上来撕，这还有一个说法叫做“手撕狗肉”。', '-1', '1');
INSERT INTO `specialty` VALUES ('48', '1109', '2', '辣炒田螺', '34.00', 'area/1109/specialty/1ae00c18-7720-46ae-bb61-d0786889afbd.png', 'area/1109/specialty/faf2f086-5765-4a88-9751-a4c8d0aec844.png', 'area/1109/specialty/a4e12407-9dd5-4014-ac62-173ea55b2eb0.png', '田螺1000克，干辣椒150克，花椒5克，豆瓣50克，葱花5克调料：姜米20克，蒜米20克，色拉油250克，酱油10克，料酒50克，味精、盐、白糖适量。\r\n  1. 田螺(提前吐尽泥沙}洗净，剪去尾巴。\r\n\r\n2. 准备好调料。\r\n\r\n3. 炒锅倒油烧热(油要稍微多一些。\r\n\r\n4. 爆香葱姜蒜和辣椒。\r\n\r\n5. 倒入田螺翻炒均匀。\r\n\r\n6. 加入白酒炒香。\r\n\r\n7. 在加入糯米酒继续炒香。\r\n\r\n8. 加入老抽。\r\n\r\n9. 加入酱油。\r\n\r\n10. 加入糖翻炒均匀。\r\n\r\n11. ·加入蚝油继续翻炒，这时可多翻炒片刻。\r\n\r\n12. 然后加入盐翻炒。\r\n\r\n13. 加入鸡精。\r\n\r\n14. 翻炒均匀关火。', '561', '1');
INSERT INTO `specialty` VALUES ('49', '1110', '2', '齐云山南酸枣糕', '34.00', 'area/1110/specialty/7aaa793d-c805-434c-8124-eb64f02a667a.png', 'area/1110/specialty/16115652-2550-4579-878d-23149cb4a76c.png', 'area/1110/specialty/38125d3f-86fd-4055-971d-608a7b30a3d6.png', '齐云山南酸枣糕来自唯一国家命名的“中国南酸枣之乡”，原料采自原始森林的百年南酸枣树所结野生南酸枣果，结合传统古朴的加工技术，充分保留了天然野生的风味和原始森林的清香，入口由酸而甜，纯滑柔韧，风味独特，野趣盎然。\r\n齐云山南酸枣糕含鲜果固有的丰富的黄酮，果胶，维生素，钙，铁，锌等成份。一九九七年，二000年，二00三年，二00六年连续四期被中国绿色食品发展中心认证为“绿色食品”，是国内同类产品率先获此殊荣的食品。\r\n含丰富植物黄酮，更健康！目前来自绿色山野的齐云山南酸枣系列食品已经成为都市非常流行的一种绿色健康食品。\r\n无论是快乐成长的孩子们，还是爱美爱健康的女性朋友们、紧张忙碌的上班族、修身养性的中老年人，美好生活都离不开齐云山南酸枣系列食品。时时不忘齐云山南酸枣系列食品，为您的生活增加滋味，让您的生活够滋够味！', '443', '1');
INSERT INTO `specialty` VALUES ('50', '1110', '2', '麻辣鸭头', '60.00', 'area/1110/specialty/8f695646-d1cd-4827-b869-1b2e0de72df0.png', 'area/1110/specialty/2935f92d-4241-437e-9ab5-a6355623f13e.png', 'area/1110/specialty/a3d44e9e-5cf4-4b07-a35d-90464425d127.png', '\"麻辣鸭头\"之所以好吃，秘绝全在汤料里，汤料\r\n只所以巨香，根本是因为上等的香料。\"麻辣鸭头\"真正使用的香料应该是25种，自家做不太可能放齐，但味道已经相当不错，胜在健康无添加剂。\"麻辣鸭头\"，不但口感鲜美，回味无穷，还具有益气补虚、降血脂以及养颜美容等功效。我国传统中医认为:鸭属凉性，经常食之，平肝去火。味甘，功能温补，益气，配以辣、麻及几十余味中药材，使其主相辅相成。其辣，功能排毒瘦身。健体美颜;其麻，开胃益食，与辣互相作用，具益气养血之效，性温而不躁，除湿去烦，开胃健脾。', '455', '1');
INSERT INTO `specialty` VALUES ('51', '1110', '2', '白灼芥兰', '23.00', 'area/1110/specialty/e8e38973-c30e-4eb9-843b-d61bebc5396f.png', 'area/1110/specialty/ec9fcf6e-4e9d-4090-8162-3fba197b2f24.png', 'area/1110/specialty/36938ae7-2a51-46de-b911-5842704a938d.png', '\"白灼\"是粤菜的烹调技法，以煮滚的水或汤将生食烫熟，许多人觉得这是最健康的烹调方式。不过，有人在白灼时也用了大量的油和盐，不但增加了身体负担，还会更多损失营养。\r\n\r\n朝阳医院营养科营养师宋新表示，很多维生素都是水溶性的，同时易受高温破坏，焯太久会破坏营养，所以白灼未必最好。而用很少的油快炒要比白灼好一些，可缩短加热时间，减少营养流失。\r\n\r\n专家表示，不管哪种烹饪方式，关键是控制油、盐等调味料的分量。现在很多餐馆追求菜的色、香、味俱全，就算是白灼，上桌前也要淋一勺油，让菜\"好看\"，却牺牲了消费者的健康。在家做饭时，为避免过量摄取脂肪，可用少量橄榄油、花生油等来\"灼\"。\r\n\r\n蔬菜类食物最好凉拌，但不是任何食物都适合凉拌，比如马齿苋等野菜还是要焯一下，才能彻底去除尘土和小虫;而豆角、蘑菇等不适合凉拌，容易引起食物中毒，即使要拌也一定要煮熟。', '441', '1');
INSERT INTO `specialty` VALUES ('52', '1111', '2', '乐平狗肉', '78.00', 'area/1111/specialty/73db3af1-2a00-4d44-87de-9a868e40efb9.png', 'area/1111/specialty/d1b346c3-6b0d-4689-b5f8-04f45302d421.png', 'area/1111/specialty/1b66afce-897a-4e3a-9bfb-ae6a571b1ecc.png', '　乐平狗肉这一传统食品已有2000多年的历史，特别是乐平的“清蒸狗肉”是最具有乐平特色的传统做法，历经数百载而不衰，乐平狗肉的制作工艺匠心独运，望之黄莹油亮，闻之香气扑鼻，食之回味无穷。\r\n　　乐平狗肉是用蒸、煮相结合的方法烹制作冷盘食用的风味食品。因系用文火不加盐煮熟，具有肉味鲜嫩，软硬适宜，鲜美爽口，营养丰富等特点。食用时调味作料讲究，独具一格。 　　\r\n\r\n　　乐平狗肉清煮白切。煮时放入大量的茴香、八角、大蒜、生姜，注入谷酒，水开后还要放入些许萝卜，红枣。 　　\r\n　　一般要用文火煮焖一夜。天光微量，捞出狗肉，放在竹匾上摊凉。然后一副担子，前狗肉、谷酒，后小桌板凳佐料，走街串坊，到村去镇。小贩特别喜欢到煤矿边，拉长了嗓子吆喝道：“刚出锅———狗肉”。见有生意便论斤称两出售。一般先称后切，放在盘里，洒上红辣椒粉，也有要加上蒜泥的，当然这是粗浅的吃法。随后摆在小矮桌上，一口谷酒，一口狗肉，说上一通不着边际的话题。卖狗肉的只在有空时，不是听上两句，就是插上两句，他生怕怠慢了顾客。一般矿工出井，半斤狗肉半斤酒，活血壮阳，正得其时。 　　\r\n\r\n　　乐平狗肉皮糯、肉香、骨酥。没有皮的狗肉，烧煮出的肉不凝；芸芸众肉，猪肉肥、牛肉腥、羊肉膻，唯独被狗肉占去“香”；只有骨酥之时，狗肉与骨可脱，因此乐平的狗肉是带皮去骨。江西乐平狗肉与四川花江狗肉烹制不同：花江狗肉是放在火锅上涮，蘸着佐料吃；也与延边朝鲜族狗肉吃法不同：延边的狗肉肥腴，一般与冻豆腐煮着吃。而乐平狗肉清煮白切，尽得狗肉之本色原味。尽管做此生意的都是小本买卖，一副货郎担即可，但乐平的狗肉生意一直不错。食者往往是图狗肉香糯，也图谷酒醇厚。乐平狗肉一年之中只在深秋到翌年清明上货供应，这正体现中国人朴实的饮食天人合一思想。 　　\r\n　　雅自有雅的高贵，但俗亦有俗的可爱，吃乐平狗肉无疑是市井风俗。 　　\r\n\r\n　　其实狗在古时候便排入六畜之列，吃狗肉定是地方风俗。其中似乎粗俗武人为多：汉刘邦帐下的樊哙在当时就是一位不错的杀狗师傅；《水浒传》中的花和尚鲁智深是在吃了一条狗腿、大量谷酒后才一膀子掀倒了半山亭。也似乎是吃狗肉才长力气，前面樊哙、鲁智深是例，矿工喜好狗肉也是信此如此。矿工累了一班，酒足肉饱醺醺然乐陶陶地搭着衣服，回家了。', '566', '1');
INSERT INTO `specialty` VALUES ('53', '1111', '2', '凉伴冷粉', '12.00', 'area/1111/specialty/618ee499-b68b-488a-8b29-3a0f4fcd825b.png', 'area/1111/specialty/8cb5f528-791a-4a36-89f0-d56757b9f49b.png', 'area/1111/specialty/955cef1f-043f-45a3-82bb-eeb0c2649aa8.png', '　1、选料：无霉变的普通大米均可，也可用碎米作原料。\r\n\r\n2、浸泡：把大米洗净除去杂物，用水浸泡4-5小时。同时另取洁白、纯净的优质石灰，加水溶化成饱和石灰水，让其自然澄清，取其上清液装好备用。\r\n\r\n\r\n3、磨浆：大米浸泡后，连水带米在石磨（钢磨或粉碎机）上磨成米浆，盛入锅中。\r\n\r\n\r\n4、熬煮：用旺火将锅中米浆熬沸，然后以小火熬煎，并用一木棒在锅中不断搅动。这时把事先准备好的石灰水缓缓注进锅中，使米浆凝固。石灰水的用量，可根据对凉粉老嫩程度的不同要求略有差别，以做到无苦涩味为宜。一般1kg大米用100-200g饱和石灰水。加过石灰水的米浆还须熬一下（不断搅动，以防糊锅），当用锅铲挑起一点米浆，其粘度恰好处在似滴非滴状态，并在铲上呈一处状，表示火候已到，应及时舀出。盛在瓷盆中，让其自然凉透后，翻倒在有清洁湿布的案板上即可。\r\n\r\n\r\n\r\n5、取食：拌以酱油、醋、蒜泥、味精、油辣子等不同佐料，即成各式清凉、爽滑的凉粉。若拌以豆豉酱，则可制成四川凉粉之正宗——川北凉粉。\r\n\r\n\r\n\r\n承德一带居民食用凉粉已有300多年的历史。人们所以喜欢食用凉粉，不仅因它味美消暑，还因为它含有丰富的营养。\r\n\r\n\r\n\r\n现在市面上都有做成形的凉粉了，一般家庭只需要买回家切好拌上佐料即可，当然，你也可以上四川街上的小店品尝拌好的凉粉，风味独特：）\r\n\r\n\r\n洛带镇中广东会馆的伤心凉粉广为盛名，以辣为盛。深受游客喜爱。', '444', '1');
INSERT INTO `specialty` VALUES ('54', '1111', '2', '历居山杨梅', '10.00', 'area/1111/specialty/e940c2fb-62c3-4741-9b1c-bdddeaaeda02.png', 'area/1111/specialty/34a3be12-e58e-495b-8853-1260f9eca78a.png', 'area/1111/specialty/32bb795d-402b-423c-8d0c-a64a3ee6cd04.png', '历居山杨梅生长在远离城市的洪岩镇历居山山区。当地栽种杨梅树完全处于野生状态下生长，由于杨梅在整个生长过程中不需要使用化肥和农药，本身就具有抗病虫害的能力。因此，历居山庄杨梅现已被国家绿色食品委员会认证为绿色食品。\r\n　历居山杨梅宋代已有栽培。明嘉靖《乐平县志》记述，杨梅产历居山，其味绝诸果，故而得名。当地杨梅以黑炭和东魁两个品种为主，现在已形成了以“万紫”、“??峰”等杨梅品牌。\r\n\r\n　　 历居山杨梅其味鲜甘酸，风味独特，核小肉多，营养丰富，被誉为果中珍品。历居山杨梅果肉质细软、汁液多、叶甜微酸、略有香气，富含纤维素和矿质元素，据测定，钙1.2%、铁0.6%、热量129.58千焦耳、蛋白质0.7%、脂肪0.3%及硒、果胶等8种对人体有益的氨基酸和物质。\r\n\r\n　　 杨梅含有大量杨梅素及黄酮，具有很强的防氧化活性、抗炎和抗皮肤癌作用，对金色的葡萄球菌、大肠杆菌等有强力的杀灭作用。本草纲目记载“杨梅可止渴和五脏，能涤肠胃，除烦愤恶气”。据民间验证，对下痢不止、头风作痛、腹痛、呕吐、食物中毒、必腹绞痛、口干舌燥、低热烦渴、牙龈出血等用杨梅及杨梅树皮作单方服用，均有良好疗效。', '535', '1');

-- ----------------------------
-- Table structure for travelcomment
-- ----------------------------
DROP TABLE IF EXISTS `travelcomment`;
CREATE TABLE `travelcomment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `usernum` varchar(255) DEFAULT NULL,
  `travelNum` int(11) DEFAULT NULL,
  `content` text,
  `ptime` datetime DEFAULT NULL,
  `stage` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `travelNum` (`travelNum`) USING BTREE,
  KEY `usernum` (`usernum`),
  CONSTRAINT `travelNum` FOREIGN KEY (`travelNum`) REFERENCES `travels` (`id`),
  CONSTRAINT `uid` FOREIGN KEY (`usernum`) REFERENCES `user` (`userId`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of travelcomment
-- ----------------------------
