INSERT INTO `travelcomment` VALUES ('20', '17516019380', '29', '好美的地方！！', '2018-05-04 21:31:11', '1');

-- ----------------------------
-- Table structure for travels
-- ----------------------------
DROP TABLE IF EXISTS `travels`;
CREATE TABLE `travels` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `userNum` varchar(255) NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  `ptime` datetime DEFAULT NULL,
  `describle1` text,
  `pic1` varchar(255) DEFAULT NULL,
  `describle2` text,
  `pic2` varchar(255) DEFAULT NULL,
  `describle3` text,
  `pic3` varchar(255) DEFAULT NULL,
  `readnum` int(11) DEFAULT NULL,
  `stage` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `userId` (`userNum`),
  CONSTRAINT `userid` FOREIGN KEY (`userNum`) REFERENCES `user` (`userId`)
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of travels
-- ----------------------------
INSERT INTO `travels` VALUES ('19', '11', '韶光正好——我与庐山有个美妙的大约会', '2018-04-16 20:29:21', '庐山位于中国中部江西省九江市南，北濒长江，东接鄱阳湖，面积302平方公里，全山共有90多座山峰，最高峰大汉阳峰，海拔1473.4米。群峰间散布有许多壑谷、岩洞、瀑布、溪涧，地形地貌复杂。 “春如梦、夏如滴、秋如醉、冬如玉”，庐山的四季美如斯，而其中尤以凉爽的夏季最为人们青睐，是享誉海内外的避暑胜地。庐山无数次出现在文人墨客的诗词歌赋中，也成为我魂牵梦绕的必去风景区之一。', 'travel\\76c0bc30-ef31-41b4-ba0e-1584e5107a94.jpg', '庐山的美景很多，可分为三条游览线路。一线：花径、锦绣谷、仙人洞、大天池、龙首崖、黄龙潭、乌龙潭、三宝树、芦林湖、博物馆、庐山会址、美庐别墅；二线：含鄱口、植物园、五老峰、三叠泉；三线：白鹿洞书院、秀峰、东林寺。另有碧龙潭、汉阳峰、石门涧、铁船峰等其他景点。庐山的精华景点集中在一线和二线，所以两至三天能游览大部分的风景名胜，如有三至四天时间便可饱览全景。\r\n\r\n　　十二点左右到九江站下车，许多拉客的司机都自动地上来报价拉客，都大声地囔囔:10元一个上庐山咯。虽然司机大哥们都很热情但是出于安全考虑，我们还是选择了去正规的客运站坐车。于是我们就按照规划的路线打的到“三里街”，并在那里打车有车上庐山，去庐山的车费是15每个人。', 'travel\\08df18bf-5f2c-4c84-ae83-bacac57da947.jpg', '来之前就听闻庐山的山曲折蜿蜒，是出了名的山路十八弯，可是没想到这弯弯曲曲配上老司机急转弯的杀手锏简直是催吐神器。大概20分钟的车程，30个人左右的中层大巴车。在颠簸中，我们终于在庐山脚下停了下来，也就是卖上庐山门票的地方。上庐山的门票是学生票135人，买完票之后我们就开始上山路。刚刚上山的时候两边是郁郁葱葱的树木，一眼望过去都是秀丽的风光。司机热心地告诉我们如果碰巧遇到下雨天过雨，我们还能看见白茫茫的云海，那感觉就好像在空中腾云驾雾。随着海拔的升高，兴奋的情绪在一群九江方言的嘈杂中沉淀下来。虽然因为山路还有一点点头晕的感觉，但是内心却也充满了满满期待。在此建议容易晕车的朋友们，可以事先准备一点药物，少吃一点干粮。', 'travel\\c423db2f-3799-4048-b550-10d47f67312f.jpg', '17', '1');
INSERT INTO `travels` VALUES ('20', '12', '“秀丽三清，醉美玉山”三天两夜实用攻略', '2018-04-13 21:45:55', '行程天数：3天人均花费：756亲子游\r\n\r\n　　作为一个地道的上饶人，对于三清山的秀美也是赞不绝口，不得不说，三清山以她的灵动，清绝出尘震撼着一个又一个远道而来的游客。\r\n\r\n　　每次旅行，都会给体内灌输一些正能量，旅行的意义又是什么呢？我想每个人都有着自己独特的对旅行的理解，对于我来说，每一次旅行就像一次探险一样，你总会找到那些独特的宝藏。\r\n\r\n　　三清山位于中国江西省上饶市玉山县，是世界自然遗产，世界地质公园，国家自然遗产，国家地质公园，游三清山没有个3-4天不行，因为三清山的美值得你驻足欣赏。那么当然作为本地人，对于三清山又有着别样的情怀，为了让更多的人来钟灵毓秀的三清山，特写此三清山攻略，希望能够对您的旅行有所帮助。\r\n\r\n　　穿衣指南：春(3-5月)夏(6-8月)季节一般穿短袖、衬衫，薄外套就足够了，如果打算在山上过夜的朋友们，建议带上一件稍厚一点的外套以防感冒。秋冬季节一般衬衫，羊毛衫，厚外套。山上昼夜温差大，请注意防寒保暖。4-6月为三清山的雨季，记得带上雨具(山下一般有一次性雨衣，15元一件)，夏季注意防晒。\r\n\r\n　　语言：三清山当地语言以上饶玉山话为主，接近浙南吴语，和周边江山，常山，广丰等地人能交流。\r\n\r\n　　登山指南：我们是住在离金沙索道只有五分钟车程的卧龙国际酒店，所以我们是早上六点起床，去餐厅用完早餐，便上山了。如果腿力不好的好，可在酒店门口买一两根登山杖，是当地的小竹棍，10元一根，如果人多可以讲价，15元两根也是可以的，总之就是人越多价格越实惠。因为当时是九月初，所以我当时穿的是一件T恤，穿了一条牛仔裤，买了雨衣，也去酒店附近的小店买了水和小面包，山上的物价有点小贵，水是10元一瓶，山下就正常价，农夫山泉1.5元一瓶，盼盼小面包8元10个，如果人多可以多买点，分开背，可以省很多。记得一定要穿平底鞋或者运动鞋，山上很陡，真的陡。', 'travel\\4daef087-18f1-4d71-ac2d-0abbfe25ca42.jpg', '三清山景区位于上饶市玉山县境内，玉山县有两个火车站，分别为玉山南站(高铁站)以及玉山站(火车站)，外地的朋友们可以从当地火车站搜索到玉山南或玉山站的火车，根据自己的行程选择合适的车次。玉山南和玉山站出站广场都有直达玉山县城的公交，玉山南的为6号线(玉山南-玉山汽车站)，玉山站的为5号线(火车站-十七都)，需要去县城购物的朋友们可选择在步行街或者商业城下车，下车就是县城中央购物区，有玉山当地特色的食品，以及全国连锁的超市，家润福等，你可在此购买你上山游玩所需的物品，相比于山上的物价，便宜了好多哦。从玉山汽车站就有直达三清山的客车，有两个方向的车，分别为三清山南(外双溪索道)以及三清山东部(金沙索道)，票价为人均16元，20分钟一趟，夏季早上6:00便有车，冬季6:30左右有首班车。有一个小贴士，如果是容易晕车的朋友，建议搭乘三清山东部的车，因为这边的地势较平缓，盘山公路的转折不是特别急。如果喜欢刺激，那么欢迎乘坐三清山南部的车，心情绝对跌宕起伏。\r\n\r\n　　班车时间：三清山南部到玉山汽车站最早8：30，最晚17：00，间隔50分钟一趟(除中午间隔时间约2个小时)三清山东部到玉山汽车站最早8：30，最晚17：00，间隔50分钟一趟(除中午间隔时间约2个小时)玉山乘车地点：玉山新汽车站三清山乘车地点：三清山东部金沙索道旁', 'travel\\33d9ac8f-37bd-4c24-ab89-41cc58511da9.png', '三清山脚下有很多的酒店，喜来登酒店，花园酒店，卧龙国际酒店等，还有一些客栈，都是当地人开的，价格根据旅游实况会有些许调整，我们去的时候住的是卧龙国际酒店，相对来说价格比较合理，当然我们也是比较了各家酒店，然后综合考虑之下，才选择卧龙国际酒店。我们还是在携程上定的房，豪华标间是173元，双早，有线无线宽带免费。', 'travel\\83a51e92-a975-4823-a77a-2442d04cdb1c.jpg', '7', '1');
INSERT INTO `travels` VALUES ('21', '13', '红色之旅——邂逅井冈山', '2018-04-13 21:54:05', '在去井冈山之前，我对它最直白也是最根深蒂固的印象便是深入人心的“红色热土”、“革命摇篮”。而当我踏上这片土地，触及到这里一山一水的气息，才恍然发现，原来井冈山不只只是红色。这里有穿过峡谷山石的微风，亲吻澄澈的流水，青绿色的稻田，颤动的竿竿翠竹，这里的一瀑一泉都透着清亮，一切恍如诗意，让人不忍离去。井冈山——革命山——旅游山——文化山。“四面重峦障，五溪曲水萦。红根已深植，今日正繁荣。”是老一辈无产阶级革命家董必武1960年访问井冈山时对井冈山发展的评价。井冈山从红色中走来，又向绿色中走去。走进井冈山，你会发现，大自然就是最鬼斧神工的设计师。井冈山的每一条路，每一棵树，每一条河，都经过了细心雕琢。上了年岁的小河护栏，老木上的裂痕，一草一木都彰显出沧桑和年岁。身处其中，眯上眼，便可用心感受到自然之美，绿荫围绕，蓝天白云，微风拂面，鸟叫虫鸣。', 'travel\\d66f11e0-599d-407a-89cb-3583ab54475c.png', '到井冈山旅游，必须搞清楚的两个地方：\r\n\r\n　　一个是茨坪镇，景区的大本营所在，所有景区都离茨坪镇非常的近，所以一般都选择在这里落脚住宿。镇内有几个景点：北山烈士陵园、毛泽东旧居、革命博物馆，挹翠湖、天街都很值得一看。\r\n\r\n二便是井冈山景区，景区内有三条线，一日就可以游完。A线：黄洋界—百竹园和红军造币厂—龙潭瀑布—小井红军医院；B线路：大井井冈山村—水口彩虹瀑布；C线路：五指峰主峰景区—井冈湖(有快艇来往C线的主峰景区42元/人)。而D线路是笔架山专线，这个要专门用一日时间玩。实际上井冈山游览需要花费2天半的时间，如果赶时间的话也可以不去笔架山，便可以省一日时间。', 'travel\\de55eba3-e978-4757-aa6e-7c56b36ebdcc.png', '到达井冈山：1、坐飞机到井冈山机场，然后坐机场巴士到达吉安市，再至城南车站坐班车至井冈山茨坪。2、坐火车到井冈山，火车站在井冈山新城，站离茨坪36公里，可再转坐公交车或者出租车到茨坪。3、坐汽车到井冈山，从吉安坐汽车到井冈山需时两小时左右，从赣州到井冈山大约三到四小时，汽车的优势是可以直接到茨坪下车。景区内交通：4、在井冈山风景区，我们可以丝毫不用担心交通不便的问题。井冈山对核心景区道路实行交通管制其中主要包括：大小五井、黄洋界、龙潭、水口和主峰等核心景区，同时景区内设旅游观光车可到各个景点。观光的票价也十分平民，70元/张，总站下设黄洋界、龙潭、大井、百竹园、水口、主峰等13个分站。井冈山门票要在茨坪镇坐绿色的旅游公交去旅游总站买，旅游公交从总站开出环绕茨坪镇一周又回到总站。通往各个景区，都必须先到旅游总站发车。在游玩的过程中我们要注意下往返末班车的时间。一般一年分为两个大段：3月16日至11月15日总站发班时间为7:00，景点返回总站最后收班时间为18:30。11月16日至次年3月15日总站发班时间为7:30，景点返回总站最后收班时间为18:00。', 'travel\\121799e9-61fb-46e6-bb8e-8295b8088eec.png', '8', '1');
INSERT INTO `travels` VALUES ('22', '11', '新余两日游——自古新余美如画', '2018-04-13 21:57:37', '　题记\r\n\r\n　　休憩\r\n\r\n　　春游春回大地欣然，唤伙伴登舟荡漾欢。\r\n\r\n　　看碧波万顷，湖光潋滟；\r\n\r\n　　画屏抱石，仙女湖嫣。\r\n\r\n　　惬意春阳，天光水影，倒映群山红杜鹃。\r\n\r\n　　青啤醉，共班荆道故，宴乐齐天。\r\n\r\nDay 1作为北方人，对于江南美景一直充满着期待，这次借着毕业旅行的光，最终决定去新余看看。为了时间上的方便(准确的说是不想早起嘿嘿)，我们买了10:45的高铁票，七个多小时就从北京西到达了新余北，怀着激动的心情终于开始了旅途。据说融城大饭店是新余最好的酒店，从新余北到新余融城大饭店有直达的公交车K403，1元/人，大约三十分钟左右坐到车管所，下了车再走个五分钟就能到酒店门口，交通方便，位置很好找。', 'travel\\3e580262-139d-49a4-ae80-5fbe1cc785f8.png', '酒店整体来说装修比较豪华，顶灯的设计映入眼帘，很有韵味。在前台登记入住时，服务人员给我们倒花茶并询问我们需要的物品。之后有专门的人员领我们去乘电梯，并告知我们电梯内需要刷卡才能按楼层按键，这一点让我们感觉这里安全性非常高。酒店服务态度好，干净整洁，还送了两杯热鲜奶还有果盘，非常贴心。我们入住的是大床房(在美团上订购不到500元一晚)，房间内是中央空调，床睡上去很舒服，每张床配有两个枕头。房间里的设施也很齐全。有小冰箱，小冰箱里有啤酒，饮料，牛奶，酸奶等，品种很多。还有吹风机和电子秤，洗澡还有浴缸。可以说是一次完美的体验。', 'travel\\8932c5a0-3678-4d45-8be2-e19f2b72d7b8.png', '来之前在网上看到新余的鸭三件很有名，包括鸭头、鸭翅、还有鸭爪。于是下午打车去了总工会的大排档，只要七元左右。推荐正宗老陈鸭头，小龙虾和鸭三件都炒鸡好吃，小龙虾还都去了虾线。提到鸭三件，老陈鸭头就靠这道菜打响名气！听说无论什么时候去都是人气爆棚，菜以香辣为特色，虽然很辣，但总是让人停不下筷子，吃了还想吃。那\"鸭三件\"早就卤过一遍，再加上辣椒，生姜，大蒜子，五香，麻油，香油，在锅里煮十来分钟，一盘香嘭嘭的麻辣鸭头就可以上桌了。它吃起来麻麻的，酥酥的，鸭肉鲜美，酥软，特别是那鸭头，咬开脑壳，那脑髓特好吃。还有那鸭翅，鸭掌，特有一番嚼头。这时候，如果再喝上一杯冰啤，简直爽呆了!新余这道小吃菜，只有一个地方做得最最地道，好吃，那就是新余城北的总工会夜宵一条街，以至于好多人不得不炒这道菜带回家吃。', 'travel\\aef3ee23-db67-4983-af34-a36b64d72e42.png', '5', '1');
INSERT INTO `travels` VALUES ('23', '14', '赣州三天游——五一自驾游', '2018-04-13 22:23:17', '“不旅行的人生和咸鱼有什么区别”，我朋友和我这样说到。朋友说的也有道理，人生苦短，总不能一直在一个地方待着做井底之蛙吧，世界那么大，总要去看看。说走就走，刚好趁着这次五一三天假，我和朋友一行四人决定去江西的赣州瞧瞧。我们在郴州工作，因为距离赣州也不是很远，我们决定自驾去，自己开车也比较方便灵活，不需要拘泥于按部就班的旅游路线。\r\n\r\n　　车在做好加油准备之后，上午八点多从夏蓉高速郴州西收费站出发，郴州到赣州全程将近300公里。在夏蓉高速公路郴州汝城和赣州崇义路段，有接近四十条隧道，很多是两公里以上路程隧道，最长为4公里多长，在这种情况下，我们觉得自己已经不再是路人甲而已经是穿山甲了。一路折腾，总共消耗了近五个小时才到达赣州市区。', 'travel\\4b014958-1913-4114-841a-1beab3537aa7.png', '对于赣州还是简单的介绍一下吧。赣州，简称“虔”，别称“虔城”，也称“赣南”，位于江西省南部，是江西省的南大门，是江西省面积最大、人口和下辖县市最多的地级市。赣州是江西省省域副中心城市，拥有4个国家级经济技术开发区和全省首个综合保税区。赣州是全国稀有金属产业基地和先进制造业基地、红色文化传承创新区和著名的红色旅游目的地、区域性综合交通枢纽、原中央苏区振兴发展示范区、赣粤闽湘四省通衢的区域性现代化中心城市。赣州都市区也是江西省重点培育和发展的三大都市区之一。\r\n\r\n　　我们事先在网上搜索了一些相关的游记和攻略，经过深思熟虑以及朋友间的讨论，我们把这次旅行的目的地定在赣州市章贡区的通天岩，瑞金共和国摇篮景区以及大余的丫山风景区，计划旅游的时间是三天两夜，五月一号晚上赶回去，然后五月二号继续上班。\r\n\r\n　　到了赣州市去以后，已经是饭点了，我们决定吃过午饭后在赶往通天岩。既然来到了赣州，肯定得吃点赣州的特色了。我们来到了被赣州人认为是最好吃的一条街——钓鱼台美食街，。我们来到了一家不起眼的饭馆，名字具体叫什么我忘了，不过味道还不错。我们点了一份兴国米粉鱼，一份粉蒸肉，一份酿豆腐，还点了一份清明果。兴国米粉鱼是江西兴国地区传统名菜，以草鱼为制作主料，烹饪技巧以蒸菜为主，口味属于咸鲜。而粉蒸肉的流行就比较广泛，但是江西的比较有名，根据烹饪方法，粉蒸肉可以偏辣，也可以偏甜，配料可以辅以香菇、老藕、南瓜、红薯等配菜。酿豆腐我们郴州那也是有的，但是味道不一样，赣州的酿豆腐属于客家风味，较为咸鲜。', 'travel\\5475a277-71d6-4133-ad7e-d2e8f37d1f7a.png', '吃过午饭后，我们便继续驾车前往通天岩了，同是在章贡区，所以我们从美食街很快便到了通天岩。通天岩景区是赣州的被称为“江南第一石窟”，票价是60元，我们一行四个人，没有儿童，买的都是成人票。通天岩景区可分为古代石刻区和现代休闲区两部分，游步道大致可看作相套的两个环形，最精华的石刻区集中在内环靠近景区大门的地方，主要有观心岩、忘归岩、龙虎岩、通天岩、翠微岩等五个岩洞，是游览重点。外圈以爬山、游览人工景点为主，整个走下来通常需要大半天。', 'travel\\bd2518b6-dc76-4971-8620-48652c2051d2.png', '3', '1');
INSERT INTO `travels` VALUES ('25', '13', '农家乐-九江市庐山区莲花镇枫桂庄园', '2018-04-13 22:29:05', '九江枫桂庄园(九江农耕文化休闲产业园)位于庐山区莲花镇，是一家非常具有高大上趣味的农家乐。园内占地面积近六十余亩，建筑材料均来自九江浔阳古城的拆迁旧料，建筑风格具有浓厚的地方特色和赣北的传统古风。园内名木成林，不仅有百余款形态各异美不胜收的盆栽，还有近二十处庐山历代名人石刻和各类农耕文化。浓厚的文化氛围，使来客流连忘返，光顾再三。园内餐厅面积达二千平方米，可同时容纳五百多名游客用餐。菜品以九江市区传统特色为主，各县区的传统名菜、著名小吃为辅。是九江及周边市民亲近自然，体验农趣的首选休闲点。', 'travel\\a2ad1426-1928-4904-aa84-1efcdeb8be08.png', '庄园很大，有鱼塘、草坪、菜园等各种乡村结构，可以亲自体验钓鱼和种植蔬菜的乐趣。农庄环境还特别的好、空气清新，在这里可以吃到最正宗、最绿色的农家菜。\r\n\r\n　　这里曾被江西省农家旅馆星级评定委员会荣评为“江西三星特色农家乐”；被九江市旅游发展委员会评定为“最具人气农家乐”；被九江市文化新闻出版局评为“九江市文化产业示范基地”。这里远离城市喧嚣，清静的环境和新鲜空气，农家菜和文化氛围都格外的独特。', 'travel\\2de699bc-0c43-48ed-870e-79a1aadb280d.png', '井冈山市下七乡汉头村，总面积一千多亩，拥有果园面积600多亩。良好的自然生态环境基础和发达的现代化农牧业，农场的生态养殖、种植独具特色，游客可以在青山、水畔、果园、劳动园地中爬山、踏青、体会农业劳动之乐；在野鸡场、白鸽场、杨梅园等示范区漫步；还有机会品尝丰富的时令鲜果、蔬菜、石锅鸡、酒酿蒸鸡等农场特色菜(采用农场生态养殖贵妃鸡、珍珠鸡为原材料)。', 'travel\\97ec9fd8-588c-41c1-b290-30664f55ba7d.png', '18', '1');
INSERT INTO `travels` VALUES ('26', '17', '婺源二日游', '2018-04-15 19:57:45', 'A方案：根据一日游的A线和B线两者相加就是二日游行程\r\n\r\n       第一天：北线：县城出发——徽商古村落思溪、延村——途经山水田园风光长滩——千年古镇清华、宋代廊桥彩虹桥——生态自然风光代表大鄣山卧龙谷——严田古樟——返回清华\r\n\r\n       第二天：东线：县城出发——小桥流水人家李坑——古村落，俞氏宗祠汪口——生态村晓起（根据不同的季节时间早晚安排住宿或者到下一个景点江岭或者庆源）——田园风光江岭——深山小桃源庆源。\r\n\r\n       点评：说实话，这两天的行程是很紧张的，第二天如果去掉江岭，庆源则要宽松一些，比较适合包车游，如果是乘当地的交通车，建议把它分成三天来完成。住宿点选择：根据自己的实际情况而决定了。', 'travel\\0b472a40-30a7-4b5e-ad0b-cb51a8cbd6a7.png', 'B方案：北线为主，兼顾东线\r\n       第一日：县城——徽商古村落思溪、延村——千年古镇清华、宋代廊桥——长寿古里洪村——严田古樟——生态自然风光大鄣山卧龙谷\r\n       第二日：清华——文化底蕴浓厚的古村落理坑——丛溪飘流——县城——小桥流水人家李坑\r\n\r\n       点评：这条线增加了北线一个未开发的古村落——长寿古里洪村，适合一些喜欢淳朴，没有商业气氛的地方，然后是北线一个著名的深山进士村理坑，也是开发得比较少的一处古村落。严田古樟是一处有婺源最大最老的一棵古樟树的水口风光，属休闲性的景点，放松一下是很不错的。', 'travel\\7e414d93-87fb-4146-84fb-97af08cc04c6.png', 'C方案：东线、北线、西线兼顾\r\n       第一日：县城——小桥流水人家李坑——古村落汪口俞氏宗祠——生态村晓起——庆源，住宿或者是返回晓起住宿。\r\n\r\n       第二日：县城——千年古镇清华、宋代廊桥彩虹桥——严田古樟——赋春鸳鸯湖——文公山——县城\r\n\r\n       点评：这条线舍弃了北线的大鄣山、理坑、安排了西线的鸳鸯湖，这是季节性的景点，冬天有鸳鸯。还有一处与朱熹有关的文化景点：文公山。', 'travel\\1a46ab33-da68-4da7-a8db-1ba6f3d99499.png', '10', '1');
INSERT INTO `travels` VALUES ('27', '11', '婺源旅行攻略 仙气儿里的云卷云舒是生活', '2018-04-16 18:49:57', '巴蜀在线讯  谈起婺源，就会想起漫山遍野的油菜花、白墙黑瓦的水墨画、与“敦煌学”、“藏学”并称为中国三大地方显学的“徽文化”。今天小编跟大家分享一下婺源旅行攻略，助大家玩转婺源。在婺源乘坐中巴宜早不宜迟，一定要提前查好班次，如果想轻松玩遍婺源建议包车游玩。旺季时婺源堵车会很严重，建议错峰出行。', 'travel\\50ce0838-afc8-4e74-aaf3-2fa754c1098f.png', '餐饮攻略婺源以本土菜系粉蒸、清蒸和糊菜为特色，特色菜有：粉蒸肉、荷包鲤鱼等，土鸡、腊肉等乡野风味也不容错过，此外，还有小吃清明果、气糕等。当地特色柴火饭，菜园菜，地道农家菜，距离李坑景区非常近。特色饮品查记酒坊(李坑景区店)，当地有名的老字号酒坊，可以免费尝酒。农家菜老屋饭店，装修古色古香，价格实惠。', 'travel\\91cc756e-2267-4a34-a77c-75a6ed0a0a87.png', '农家菜婺源庆源古宅客栈，一栋拥有二百多年历史的古宅，同时提供住宿和餐饮。素有小桃园之称，长期与世隔绝，是婺源少有的保持古朴本色的村落。晓起被称为“生态家园”，可以说是婺源风景最美的村庄 ，透出古朴典雅的气质。当地特色村里村外大碗菜，婺源第一人气餐厅，婺源特色菜一站品尝地。当地特色婺源第一楼，酒店以婺源特色菜及徽菜为主，同时兼有赣菜、湘菜、川菜等特色菜系。\r\n\r\n位置：婺源县文公北路(方汇小区大门对面）\r\n\r\n价格：人均60元\r\n\r\n思溪延村\r\n\r\n徽派老建筑，有精湛的石雕、木雕、砖雕，1987年版的《聊斋》在此取景。', 'travel\\00240f86-4d2c-4d9b-a728-d1439067c77f.png', '14', '1');
INSERT INTO `travels` VALUES ('28', '11', '婺源小城故事 | 愿你既能朝九晚五，又可以浪迹天涯。', '2018-04-26 17:13:47', '我想很多人都很喜欢冰叔说的一句话：请相信，这个世界真的有人在过着你想要的生活，既能朝九晚五，又可以浪迹天涯。现在越来越多的人在不同的维度里面选择自己不同的生活方式。', 'travel/07609692-01c6-4014-a70e-2d9aee38fa20.png', '时光永远给我们以希望和焦虑。或许是天性的不满足使然，我们需要体面的工作成就日常的谈资，内心却深陷焦虑与枯燥，渴望一场不管不顾的出走，而山清水秀是盛放内心烦躁的最佳处所。远赴巴黎、伦敦的天涯或许无法实现，但托城市建设之福，至少在某日收工的傍晚，我们还来得及跳上一辆火车，在方圆3000公里范围的天涯，浪迹一番。', 'travel/3fa09016-688c-4e58-8d8b-524b30bcb470.png', '我是说，你可以去婺源。不远不近，横贯东西与南北的高铁，刚好满足你朝九晚五、浪迹天涯的“贪心”。\r\n夏日的阵雨，分明感受到自然的力量。\r\n\r\n自 江岭 万亩梯田上，浩荡而下的雨水，在山下聚成汪洋，在雨霁天青的某日清晨，第一缕日光冲破云层，地面的“雾海”出现一道彩云。放眼山、水、天、云，青的更青，蓝的更蓝。浮华与疲惫被冲洗的干干净净，中国最美山村，显山露水。\r\n\r\n此时抵达婺源，是最好的。', 'travel/5050ac34-2435-446c-aa22-fec9f2f8746a.png', '27', '1');
INSERT INTO `travels` VALUES ('29', '18279171713', '婺源小城故事 | 愿你既能朝九晚五，又可以浪迹天涯。', '2018-05-04 21:27:27', '我想很多人都很喜欢冰叔说的一句话：请相信，这个世界真的有人在过着你想要的生活，既能朝九晚五，又可以浪迹天涯。现在越来越多的人在不同的维度里面选择自己不同的生活方式。', 'travel/20c87f8b-d01f-4a21-a468-1d39d24547bd.png', '时光永远给我们以希望和焦虑。或许是天性的不满足使然，我们需要体面的工作成就日常的谈资，内心却深陷焦虑与枯燥，渴望一场不管不顾的出走，而山清水秀是盛放内心烦躁的最佳处所。远赴巴黎、伦敦的天涯或许无法实现，但托城市建设之福，至少在某日收工的傍晚，我们还来得及跳上一辆火车，在方圆3000公里范围的天涯，浪迹一番。', 'travel/07b63b76-1d62-4058-b8e5-aef8685ee78f.png', '我是说，你可以去婺源。不远不近，横贯东西与南北的高铁，刚好满足你朝九晚五、浪迹天涯的“贪心”。\r\n夏日的阵雨，分明感受到自然的力量。\r\n\r\n自 江岭 万亩梯田上，浩荡而下的雨水，在山下聚成汪洋，在雨霁天青的某日清晨，第一缕日光冲破云层，地面的“雾海”出现一道彩云。放眼山、水、天、云，青的更青，蓝的更蓝。浮华与疲惫被冲洗的干干净净，中国最美山村，显山露水。\r\n\r\n此时抵达婺源，是最好的。', 'travel/4f8b0089-019f-4748-9e46-bd8dd75e9a14.png', '23', '1');

